    background-color: black !important;
    background-image: none;
}
body #categoryFilter {
    background-color: black !important;
    border-color: black !important;
}
.alzaCarousel.standardbox, #boxc .box .dynamicPromo, #boxc .box:hover .codec, #tabs, .categoryPage, #boxc .box {
    background: none !important;
}
#boxc #binfo a:hover {
    background-color: #a1a1a1;
}
#boxc #binfo {
    border: 1px solid #a1a1a1;
}
body #lblAllB, body #lblLogin, body a, body h1, body h1 a, body h2, body h2 a, body h3, body h3 a, body h4, body h5, body h5 a, body h6, body h6 a, #parametrization .parameter .parameterName, .priceDetail #avlc #avl .detailBuyPhone, .priceDetail #avlc #avl .detailBuyPhone .fa {
    color: #c1c1c1 !important;
}
#parametrization .parameter.enum .parameterContent .parameterValue .name {
    color: #9a9a9a !important;
}
.categoryPage.withLeftParametrization #leftParametrizationContainer, .categoryPage.withLeftParametrization .mainContent, #boxc .box {
    border-color: transparent !important;
}
.box .c1.red, .box .c2 {
    color: #b31919;
}
#boxc .box {
    border-top: 1px #a1a1a1 solid !important;
}
#boxc .btnkx .btnk1, #boxc .btnkx .btnk1:hover, #boxc .btnkx .btnk2, #boxc .btnkx.op .btnk2 {
    background: #2b2b2b;
}
