    border-top: 1px #dedede solid !important;
    border-right: 1px #dedede solid !important;
    border-bottom: 1px #a1a1a1 solid !important;
    width: 20px;
    height: 26px;
    text-decoration: none;
    border-radius: 0 3px 3px 0;
}
#boxc .btnkx .btnk2:hover {
    border-top: 1px #a1a1a1 solid !important;
    border-right: 1px #a1a1a1 solid !important;
    border-bottom: 1px #a1a1a1 solid !important;
    width: 20px;
    height: 26px;
    background: #2b2b2b;
}
#boxc .btnkx .btnk2:after {
    display:block;
    content:"⌄";
    text-align:center;
    text-decoration: none;
}
#boxc .action .pricea {
    background-color: #cac66c;
    border-color: #cac66c;
    color: #b31919;
}
#boxc .action .price {
    border: 1px solid #2b2b2b;
    background: #2b2b2b;
}
#boxc .gbbRating>span {
    color: #a1a1a1;
}
