css:
@-moz-document domain("warframe.market") {
/*[[theme-d-g-b]]*/

body, html, .content__body, .order-row {
    background: var(--bg0);
}




.content__header--second, footer, .item_orders .item_orders__sort, .order-row:nth-child(2n), .user-orders__sort, .review__form textarea {
    background: var(--bg1);
}

.seeker .seeker__input, .order-unit:nth-child(2n+1), footer .footer__body .footer__body--right, footer .footer--right, .review__block, .review__block p, .statistics__header, .statistics__numbers, .item__page .content__header--transparent .container a, .messenger__block .messenger__chat_list, .messenger__block .messenger__chat_block .chat__block, .chat__form textarea {
    background: var(--bg2);
}
.floating-menu, .floating-menu__section .root-menu-button > .combobox, .floating-menu__section .root-menu-button > a, .content__header--second select, .content__header select, .order-unit, .content__header, .widget-order__content, .message.own {
    background: var(--bg3);             
}
.floating-menu__section .root-menu-button > ul, .seeker .seeker__dropdown, .chat__controls, .chat__form, .widget-order__top ul li:nth-child(2n+1) {
    background: var(--bg4);
}
.widget-order__content .widget-order__header {
    background: var(--bg5);
}
.header .search-field button, .widget-order__button .big-red__button, .floating-menu__section .root-menu-button > ul li.online-switch > span:hover, .seeker .seeker__dropdown li:hover {
    background: var(--bg6);
}
.header .search-field button:hover, .widget-order__button:hover .big-red__button, .floating-menu__section .root-menu-button > ul li.online-switch > span.active, .fetch-progress-bar .gauge, .form-control, .form-control:focus {
    background: var(--bg7);
}



.widget-order__content .widget-order__header, .widget-order .seeker__input, .chat__controls, .chat__form, .chat__form textarea {
    border-color: transparent;
}
.chat__row:last-of-type, .chat__row:nth-child(2n) {
    border-color: var(--bg5);
}




.floating-menu__section li.separator {
    border-left: 1px solid var(--bg6);
}
.floating-menu .logotype img {
    filter: invert(50%) brightness(200%);
    -webkit-filter: invert(50%) brightness(200%);
}
.floating-menu__section .user__avatar {
    border-color: var(--bg6);
    border-radius: 50%;
}
.filter__unit input[type="number"], .description__flex {
    border-color: var(--bg6);
}
.floating-menu__section .root-menu-button > ul {
    border-top: 1px solid var(--bg6);
}
.floating-menu__section .root-menu-button > ul li.online-switch {
    border-top: 1px solid var(--bg6);
    border-bottom: 1px solid var(--bg6);
}
.floating-menu__section .root-menu-button > ul li.button > a:hover, .floating-menu__section .root-menu-button > ul li.button > div:hover {
    background: var(--bg6) !important;
}
.load__box .btn:first-child {
    margin-right: 35px;
}
.seeker .seeker--focus {
    box-shadow: 0 0 10px 3px var(--blue);
}
.item_orders .item_orders__sort, .user-orders__sort, .review__form textarea, .statistics__header {
    border-color: transparent;
}
.btn__light:hover {
    background: var(--bg2) !important;
}
.btn__light.active, .btn__light:active, .btn__light:focus {
    background: var(--bg4) !important;
}
.order__own_line, .order__own_line:nth-child(2n+1) {
    background: var(--bg5) !important;
}


























.content a, .content h2 {
    color: #4995aa;
}
.widget-order__button .big-red__text, footer .footer__body .footer__disclaimer p {
    color: #aaa;
}
.content h1, .content__header--second select, .content__header select, .filter__unit input[type="number"], .floating-menu__section .root-menu-button > ul li.submenu__header span, .content, .user__name {
    color: #eee;
}
.seeker .seeker__input input, .content h3, .widget-order__button:hover .big-red__text, footer h3, .review__form textarea, .widget-order h2, .message .user__name, .chat__form textarea, .form-control {
    color: #fff;
}







/* HIDE AD */
.ad__line {
    height: 0 !important;
    max-height: 0 !important;
    min-height: 0 !important;
    padding: 0 !important;
    margin: 0 !important;
    overflow: hidden !important;
    border: none !important;
    width: 0 !important;
    max-width: 0 !important;
    min-width: 0 !important;
}
.ad__unit {
    display: none;
}



}
