css:
@-moz-document url-prefix("http://www.jeuxvideo.com/forums") {
body {
font-family : arial;
}

.titre-bloc.titre-bloc-forum {
color: #fff;
font-family : arial;
}

#page-topics,
.container-content {
	background: #fff;
}

.jv-header-bottom,
.bloc-boutique-droite {
	display: none !important;
}

.jv-header-menu .jv-header-top {
	background: #fafafb;
    height: 50px;
    box-shadow: 0 1px 0 rgba(12,13,14,0.1), 0 1px 6px rgba(59,64,69,0.1);
}

.logo {
    width: 150px;
    height: 30px;
	background-image: url(https://cdn.sstatic.net/Sites/stackoverflow/company/img/logos/so/so-logo.svg?v=a010291124bf);
	background-size: 100%;
    margin-top: 6px;
}

.logo i {
    opacity: 0;
}

.nav-icon-bell-on:before, .jv-header-menu .jv-account-number-notif:before,
.nav-icon-pm:before, .jv-header-menu .jv-account-number-mp:before, .jv-header-menu .account-pseudo, .nav-icon-search:before {
    color: #848d95;
}

#content {
    margin-top: 50px;
    background: #fff;
}

.jv-global-user {
	margin-top: -4px;
}


.topic-list {
	border: none;
}

.topic-list li {
    background: #fff !important;
    height: 70px;
}

.topic-list li span {
    border-bottom: 1px solid #eff0f1;
    padding-top: 25px !important;
}

.lien-jv.topic-title {
	color: #005999;
    font-family: arial;
    font-size: 16px;
    font-weight: 300;
    margin-left: 0;
}

.topic-list .topic-head {
	display : none;
}

.topic-list .topic-img[title="Topic hot"] {
    width: 0px;
    padding-right: 36px;
    padding-bottom: 18px;
    background-image: url(https://image.noelshack.com/fichiers/2018/27/2/1530623476-cplusplus.jpg);
    top: 47px;
    background-position: 0 -2px;
}

.topic-list .topic-img[title="Topic"],
.topic-list .topic-img[title="Topic résolu"] {
	width: 0px;
    padding-right: 36px;
    padding-bottom: 18px;
    background-image: url(https://image.noelshack.com/fichiers/2018/27/2/1530624887-php.jpg);
    top: 47px;
    background-position: 1px -3px;
}

.topic-list .topic-img[title="Topic épinglé"] {
	width: 0px;
    padding-right: 36px;
    padding-bottom: 18px;
    background-image: url(https://image.noelshack.com/fichiers/2018/27/2/1530625162-html.jpg);
    top: 47px;
    background-position: 1px -3px;
}


/*img[title="Topic hot"] {
    opacity:0;
    content: 'R';
}*/

#jv-footer {
	display: none;
}

.icon-title:before, .titre-bloc:before {
	content: "Forum développement informatique";
    color: #242729;
    font-family: arial;
    font-size: 22px;
}

.wads_po_push-right-1_p {
	display: none;
}

.panel-jv-forum {
    background-color: #FFF8DC;
    border: 1px solid #E0DCBF;
    color: #005999;
}

.panel-jv-forum>.panel-heading {
background-color: #FFF8DC;
    color: #9C988B;
}

.panel-jv-forum .titre-info-fofo {
 color: #9C988B;
}

.panel-jv-forum .bloc-sous-forums .lien-jv,
.panel-jv-forum .bloc-info-forum .lien-jv {
color: #005999;
font-weight: 300;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb a, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb a,
.bloc-fil-ariane-crumb .fil-ariane-crumb h1, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb h1 {
    color: #878787;
    font-weight: 300;
    font-family: arial;
}

#zone-sponso {
display: none;
}

.topic-list .topic-author {
border-bottom: 1px solid #eff0f1;
}

#page-messages-forum .titre-bloc.titre-bloc-forum {
    color: #242729;
    font-family: arial;
    font-size: 14px;
}

#page-messages-forum .icon-title:before, #page-messages-forum .titre-bloc:before {
    display: none;
}

#bloc-formulaire-forum .titre-bloc:before {
display: none;
}

.titre-bloc {
    font-family: arial;
    color: #242729;
}

.btn-poster-msg {
	color: #39739d;
    background-color: #E1ECF4;
}

.boutons-spotify-topic .btn {
    color: #39739d;
    background-color: #E1ECF4;
    text-transform: lowercase;
    border: none;
    font-family: arial;
}

.boutons-spotify-topic .btn strong {
	font-weight: 300;
}

.btn-ajouter-sondage {
    color: #39739d;
    background-color: #E1ECF4;
    text-transform: lowercase;
    border: none;
    font-family: arial;
}

.btn-ajouter-sondage strong {
	font-weight: 300;
}

.bloc-message-forum {
    background: #FFF!important;
    border: none !important;
    border-bottom: 1px solid #d5d5d5!important;
}

.txt-msg {
	font-family: arial;
    font-size: 15px;
}

.text-enrichi-forum img {
	filter: grayscale(100%);
    max-width: 40px;
    height: auto;
}

.bloc-message-forum .user-avatar-msg {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}

.btn-repondre-msg,
.btn-topic-resolu,
.btn-actu-new-list-forum,
.bloc-liste-num-page .page-active
{
    color: #33658a;
    background-color: #cee0ed;
    border: none;
}

.bloc-pagi-default .pagi-debut-actif, .bloc-pagi-default .pagi-precedent-actif, .bloc-pagi-default .pagi-suivant-actif, .bloc-pagi-default .pagi-fin-actif {
    color: #33658a;
    background-color: #cee0ed;
    border: none;
}

@media (min-width: 92.5rem) {
    .topic-list {
        width: 754px;
    }
}

@media screen and (max-width: 74.9375rem) {
    .topic-list .topic-img {
    	left: 150px;
    }
}
}
