css:
@-moz-document domain(www.youtube.com) {
#notification-count {
  background-color: #EB56C5 !important;
}
span.deemphasize.style-scope.yt-formatted-string {
  color:white;
}
.ytp-play-progress {
   background-color:none !important;
   background-image: repeating-linear-gradient(170deg, rgba(0,0,0,0.75) 0%, rgba(255,255,255,0) 5%, rgba(0,0,0,0.75) 10%);
  border-right: 5px dashed black;
}
.ytp-swatch-background-color { background-color: none !important;}
.ytp-progress-list {
  background-image: linear-gradient(90deg, rgba(0,255,255,1) 0%,  rgba(100,255,100,1) 20%, rgba(255, 255,0,1) 40%, rgba(255,0,0,1) 60%, rgba(0,255,255,1) 80%, rgba(0,0,255,1) 100%);
}
.style-scope.ytd-page-manager.hide-skeleton {
 	background-image: url("https://data.whicdn.com/images/294447387/original.gif");
  background-size: 100% 200%;
}
.video-stream.html5-main-video {
    border-left:15px solid;
 	border-color: rgba(0, 122.5, 255, 0.5);
    border-radius: 20px;
}
.ytp-scrubber-button {
	background-color: #756EF4 !important;
}
.ytp-hover-progress {
  background-color: #2C71D3 !important;
}
.ytp-chrome-bottom {
  background-color: rgba(255, 0, 0, 0.05) !important;
}
.ytd-video-primary-info-renderer {
  color: #FF0000 !important;
}
.ytd-toggle-button-renderer.style-text {
	color: #0000FF !important;
}
yt-icon {
  color: #00FFCC !important;
}
span#video-title.ytd-compact-video-renderer, span#video-title.ytd-compact-radio-renderer {
  color: #FFFF00 !important;
}
.date {
  color: #FFFFFF !important;
}
a#video-title.yt-simple-endpoint.style-scope.ytd-grid-video-renderer {
  color: #FFFF00 !important;
}
a.yt-simple-endpoint.style-scope.yt-formatted-string, .style-scope.ytd-video-meta-block {
  color: #9F009F !important;
}
span.style-scope.ytd-grid-video-renderer {
  color: #0000FF !important;
}
svg {
   fill: #00FFFF !important; 
}
#subtitle.can-be-empty.style-scope.ytd-shelf-renderer {
  color: #5CFF00! important;
}
.title.style-scope.ytd-guide-entry-renderer, .style-scope.ytd-video-meta-block {
  color: #0055FF !important;
}
.style-scope.ytd-subscribe-button-renderer {
  background-color: #0000FF !important;
  color: #FFFFFF !important;
  border-radius: 20px;
}
yt-formatted-string#byline.style-scope.ytd-video-meta-block {
  color: #9F009F !important;
}
yt-formatted-string.more-button.style-scope.ytd-video-secondary-info-renderer {
  color: #FFCCFF !important;
}
.count-text.style-scope.ytd-comments-header-renderer {
  color: #FFFF00 !important;
}
div#icon-label.style-scope.yt-dropdown-menu {
  color: #FFDDFF !important;
}
.style-scope.ytd-comment-renderer {
  color: #00FFFF !important;
}
.style-scope#content-text.ytd-comment-renderer {
  color: #FFFFFF !important;
}
span#vote-count-middle.style-scope.ytd-comment-action-buttons-renderer {
  color: #00FF11 !important;
}
yt-formatted-string#text.style-scope.ytd-toggle-button-renderer.style-text, 
yt-formatted-string#text.style-scope.ytd-button-renderer.style-default, #title, span.guide-entry-count.style-scope.ytd-guide-entry-renderer, .content-text.style-scope.ytd-metadata-with-image-row-renderer, .ytp-hover-progress-light {
  color: #00FF00 !important;
}
div#container.style-scope.ytd-masthead {
  background-image: url("https://i.pinimg.com/originals/45/97/b5/4597b5006253b21f8405836fafaa6cb0.jpg") !important;
  background-position: 50% 80%;
  background-size: 100% 350%;
}
input.ytd-searchbox{
  background-color: rgba(0, 0, 0, 0) !important;
  color: black !important;
}
div.ytd-searchbox {
  border: 1px solid black !important;
  border-radius: 20px !important;
  background-color: rgba(0,0,0,0.25) !important;
  background-image: linear-gradient(rgba(255,255,255,0.75), rgba(0,0,150,0.65)) !important;
}
#img, .style-scope.ytd-thumbnail { border-radius: 30px; }
.style-scope.ytd-thumbnail { display: hidden !important; visibility: none !important; }
ytd-thumbnail-overlay-time-status-renderer { background-color: white; }
.badge.badge-style-type-live-now.style-scope.ytd-badge-supported-renderer { border-color: #00CCFF; color: #00CCFF}
 [title = "Watch later"] { background-color: rgba( 255, 0, 255, 0.3); color: rgba(255, 0, 255, 1); }
.ytd-guide-renderer, #guide-renderer, #guide-content, #sections { background-color: rgba(255,255,255,0.1); }}
