css:
@-moz-document domain("lingojam.com") {
:root, body {
    background-color: #000;
    background-image: none;
}
#submit-suggestion:hover {
    background-color: #000;
}
.main.style1 {
    background: #000;
}
body {
    background: #000 !important;
}
.white-section-area-thing {
    background: #222;
}
.intro {
    background-color: #111;
}
:root, button, input, select, textarea, .list-container .list1, .list-container .list2 {
    color: #ccc;
}
input, textarea {
    background-color: #111 !important;
    border: 1px solid #555;
}
a:-webkit-any-link {
    color: #55f;
}
p a {
    color: #ccc !important;
}
hr {
    border-top: 1px solid #555;
}
body {
    color: #ddd;
}
.readmore-box span:hover {
    color: #fff;
    border: 1px solid #fff;
}
.readmore-box span {
    color: #ccc;
    border: 1px solid #ccc;
}
h1, h2, h3, h4, h5, h6 {
    color: #ccc !important;
}
.main-title .subtitle {
    color: #aaa;
}
#translator-lists .list {
    column-rule: 1px outset #aaa;
}

.hover-tab {
    background-color: #222;
}
#suggestion-area, .disqus-container, .adsense-area {
    background: #111;
}
.post-content .post-body spoiler:focus, .post-content .post-body spoiler:hover {
    background: #080808;
}
.textarea-outer-wrapper .textarea-wrapper {
    background: #000 !important;
}

.info-box, #suggestion-box, #submit-suggestion {
    background: #111;
    box-shadow: none;
}
.selected-tab {
    background-color: #01192b;
}
.dark .textarea {
    border-color: #080808;
}

.input--text, .textarea-outer-wrapper .textarea-wrapper, .user-mention__list{
    border: 2px solid #333;
}

.readmore-gradient {
    background-image: -webkit-gradient(linear, 0% 100%, 0% 0%,color-stop(0, rgb(16,16,16)),color-stop(0.436, rgba(16,16,16, 0.96)),color-stop(1, rgba(0, 0, 0, 0)));
    background-image: -webkit-repeating-linear-gradient(bottom,rgb(16,16,16) 0%,rgba(16,16,16, 0.96) 43.6%,rgba(0, 0, 0, 0) 100%)
}
#tab-buttons {
    border-bottom: solid 1px #555;
}
.dark .load-more__button {
    background: rgba(0,0,0,.85);
    color: #bbb;
}

}

@-moz-document url-prefix("https://www.lingojam.com/translators") {
tr:nth-child(2n+1) td {
    background: #222;
    color: #bbb;
}
tr:nth-child(2n+1):hover td {
    background: #080808;
    color: #eee;
}


tr:nth-child(2n) td {
    background: #111;
    color: #bbb;
}
tr:nth-child(2n):hover td {
    background: #060606;
    color: #eee;
}

}
