css:
@-moz-document domain("youtube.com") {
/* Addons  styling for compatibility */
#info > #count > a > span
{
	color: #3c3c3c !important;
}




/* /Addons */
#logo > #logo-icon,
#endpoint > *:nth-child(2) > *:not(:first-child),
#appbar-guide-button > span > span,
.privacy-icon-clickable,
.private-icon,
.public-icon,
#trigger > #label > #label-icon > svg > g,
.yt-masthead-picker-info > a:nth-child(4) > span,
#yt-masthead-user > a,
#yt-masthead-notifications-button
{
	background-color: #ffe000 !important;
}

#guide-button.ytd-masthead
{
	background-color: #3c3c3c;
}


/* subscribe button text*/
yt-formatted-string#text
{
	color: #ffe000 !important;
}

.toggle-bar.paper-toggle-button
{
	background-color: var(--paper-toggle-button-unchecked-bar-color, white);
}

html:not([style-scope]):not(.style-scope)
{
	--ytd-toggle-color: #3c3c3c;
	--ytd-survey-button-color: #3c3c3c;
}

html[dark]:not([style-scope]):not(.style-scope),
[dark]:not([style-scope]):not(.style-scope)
{
	--ytd-toggle-color: #3c3c3c;
	--ytd-survey-button-color: #3c3c3c;
}

.yt-uix-button-subscribe-unbranded
{
	background-color: #3c3c3c !important;
}

#player-ads,
#google_companion_ad_div,
#expand-ad,
#google_image_div,
.ytd-player-legacy-desktop-watch-ads-renderer,
.adDisplay
{
	visibility: hidden !important;
	height: 0px !important;
	width: 0px !important;
	font-size: 0px !important;
	display: none !important;
}

#notification-button > *
{
	filter: brightness(1.25) !important;
	border-radius: 50% !important;
}


/*notifications about channel - subscribed to */
.guide-icon > svg > g:nth-child(1),
.paper-tab:hover,
[aria-label="Unlike"]
{
	filter: sepia(100%) hue-rotate(156deg) saturate(700%) brightness(.75) !important;
	transform: scale(1.14) !important;
}

.guide-icon
{
	background-color: #fee000;
}

#label > yt-formatted-string
{
	color: white !important;
	transition: color .5s ease-out !important;
}


/* Filter choices */
#label > yt-formatted-string:hover
{
	color: #afe !important;
	transition: color .3s ease-in !important;
	cursor: pointer !important;
}


/* Filter choices */
#movie_player > .ytp-gradient-bottom,
.ytp-chrome-top,
.ytp-gradient-top
{
	display: none !important;
}

[aria-label="Play"]:hover > svg > path,
[aria-label="Pause"]:hover > svg > path
{
	fill: #ffe000 !important;
}


/* Play button */
.ytp-left-controls > a:first-child:hover > svg > path
{
	fill: #ffe000 !important;
}


/* Previous/replay button */
.ytp-left-controls > .ytp-next-button:hover > svg > path
{
	fill: #ffe000 !important;
}


/* Previous/replay button */
.ytp-mute-button:hover > svg > path
{
	fill: #ffe000 !important;
}


/* Mute/unmute button */
.ytp-volume-slider-handle:hover:before,
.ytp-volume-slider-handle:hover
{
	background: #ffe000 !important;
	z-index: 9999 !important;
}


/* Volume slider */
.ytp-right-controls > button:hover > svg > path,
.ytp-fullscreen-button:hover > svg > g > path
{
	fill: #ffe000 !important;
}


/* Settings/scale/fullscreen buttons */
.ytp-settings-button.ytp-hd-quality-badge::after
{
	background-color: #ffe000 !important;
}

#container
{
	border-bottom: 1px solid #ffe000;
}

.title.ytd-guide-entry-renderer
{
	font-weight: 900;
	color: #ffe000 !important;
}

.ytp-red2 .ytp-swatch-background-color,
.ytp-red2 .ytp-swatch-background-color-secondary
{
	background-color: #3c3c3c !important;
}


/* Progress Bar */
#top,
#player,
#search-form > #container,
#search-icon-legacy,
#container:nth-child(2),
#contentContainer > *:not(.banner-visible-area),
[is="yt-endpoint"],
#chat-messages > yt-live-chat-header-renderer,
yt-live-chat-renderer > *,
#action-panel > *,
#page-manager,
.badge-style-type-live-now,
.badge-style-type-simple,
#page-manager > ytd-watch,
.gstl_50 *,
.sbdd_c *,
#playlist > #container > *,
yt-confirm-dialog-renderer,
[page-subtype="playlist"] > *,
[title="Search"],
#search-btn,
#page-container,
.secondary-header-contents,
#c4-primary-header-contents,
#pl-header,
.pl-header-content-front > pl-header-content > .pl-header-title,
#browse-items-primary,
.playlist-play-all,
#playlist-settings-editor > button,
#gh-playlist-add-video,
.yt-ui-menu-content,
yt-next-continuation > *,
#creator-page-sidebar,
#body-container,
#footer-container,
#masthead-appbar > #appbar-content > #creator-appbar-actions-bar > .vm-video-actions-bar,
#vm-search-container *:not(#advanced-search-button),
#advanced-search-dropdown,
.yt-uix-clickcard-card-border div,
.yt-uix-clickcard-card-border div a,
#info-contents > ytd-video-primary-info-renderer > #container > #info > #menu-container > #sentiment,
ytd-dismissal-follow-up-renderer
{
	background-color: #3c3c3c !important;
	border-color: #3c3c3c !important;
}

*:not(.ytd-notification-topbar-button-renderer),
iron-dropdown *,
.gstl_50 *,
.sbdd_c *
{
	color: #ffe000 !important;
}


/* live chat */
yt-live-chat-message-buy-flow-renderer
{
	background-color: #3c3c3c !important;
}

#purchase-amount,
.yt-live-chat-text-input-field-renderer
{
	color: black !important;
}

#picker-buttons
{
	background-color: #ffe000 !important;
}

#notification-count.ytd-notification-topbar-button-renderer
{
	color: #ffe000 !important;
	transform: scale(1.2) !important;
}


/* notification number */
.view-stats-metric-link:hover,
[title="View analytics"]:hover,
[data-content-id="yt-uix-tooltip14-content"]:hover
{
	background-color: #3c3c3c !important;
}

.unseen
{
	filter: brightness(1.2) !important;
}


/* unseen notification */
#play-all > ytd-button-renderer > a:hover
{
	filter: brightness(1.2) !important;
}

#creator-page-content > div,
#non-appbar-vm-video-actions-bar,
#vm-playlist-video-list-ol > li > div,
[aria-label="Action menu."],
.vm-addto-button,
.edit-expand-menu-button,
.vm-playlist-edit-video,
#creator-sidebar > li > h3 > a:hover,
#creator-page-content > div:nth-child(10) > div
{
	background-color: #ffe000 !important;
}

.account-section-setting > span > span,
.yt-uix-form-input-select-content,
#footer-main > ul > li > button,
#footer-main > a,
#footer-main > button,
#footer-main > [data-loaded="1"],
.creator-sidebar-item-link:hover,
.sort-menu > div > button,
#creator-page-content *:not(.ytrp_wrapper):not(.ytrp_tooltip):not(.ytrp_rb_bg_bottom):not(.ytrp_rb_bg):not(.C2KYWWB-g-e),
#creator-sidebar-section-id-dashboard > h3 > a,
#creator-page-content
{
	background-color: #ffe000 !important;
	border-color: grey !important;
}

.creator-sidebar-item.selected
{
	background-color: #ffe000 !important;
}

.yt-uix-button-subscribe-unbranded
{
	background-color: #ffe000 !important;
	border-color: grey !important;
}


/* Subscribers unsubscribed to */
.yt-uix-button-subscribed-unbranded
{
	background-color: #3c3c3c !important;
	border-color: grey !important;
}


/* Subscribers subscribed to  */
.send-button-info > button
{
	filter: invert(89%) !important;
}

#search-form > *
{
	border: none !important;
	box-shadow: .45px .45px .45px .45px rgba(255, 224, 0, 0.35) !important;
}

.C2KYWWB-b-k *:not(.C2KYWWB-n-c),
#gwt-debug-datePicker *:not(.C2KYWWB-K-c),
.C2KYWWB-X-d *
{
	background: transparent !important;
}

iron-dropdown > #contentWrapper > ytd-menu-popup-renderer > paper-menu *:not(yt-formatted-string):hover,
.yt-ui-menu-content > button:hover,
.yt-ui-menu-content > .addto-playlist-button:hover,
#addto-list-panel > div > ul > li:hover,
[title="Enter a playlist name to search for"]
{
	background-color: #f3e000 !important;
	color: rgb(0, 0, 0) !important;
}

yt-formatted-string,
#buttons *
{
	background-color: transparent !important;
}

#search-icon-legacy.ytd-searchbox:hover
{
	filter: sepia(100%) brightness(1) hue-rotate(180deg) !important;
}

paper-tooltip *
{
	background: rgba(10, 11, 12, .75) !important;
	border-radius: 55px / 25px !important;
	color: azure !important;
}

#chat
{
	height: 30% !important;
}

yt-live-chat-text-message-renderer > #menu
{
	filter: invert(70%) brightness(1) !important;
}

#author-name,
.yt-uix-form-input-text
{
	color: rgb(85, 95, 115) !important;
}

#expander > #content > #content-text
{
	color: rgb(190, 192, 192) !important;
}

#show-hide-button
{
	border-bottom: 1px white solid !important;
}

@keyframes subSub
{
	from
	{
		border-radius: 400px 70px/90px 100px 0px
	}

	to
	{
		border-radius: 300px 130px/100px 400px 150px
	}
}

@keyframes notNot
{
	from
	{
		border-radius: 100px
	}

	to
	{
		border-radius: 15px
	}
}

paper-button.ytd-subscribe-button-renderer
{
	background-color: rgba(45, 46, 53, .5) !important;
	color: rgb(100, 100, 100) !important;
	border-radius: 400px 70px/90px 100px 0px;
	transition: background-color .5s ease-out !important;
}

paper-button.ytd-subscribe-button-renderer:hover
{
	background-color: rgb(70, 80, 100) !important;
	color: white !important;
	animation-duration: .6s;
	animation-name: subSub;
	animation-iteration-count: infinite;
	animation-direction: alternate;
}

#notification-button:hover,
.ytp-sb-unsubscribe
{
	background-color: rgb(30, 40, 80) !important;
	animation-duration: .4s;
	animation-name: notNot;
	animation-iteration-count: infinite;
	animation-direction: alternate;
}

#masthead-container > #masthead,
#yt-masthead-container,
#channel-header,
#tabs,
ytd-browse[page-subtype="channels"]
{
	background: rgb(20, 22, 25) !important;
}

#guide-wrapper > #guide-content *,
#tabs-container > #tabs-inner-container
{
	background: #3c3c3c !important;
}


/* Notifications */
[aria-label="Notifications"] > yt-icon > svg > g > path
{
	fill: #ffe000 !important;
	color: #ffe000 !important;
}


/* /Notifications */
ytd-multi-page-menu-renderer > #container > #sections
{
	overflow: hidden !important;
}

#left-arrow-container,
#right-arrow-container,
#selectionBar.paper-tabs
{
	background: rgba(120, 110, 110, .8) !important;
	filter: invert(100%) !important;
}

#search-icon-legacy
{
	border: 1px rgba(50, 100, 100, .0) solid !important;
	border-radius: 0px 10px 10px 0px !important;
}

ytd-author-comment-badge-renderer
{
	background-color: #ffe000 !important;
}

#author-comment-badge > ytd-author-comment-badge-renderer > a > yt-formatted-string
{
	color: rgb(116, 133, 139) !important;
}

#author-comment-badge > ytd-author-comment-badge-renderer > a > yt-formatted-string:hover
{
	color: rgb(146, 163, 169) !important;
}

ytd-comment-renderer > #body > #author-thumbnail > a > yt-img-shadow
{
	transform: scale(1.3) !important;
	filter: brightness(.95) !important;
}

*:not(#search-icon-legacy):not(.ytd-searchbox):not(#search):not(#ytd-searchbox):not(#search-container):not(#container)
{
	border-color: #ffe000 !important;
	border-width: 1px !important;
}

#guide
{
	border-right: 1px solid #3c3c3c !important;
	background-color: #3c3c3c !important;
	transform: scale(1.1) !important;
	display: table-cell;
	z-index: 99 !important;
}
html:not([style-scope]):not(.style-scope){
	--yt-spec-overlay-opacity-10:#ffe000;
}

button.yt-icon-button
{
	background-color: #3c3c3c !important;
}

#guide-icon > svg > g
{
	filter: invert(100%) brightness(2) !important;
}

.ytp-swatch-background-color-secondary *
{
	background-color: rgb(230, 230, 230) !important;
}

#yt-masthead-container,
#masthead-appbar,
#header
{
	border-bottom-color: #3c3c3c !important;
}

#items > ytd-grid-video-renderer
{
	background-color: #3c3c3c;
}

#container.ytd-masthead
{
	background-color: #3c3c3c;
}


/* Thumbnail div */
#dismissable > *:not([width="210"]),
#dismissable > #details a
{
	color: #ffe000 !important;
	transition: .09s !important;
}

#dismissable > #details > #meta > div > div > div > #byline > a:hover
{
	color: #ffe000 !important;
}

#metadata-line *
{
	color: rgb(150, 150, 150) !important;
}

#container:not(.ytd-masthead)
{
	border-bottom: none !important;
}


/* Like/Dislike buttons */
#top-level-buttons > ytd-toggle-button-renderer > a > button
{
	transition: .1s ease-in-out !important;
}

#top-level-buttons > ytd-toggle-button-renderer:nth-child(1) > a > button:hover,
#top-level-buttons > ytd-toggle-button-renderer:nth-child(2) > a > button:hover
{
	filter: brightness(2.5);
	transform: scale(1.3) !important;
}

#top-level-buttons > ytd-toggle-button-renderer:nth-child(1) > a > [aria-pressed="true"]
{
	filter: brightness(.8) sepia(100%) hue-rotate(150deg) saturate(2) !important;
}

#top-level-buttons > ytd-toggle-button-renderer:nth-child(2) > a > [aria-pressed="true"]
{
	filter: brightness(.8) sepia(100%) hue-rotate(-40deg) saturate(5) !important;
}

[aria-label="Remove dislike"]
{
	filter: sepia(100%) hue-rotate(315deg) saturate(800%) brightness(.85) !important;
}

#header
{
	z-index: 1 !important;
}

#guide-inner-content *
{
	background-color: rgba(30, 31, 32, .8) !important;
	background-size: 50px 50px !important;
}

#appbar-guide-menu,
#appbar-guide-menu *
{
	border-color: black !important;
}

.yt-formatted-string,
#guide-renderer * a
{
	color: #ffe000 !important;
	background: none !important;
}

.date
{
	color: #ffe000 !important;
}

#byline
{
	color: #ffe000 !important;
}

a:not(.yt-formatted-string)
{
	color: #ffe000 !important;
}

a:hover
{
	color: #ffe000 !important;
}

#label-icon
{
	transform: scale(1.3) !important;
}

#video-title
{
	color: #ffe000;
}


/*LOGO*/
:root
{
	--color2: #ffe000;
	--color6: #ffe000;
	--color8: #ffe000;
}

#logo-icon > svg > g > g:nth-child(1) > path
{
	fill: var(--color2);
}

#guide-icon.ytd-masthead,
.yt-icon-container.yt-icon,
#label-icon.yt-dropdown-menu,
#guide-icon.ytd-app,
.yt-live-chat-header-renderer iron-icon
{
	fill: var(--color6);
}

.yt-live-chat-icon-toggle-button-renderer iron-icon,
.yt-live-chat-text-message-renderer iron-icon
{
	fill: var(--color8);
}

#youtube-paths > path:nth-child(1),
#youtube-paths > path:nth-child(2),
#youtube-paths > path:nth-child(3),
#youtube-paths > path:nth-child(4),
#youtube-paths > path:nth-child(5),
#youtube-paths > path:nth-child(6),
#youtube-paths > path:nth-child(7)
{
	fill: var(--color8)!important;
}


/*SLider*/
.html5-scrubber-button,
.ytp-scrubber-button
{
	background: #3c3c3c !important;
	border-color: rgba(255, 224, 0, 0.5) !important;
	box-shadow: 0 0 4px rgba(255, 224, 0, 0.9) !important;
}


/* бегунок  при наведени*/
.html5-scrubber-button:hover,
.ytp-scrubber-button:hover
{
	background: #ffe000 !important;
	border-color: rgba(60, 60, 60, 0.5) !important;
	box-shadow: 0 0 9px rgba(255, 224, 0,0.9) !important;
}


/* нижний бар */
.html5-player-chrome
{
	background-color: #000 !important;
	background: linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important;
	background: -webkit-linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important;
}


/* загрузка < */
.html5-play-progress,
.ytp-play-progress
{
	background: #ffe000!important;
}


/* загрузка > */
.html5-play-progress,
.ytp-load-progress
{
	background: #ffe000!important;
	box-shadow: 0px 0px 1px #3c3c3c inset !important
}


/* загрузка */
.ytp-unloaded-progress
{
	background: #3c3c3c !important
}


/* 10 сек */
.ytp-ad-progress
{
	background: #ffe000 !important;
	background: linear-gradient(to bottom, #3c3c3c) !important;
	background: -webkit-linear-gradient(to bottom, #ffe000) !important
}


/* бегунок звука */
.ytp-volume-slider-foreground
{
	background: #3c3c3c!important;
	box-shadow: 0px 0px 5px rgba(255, 224, 0, 0.6)inset !important
}


/* звук > */
.ytp-volume-slider-foreground:after
{
	background: #ffe000!important;
	box-shadow: 0px 0px 5px #3c3c3c inset !important
}


/* звук < */
.ytp-volume-slider-foreground:before
{
	background: #ffe000 !important;
	box-shadow: 0px 0px 1px rgba(0, 0, 0, 1.0)inset !important
}


/*цвет текста*/
.ytp-time-display,
.ytp-time-current,
.ytp-time-separator,
.ytp-time-duration
{
	color: #ffe000!important
}


/* Video progress bar glow*/
.html5-play-progress,
.ytp-play-progress
{
	background: #ffe000;
	background: -moz-linear-gradient(top, #ffe000 0%, #ffe000 35%, #000 65%, #ffe000 100%);
	background: -webkit-linear-gradient(top, #ffe000 0%, #000 35%, #000 65%, #ffe000 100%);
	background: linear-gradient(to bottom, #ffe000 0%, #000 24%, #000 65%, #ffe000 100%);
	box-shadow: 0px 0px 20px #3c3c3c, 0px 0px 20px #3c3c3c, 0px 0px 20px #3c3c3c !important;
}


/* Video load bar */
.html5-load-progress,
.ytp-load-progress
{
	background: #3c3c3c;
	background: -moz-linear-gradient(top, #3c3c3c 0%, #3c3c3c 45%, rgba(0, 0, 0, 0.3) 55%, #3c3c3c 100%);
	background: -webkit-linear-gradient(top, #3c3c3c 0%, rgba(0, 0, 0, 0.3) 45%, rgba(0, 0, 0, 0.3) 55%, #3c3c3c 100%);
	background: linear-gradient(to bottom, #3c3c3c 0%, rgba(0, 0, 0, 0.3) 40%, rgba(0, 0, 0, 0.3) 55%, #3c3c3c 100%);
	box-shadow: 0px 0px 12px #3c3c3c, 0px 0px 12px #3c3c3c, 0px 0px 12px #3c3c3c !important;
}

}
