css:
@-moz-document domain("rpcauthority.wikidot.com") {
div#container-wrap {
    background: url(http://rpcsandbox.wdfiles.com/local--files/component:vaughntheme/supremacy_theme.png) top left repeat-x;
    background: var(container-wrap);
}

/* COMMON */

/*Background block for newbies is set in HTML style="background-color: #f0fff6;"*/
:root {
    /* Background gradient variables*/
    --header-top: #ef9800;
    --header-bottom: #061f31;
    --panel-top: #29313d;
    --panel-bottom: #30323e;
    --panel-shadow: #012749;
    --main-background-top: #dbdbd6;
    --main-background-bottom: #dbdbd6;
    /*Generic borders*/
    --dark-border: #330b0b;
    --light-border: #962222;
    --element-border-radius: 2px;
    /*Search*/
    --search-button: linear-gradient(to bottom, #511212, #3f0e0e, #3f0e0e);
    --search-button-hover: linear-gradient(to bottom, #701919, #661717, #661717);
    --search-input-color: #350c0c;
    --search-input-hover: #601616;
    /*Menu with dropdown*/
    --dropdown-background: #e3e3e3;
    --panel-link-hover: white;
    --panel-background-highlight: #011a49;
    --dropdown-text-color: #262626;
    /*Rating widget*/
    --rating-background: #8a1b1b;
    --rating-cancel-color: white;
    --text-header-color: #751414;
    --highlight-color: #5192e1;
    --highlight-hover: #84b7f5;
    --create-page: #394175;
    --highlight-shadow: #011a4973;
    --icon-hack-recolor: hue-rotate(30deg) brightness(5) saturation(0.5);
    /*Forum*/
    --forum-entry-background: #fefff7;
    --post-head-color: #eee;
    --forum-border: lightgray;
    --forum-background: #fffef7;
    /*Tabs*/
    --selected-tab: black;
    --hover-tab: lightgray;
    --inactive-tab: white;
    /*Turn off PNGs*/
    --container-wrap: none;
}

