}
#content-wrap {
    position: relative;
    margin: 2em auto 0;
    max-width: 1040px;
    min-height: 1300px;
    height: auto !important;
    height: 1500px;
}
h1,
#page-title {
    color: var(--header-color);
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
h1 {
    margin-top: 0.7em;
    padding: 0;
    font-weight: bold;
}
h2,
h3,
h4,
h5,
h6,
h7 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
}
#page-title {
    border-color: #bbb;
}
ul {
    /* list-style-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/bullet.gif); */
    list-style: square;
}
#top-bar ul {
    /* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
    margin-top: 5px;
}
li,
p {
    line-height: 141%;
}
a {
    color: #ad3030;
    text-decoration: none;
    background: transparent;
    transition: color 0.5s ease;
}
a.newpage {
    color: var(--create-page);
    text-decoration: none;
    background: transparent;
}
a:hover {
    text-decoration: underline;
    background-color: transparent;
    color: #c64949;
}
.form-control {
    width: 95%;
}

/* GLOBAL WIDTH */
#header,
#top-bar {
    width: 90%;
    max-width: 980px;
    margin: 0 auto;
}
#top-bar {
    width: 100%;
    margin: 0 auto;
}
.mobile-top-bar {
    display: none;
    position: absolute;
    left: 1em;
    bottom: 0px;
    z-index: 0;
}
div#container-wrap {
    background: url(http://rpcsandbox.wdfiles.com/local--files/component:vaughntheme/supremacy_theme.png) top left repeat-x;
    /*
    background: var(--container-wrap);
*/
}

/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px;
    /* FOR MENU */
    background: url(http://rpcsandbox.wdfiles.com/local--files/component%3Acommietheme/commie-logo.png) -15px 10px no-repeat;
}
#search-top-box {
    position: absolute;
    top: 79px;
    right: 9px;
    width: 250px;
    text-align: right;
}
#search-top-box-input {
    transition: background-color 0.5s ease;
    border: solid 1px #999;
    border-radius: var(--element-border-radius);
    color: #ccc;
    background-color: var(--search-input-color);
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: var(--search-input-hover);
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .8);
}
#search-top-box-form input[type=submit] {
    transition: background, border, color 0.5s ease;
    border: solid 1px #999;
    border-radius: var(--element-border-radius);
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #ccc;
    background-color: #633;
    background: var(--search-button);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255, 255, 255, .25);
    background-color: var(--text-header-color);
    background: var(--search-button-hover);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .8);
}
#login-status {
    color: #aaa;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #ddd;
}
#login-status ul a {
    color: #700;
    background: transparent;
}
#account-topbutton {
    background: #ccc;
    color: #700;
}
.printuser img.small {
    margin-right: 1px;
}
#header h1 {
    margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: #ffb90a;
    background: transparent;
    font-family: Tahoma, Geneva, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
}

/* TOP MENU */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: right;
}
#top-bar li {
    margin: 0;
}
#top-bar a {
    color: #fff;
    background: transparent;
}
#top-bar ul li {
    border: 0;
    position: relative;
}
#top-bar ul li ul {
    border: solid 1px var(--dark-border);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    border-top: 0;
}
#top-bar ul li a {
    border-left: solid 1px rgba(64, 64, 64, .1);
    border-right: solid 1px rgba(64, 64, 64, .1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--dropdown-background);
    color: var(--highlight-hover);
    border-left: solid 1px var(--dark-border);
    border-right: solid 1px var(--dark-border);
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid var(--border-light);
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
    color: var(--dropdown-text-color);
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--panel-background-highlight);
    color: var(--panel-link-hover);
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li,
#top-bar ul li ul li.sfhover,
#top-bar ul li ul li,
#top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
    color: white;
    transition: color 0.2s ease;
}
#top-bar ul li.sfhover a {
    color: var(--dropdown-text-color);
}
#top-bar ul li ul a,
#top-bar a:hover {
    color: var(--highlight-hover);
}
.top-bar ul li:last-of-type ul {
    right: 0;
}

/* IE7 HACK */
#top-bar ul > li > ul {
    *margin-top: -4px;
}

/* SIDE MENU */
#side-bar {
    clear: none;
    float: none;
    position: absolute;
    top: 0.5em;
    left: 2em;
    width: 17em;
    padding: 0;
    border: none;
    display: block;
}
#side-bar .side-block {
    padding: 10px;
    border: 1px solid var(--dark-border);
    border-radius: var(--element-border-radius);
    box-shadow: 0 2px 6px var(--highlight-shadow);
    background: #fff;
    margin-bottom: 15px;
}
#side-bar .side-area {
    padding: 10px;
}
#side-bar .heading {
    color: #5b1616;
    border-bottom: solid 1px var(--dark-border);
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
#side-bar p {
    margin: 0;
}
#side-bar div.menu-item {
    margin: 2px 0;
}
#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
    -webkit-filter: var(--icon-hack-recolor);
    filter: var(--icon-hack-recolor);
}
#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
    color: #999;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #666;
}
#side-bar div.menu-item.sub-item {}
#side-bar .collapsible-block-folded {
    background: url(http://www.rpcauthority.wikidot.com/local--files/nav:side/expand.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px var(--dark-border);
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #600;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: var(--highlight-hover);
    text-decoration: none;
}
#side-bar ul {
    list-style-type: none;
    padding: 0 5px 0;
}

/* CONTENT */
.content-panel {
    border: solid 1px #888880;
    border-radius: var(--element-border-radius)!important;
    background-color: #999990;
    margin: 10px 0 15px;
    margin-bottom: 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5), inset 0 1px rgba(255, 255, 255, 0.3), inset 0 10px rgba(255, 255, 255, 0.2), inset 0 10px 20px rgba(255, 255, 255, 0.25), inset 0 -15px 30px rgba(0, 0, 0, 0.1);
}
#main-content {
    margin: 0 2em 0 22em;
    padding: 1em;
    position: relative;
}

/* ACTUALLY HIDE HIDDEN TAGS */
#main-content .page-tags a[href^="/system:page-tags/tag/_"] {
    display: none;
}
#breadcrumbs {
    margin: -1em 0 1em;
    font-weight: 85%;
}

/* YUI-TABS */
.yui-navset .yui-content {
    background-color: #f5f5f5;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    transition: background-color 0.5s ease;
    background-color: var(--inactive-tab);
    background-image: none;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
/* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover {
    /* no hover effect for selected */
    background: var(--selected-tab);
    background-image: none;
    color: #fff;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: var(--hover-tab);
    background-image: none;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: var(--dark-border);
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: var(--dark-border);
}
.yui-navset li {
    line-height: normal;
}

/* FOOTER */
#footer {
    clear: both;
    font-size: 77%;
    background: #444;
    color: #bbb;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer .options {
    visibility: visible;
    display: block;
    float: right;
    width: 50%;
    font-size: 100%;
    text-align: right;
}
#footer a {
    color: #fff;
    background: transparent;
}

/* SOME NICE BOXES */
div.sexy-box {
    background: #eee;
    border: 1px solid #ccc;
    padding: 0 10px 12px;
    margin: 7px 4px 12px;
    overflow: hidden;
}
div.sexy-box div.image-container img {
    margin: 5px;
    padding: 2px;
    border: 1px solid #999;
}

/* Custom page content classes */
#page-content {
    min-height: 720px;
}
.unmargined > p {
    margin: 0;
    line-height: 100%;
    color: var(--text-header-color)
}
.content-panel {
    border: solid 1px #888880;
    border-radius: 10px;
    background-color: #999990;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5), inset 0 1px rgba(255, 255, 255, 0.3), inset 0 10px rgba(255, 255, 255, 0.2), inset 0 10px 20px rgba(255, 255, 255, 0.25), inset 0 -15px 30px rgba(0, 0, 0, 0.1);
}
.content-panel.standalone {
    background: #fcfdfb;
}
.content-panel.series {
    padding: 0 20px;
    margin-bottom: 20px;
}
.content-panel.centered {
    text-align: center;
}
.content-panel.left-column {
    float: left;
    width: 48%;
}
.content-panel.right-column {
    float: right;
    width: 48%;
}
.content-panel .panel-heading {
    padding: 2px 10px;
    color: #ffffff;
    font-size: 90%;
    font-weight: bold;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, .35);
}
.content-panel .panel-heading > p,
.content-panel .panel-footer > p {
    margin: 0;
}
.content-panel .panel-body {
    padding: 5px 10px;
    background: #fff9f0 url(http://www.rpcauthority.wikidot.com/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
    /* box-shadow: 1px 2px 3px rgba(0,0,0,.2) inset */
}
.content-panel .panel-footer {
    padding: 1px 10px;
    color: #fffff0;
    font-size: 80%;
    font-weight: bold;
    text-align: right;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, .5);
}
.content-panel .panel-footer a {
    color: #ffffff;
}
.content-panel .content-toc {
    float: right;
    padding: 0 20px;
    background-color: #fff;
    border: solid 1px #ccc;
    border-radius: var(--element-border-radius);
    margin: 20px 0 5px 5px;
    white-space: nowrap;
    box-shadow: inset 1px 2px 6px rgba(0, 0, 0, .15)
}
.alternate:nth-child(even) {
    background-color: rgba(255, 255, 255, .9);
}

/* Page Rating Module Customizations */
.page-rate-widget-box {
    display: inline-block;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, .5);
    margin-bottom: 10px;
    margin-right: 2em;
}
.page-rate-widget-box .rate-points {
    background-color: var(--rating-background) !important;
    border: solid 1px var(--dark-border);
    border-right: 0;
    border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #fff6f0;
    border-top: solid 1px var(--dark-border);
    border-bottom: solid 1px var(--dark-border);
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: var(--rating-background);
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    transition: background 0.5s ease;
    background: var(--rating-background);
    color: #fffff0;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: var(--rating-background);
    border: solid 1px var(--rating-background);
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: var(--rating-cancel-color);
}
.page-rate-widget-box .cancel a:hover {
    transition: background 0.5s ease;
    border-radius: 0 3px 3px 0;
    background: var(--rating-background);
    color: #fffff0;
    text-decoration: none;
}

/* Heritage Collection Rating Module */
.heritage-rating-module {
    display: inline-block;
    background-color: var(--rating-background);
    padding: 2px 8px 2px 5px;
    border: solid 1px #ccc066;
    border-radius: 8px;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .25);
    margin-right: 2em;
    margin-bottom: 10px;
}
.heritage-rating-module .page-rate-widget-box {
    box-shadow: none;
    margin-bottom: 0;
    margin-right: 0;
}
.heritage-rating-module .heritage-emblem {
    float: left;
    position: relative;
    top: -2px;
    left: 2px;
    height: 16px;
    width: 16px;
    overflow: visible;
    margin-right: 2px;
}
.heritage-rating-module .heritage-emblem img {
    width: 20px;
    height: 20px;
    border: 0;
}

/* Fixes for multi-line page tags */
#main-content .page-tags {
    margin: 1em 0 0;
    padding: 0;
}
#main-content .page-tags span {
    display: inline-block;
    padding: 0;
    max-width: 60%;
}
#main-content .page-tags a {
    display: inline-block;
    white-space: nowrap;
}

/* Standard Image Block */
.scp-image-block {
    border: solid 1px var(--dark-border);
    box-shadow: 0 1px 6px rgba(0, 0, 0, .25);
    width: 300px;
}
.scp-image-block.block-right {
    float: right;
    clear: right;
    margin: 0 2em 1em 2em;
}
.scp-image-block.block-left {
    float: left;
    clear: left;
    margin: 0 2em 1em 0;
}
.scp-image-block.block-center {
    margin-right: auto;
    margin-left: auto;
}
.scp-image-block img {
    border: 0;
    width: 300px;
}
.scp-image-block .scp-image-caption {
    background-color: #eee;
    border-top: solid 1px var(--dark-border);
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    width: 300px;
}
.scp-image-block > p {
    margin: 0;
}
.scp-image-block .scp-image-caption > p {
    margin: 0;
    padding: 0 10px;
}

/* Wikiwalk Navigation */
.footer-wikiwalk-nav {
    font-weight: bold;
    font-size: 75%;
}

/* Forum Customizations */
.forum-thread-box .description-block {
    padding: .5em 1em;
    border-radius: var(--element-border-radius);
    box-shadow: 0 1px 5px rgba(0, 0, 0, .15), inset 0 1px 0 rgba(255, 255, 255, .8), inset 0 10px 5px rgba(255, 255, 255, .25), inset 0 -15px 30px rgba(0, 0, 0, .1)
}
.thread-container .post .head {
    padding: 0.5em 1em;
    background-color: #eee;
    background: linear-gradient(to right, #eee, #eeecec);
    box-shadow: inset 2px 3px 6px rgba(0, 0, 0, .15);
    border-radius: 5px 5px 0 0;
}
.forum-group table td {
    border: 1px solid #DDD;
    padding: 2px 2px;
    background: var(--forum-entry-background);
}
.forum-group table td.name .title {
    font-weight: bold;
    margin-left: 5px;
}
.forum-group table td.name .description {
    color: #777;
    font-size: 87%;
    margin-left: 5px;
}
.thread-container .post .head {
    padding: 0.5em 1em;
    background-color: var(--post-head-color);
    background: none;
    box-shadow: inset 0px 0px 60px 0px rgba(0, 0, 0, .15);
    border-radius: 2px 2px 0 0;
}
.forum-category-box table td {
    border: 1px solid var(--forum-border) !important;
    padding: 5px 10px;
    background: var(--forum-background);
}

/* Hide Forum Signatures */
.signature {
    display: none !important;
}

/* Ruby by Nanimono Demonai */
.ruby,
ruby {
    display: inline-table;
    text-align: center;
    white-space: nowrap;
    line-height: 1;
    height: 1em;
    vertical-align: text-bottom;
}
.rt,
rt {
    display: table-header-group;
    font-size: 0.6em;
    line-height: 1.1;
    text-align: center;
    white-space: nowrap;
}

/* Keycap */
.keycap {
    border: 1px solid;
    border-color: #ddd #bbb #bbb #ddd;
    border-bottom-width: 2px;
    -moz-border-radius: var(--element-border-radius);
    -webkit-border-radius: var(--element-border-radius);
    border-radius: var(--element-border-radius);
    background-color: #f9f9f9;
    padding: 1px 3px;
    font-family: inherit;
    font-size: 0.85em;
    white-space: nowrap;
}

/* tag style */
.tags {
    display: inline-block;
    margin: 0 0 0 5px;
    padding: 3px 5px 3px 0px;
    height: 13px;
    line-height: 13px;
    font-size: 11px;
    background: #666;
    color: #fff;
    text-decoration: none;
    -moz-border-radius-bottomright: 4px;
    -webkit-border-bottom-right-radius: 4px;
    border-bottom-right-radius: 4px;
    -moz-border-radius-topright: 4px;
    -webkit-border-top-right-radius: 4px;
    border-top-right-radius: 4px;
}
.tags:before {
    content: "";
    padding: 0px 1px 3px 1px;
    float: left;
    position: relative;
    top: -3px;
    left: -10px;
    width: 0;
    height: 0;
    border-color: transparent #666 transparent transparent;
    border-style: solid;
    border-width: 8px 8px 8px 0px;
}
.tags:after {
    content: "";
    position: relative;
    top: 4.5px;
    left: -8px;
    float: left;
    width: 4px;
    height: 4px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    background: #fff;
    -moz-box-shadow: -1px -1px 2px #004977;
    -webkit-box-shadow: -1px -1px 2px #333;
    box-shadow: -1px -1px 2px #333;
}

/* Display Black Block by Nanimono Demonai */
.bblock {
    color: #000000;
    background-color: #000000;
    transition: 2s;
    text-decoration: none;
}
.bblock:hover {
    background-color: #000000;
    color: #006600;
    text-decoration: none;
}
.dblock {
    color: #000000;
    background-color: #000000;
    transition: 2s;
    text-decoration: none;
}
.dblock:hover {
    background-color: transparent;
    text-decoration: none;
}

/*
    2011-11-13 Minobe Hiroyuki @ Marguerite Site
    www.marguerite.jp/Nihongo/WWW/CSSTips/EmphasizeDots-CSS3.html
    Edited for the SCP Foundation by Nanimono_Demonai
*/
.emph {
    text-emphasis-style: dot;
    -webkit-text-emphasis-style: dot;
}
}
