css:
@-moz-document domain("telegram.org") {


.im_page_wrap,
.tg_head_split,
.im_message_wrap,
.im_send_form {
    max-width: 100%;
}

.im_send_panel_wrap {
    max-width: calc(100% - 60px);
    margin-left: 0;
}

.im_dialogs_col_wrap {
    width: /*[[sidebar-width]]*/;
}

.im_history_col_wrap {
    width: calc(100% - /*[[sidebar-width]]*/);
}

body.non_osx,
.non_osx .im_message_author_wrap, .non_osx .im_message_fwd_author_wrap {
    font-size: /*[[chat-font-size]]*/;
}


}
