css:
@-moz-document domain("service.tuhh.de") {
*{
    color: #a7a7a7 !important;
    box-shadow: none !important;
    text-decoration: none !important;
    outline: none !important
}

/*HELLSTES DESIGN*/
.divloginstatus, .mod_n, #wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li > ul, .qis_konto
{
    background-color: #253447 !important;
    background: none 
}
/*DUNKLES DESIGN*/
body, .divfoot_navigation, .divlinks, input, select, .ChangeReadOnlyInput, .mod_n_even, .tabelle1, .tabelle1_alignleft, .tabelle1_aligncenter, .tabelle1_alignright, #wrapper > div.divcontent > div.content_max_portal_qis > center
{
    background-color: #141d26 !important;
    background: none
}
/*HELLES DESIGN*/
.divcontent, .content_max_portal_qis, #makronavigation, .mikronavi_list, .content, #wrapper > div.divcontent > div.content > div > fieldset, #wrapper > div.divcontent > div.content > div > form > div > fieldset, #wrapper > div.divcontent > div.content_max_portal_qis > div > fieldset, .mod_n_odd,  #wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > ul > li:nth-child(even), th
{
    background-color: #1b2836 !important;
    background: none
}

/* BLACK TEXT*/
#wrapper > div.divcontent > div.content > form > table:nth-child(4) > tbody > tr:nth-child(1) > th *
{
    color: black !important
}
/* CORNERS */
.pictures_portal_border, #wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > ul
{
    border-radius: 20px
}
/*   LINKS     */
a, a:visited,#wrapper > div.divloginstatus > a > u
{
    color: #1ea1f2 !important
}
a:hover{
    color: #1ec4f2 !important
}
a:active{
    color: red !important
}

/* BUTTONS */
input.submit, .linkAsButton, #wrapper > div.divloginstatus > a, .links3, .links3, .links3, input, select, #wrapper > div.divcontent > div.content > div > form > a, .qiszul_button, button, #wrapper > div.divcontent > div.content > form > ul > li > a.regular
{
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
    
    -webkit-transition: background 200ms linear,border 200ms linear ;
    -ms-transition: background 200ms linear,border 200ms linear;
    transition: background 200ms linear,border 200ms linear ;
}
input.submit:hover, .linkAsButton:hover, #wrapper > div.divloginstatus > a:hover, .links3:focus, .links3:hover, #wrapper > div.divcontent > div.content > div > form > a:hover, .qiszul_button:hover, button:hover, #wrapper > div.divcontent > div.content > form > ul > li > a.regular:hover
{
    background: #253447 !important;
    border: 1px solid black !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
}


}

@-moz-document domain("service.tuhh.de") {
#wrapper > div.divloginstatus {
    padding: 5px 7px
}
#makronavigation {
    margin: 15px 10px 0px 15px;
    padding: 5px 5px 65px 15px;
    background-color: #253447 !important;
    border-radius: 20px;
    width: 230px
}
.divcontent {
    padding: 0px 0px 30px 0px;
    border-radius: 25px
}
#makronavigation > ul > li {
    border: 0px solid #1da1f2 !important;
    background: transparent !important;
    padding: 4px 12px;
}
#makronavigation > ul > li > a,
#makronavigation > ul > li > a:hover {
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 0px !important;
    margin: 0px;
    text-align: center;
    display: block;
    width: 170px !important;

    -webkit-transition: background 200ms linear, border 200ms linear;
    -ms-transition: background 200ms linear, border 200ms linear;
    transition: background 200ms linear, border 200ms linear;
}
#makronavigation > ul > li > a:hover {
    background: #1b2836 !important;
    border: 1px solid black !important;
}
.mikronavi_list,
input,
select {
    border: transparent !important
}
#wrapper > div.divcontent > div.content_max_portal_qis > div > form > div > ul > li > a {
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
    margin: 25px 5px !important;

    -webkit-transition: background 200ms linear, border 200ms linear;
    -ms-transition: background 200ms linear, border 200ms linear;
    transition: background 200ms linear, border 200ms linear;
}
#wrapper > div.divcontent > div.content_max_portal_qis > div > form > div > ul > li > a:hover {
    background: #253447 !important;
    border: 1px solid black !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
}
#wrapper > div.divcontent > div.content_max_portal_qis > div > form > div > ul > li:nth-child(n+1) {
    margin: 15px 0px 0px 0px
}
.qiszul_button,
.qiszul_button:hover {
    padding: 2px 8px !important
}
#wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li {
    padding-top: 15px !important;
    text-decoration: underline
}
#wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > a.Konto {
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    border-radius: 100px;
    padding: 2px 12px;
}
#wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > ul {
    padding: 10px 15px;
    margin: 10px 10px;
    margin-bottom: 0px
}
#wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > ul > li:nth-child(n) > img,
#wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li:nth-child(n) > img,
#wrapper > div.divcontent > div.content > ul > li > ul > li > img,
#wrapper > div.divcontent > div.content > ul > li > img {
    transform: translateY(2px) scale(1.25) !important;
    border-radius: 10px
}
#wrapper > div.divcontent > div.content > form > ul > li > a:nth-child(n) > img:hover{
    border: 3px dashed grey !important
}
#wrapper > div.divcontent > div.content > form > ul > li > a:nth-child(n) > img{
    content: 'Text' !important
}
#wrapper > div.divcontent > div.content > form > ul > li > ul > li:nth-child(1){
    margin-top: 15px
}
#wrapper > div.divcontent > div.content > form > table:nth-child(n){
    margin-top: 10px
}
#wrapper > div.divcontent > div.content > form > table:nth-child(3) > tbody *, #wrapper > div.divcontent > div.content > form > table:nth-child(2) > tbody *
{
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    border-radius: 100px;
    padding: 4px 12px;
    
    -webkit-transition: background 200ms linear,border 200ms linear ;
    -ms-transition: background 200ms linear,border 200ms linear;
    transition: background 200ms linear,border 200ms linear ;
}
#wrapper > div.divcontent > div.content > form > table:nth-child(n) > tbody > tr:nth-child(n) > th, #wrapper > div.divcontent > div.content > form > table:nth-child(n) > tbody > tr:nth-child(n) > td.tabelle1_alignleft{
    text-align: center
}
#wrapper > div.divcontent > div.content > form > table:nth-child(4) *{
    border: transparent !important
}
#wrapper > div.divcontent > div.content > form > table:nth-child(4) > tbody > tr:nth-child(1) > th{
    text-decoration: underline !important;
    border: 1px solid #1da1f2 !important;
    background: #116fa8 !important;
    color: black !important;
    border-radius: 100px;
    padding: 1px 12px;
    font-size: 125%
}

.tabelle1, .tabelle1_alignleft, .tabelle1_aligncenter, .tabelle1_alignright {
    line-height: 20px
}
.divloginstatus, .mod_n, #wrapper > div.divcontent > div.content > ul > li > ul > li > ul > li > ul, .qis_konto{
    line-height: initial !important
}

}

@-moz-document url("https://www.service.tuhh.de/qissos/rds?state=user&type=4&re=last&category=auth.logout&breadCrumbSource=portal"), url("https://www.service.tuhh.de/qissos/rds?state=user&type=0&breadCrumbSource=portal"), url("https://www.service.tuhh.de/qissos/rds?state=user&type=0&category=menu.browse&breadCrumbSource=&startpage=portal.vm&chco=y") {
#makronavigation{
    background-color: #1b2836 !important
}
}
