css:
/* i really want this to be global */
::-webkit-scrollbar {
    width: 15px;
}

::-webkit-scrollbar-track {
    background-color: inherit;
}

::-webkit-scrollbar-thumb {
    background-color: #6d6d6d;
}

.link {
    color: #999;
}

.link:hover {
    color: #cacaca !important;
}

.loading {
    background: #161616
}

body,
.page-root__content,
.overlay_light {
    background: #212121
}

.theme-white .deco-shade {
    background-color: #212121
}

.page-station__title,
.button__label,
.player-controls__info,
.track__info,
.nav__item,
.settings-stream__title > span,
.button-radio-slider__item > span,
.multi-auth.popup,
.theme-white .deco-link,
.station-item__title,
.multi-auth__user-name.typo,
.d-payment-show.multi-auth__item.typo-main.deco-popup-menu__item,
.station-popup__station-title,
.station-popup__content,
.station-item__operation-title {
    color: #EEEEEE;
}

.theme-white .deco-link_no-hover-color:hover {
    color: #EEEEEE !important;
}

.d-toggler .d-toggler__text,
.d-equalizer-band__max,
.d-equalizer-band__zero,
.d-equalizer-band__min,
.d-equalizer-band__hz,
.deco-popup-menu__item {
    color: #c1c1c1;
}

.station-popup__station-visibility-help {
    border: 1px solid #c1c1c1;
    color: #c1c1c1;
}

.icon_lines,
.icon_size_L,
.icon_pp,
.icon_vertical_dots {
    filter: invert(1);
}

.icon_settings,
.nav__item_arrow::after,
.d-equalizer .d-icon_equalizer,
.icon_lock-opened,
.icon_settings2,
.icon_close {
    filter: invert(0.8);
}

.d-equalizer_active .d-icon_equalizer {
    filter: invert(0.1);
}

.nav__item_arrow:hover:after,
.nav__item_selected.nav__item_arrow:after,
.station-item:hover > .station-item__extend .icon_vertical_dots,
.station-item__operation:hover > .icon_settings2 {
    filter: none;
}

.icon-theme-white .d-icon_volume-sprite,
body .local-icon-theme-white .d-icon_volume-sprite,
body .local-icon-theme-white.d-icon_volume-sprite {
    filter: invert(1);
}

.logo.logo__radio {
    background-image: url("https://sy1eront.tk/etc/yandex_radio/logo.svg");
}

.button_transparent {
    border-color: rgba(255, 255, 255, 0.2);
}

.button_transparent:hover {
    border-color: rgba(255, 255, 255, 0.4);
}

.button_round {
    background: rgba(0, 0, 0, 0.8);
}

.button_round:hover {
    background: rgba(0, 0, 0, 0.6);
}

.button_round.button_transparent {
    background: rgba(255, 255, 255, 0.0);
    box-shadow: inset 0 0 0 2px rgba(255, 255, 255, 0.3);
}

.button_round.button_transparent:hover {
    background: rgba(255, 255, 255, 0.2);
    box-shadow: inset 0 0 0 2px rgba(255, 255, 255, 0.5);
}

.progress__shape_play .progress__path {
    stroke: rgba(255, 255, 255, 0.8);
}

.progress__shape_loading .progress__path,
.progress__shape_bg .progress__path {
    stroke: rgba(255, 255, 255, 0.3);
}

.player-controls__play:hover .progress__path {
    stroke-dasharray: none;
    stroke: rgba(255, 255, 255, 255)
}

.nav__level,
.popup,
.volume__bar,
.theme-white .deco-popup-menu {
    background: #424242;
}

.volume__control {
    border: 1px #4c4c4c solid;
    background: #424242
}

.volume_top .volume__control:before {
    border-color: #4c4c4c transparent transparent transparent
}

.volume_bottom .volume__control:after,
.volume_top .volume__control:after,
.volume_bottom .volume__control:after {
    border-color: transparent transparent #424242 transparent
}

.nav__item:hover > span,
.nav__item_selected,
.button-radio-slider__item.active > span,
.station-item:hover > .station-item__info .station-item__title,
.station-item_active > .station-item__info .station-item__title,
.deco-button-stylable > .station-item__info .station-item__title,
.station-popup__bar button span span,
.station-item__operation:hover > .station-item__operation-title {
    color: #424242;
}

.station-item:hover > .station-item__info .station-item__owner,
.station-item_active > .station-item__info .station-item__owner {
    color: #616161;
}

.nav__level,
.body_state_pushed .nav__level_0 {
    border-right: 1px #616161 solid
}

.button-radio-slider {
    background-color: #616161;
}

.button-radio-slider__item:hover,
.theme-white .deco-popup-menu__item:hover,
.station-item__actions-content {
    background-color: #6d6d6d;
}

.theme-white .deco-devider {
    border-color: #616161
}

.theme-white .deco-pane-popup,
.local-theme-white.deco-pane-popup,
.local-theme-white .deco-pane-popup {
    color: #222;
    background-color: #424242;
    border-color: #444;
}

/* fixes */
.station-popup__page_type_colors,
.station-popup__page_type_images {
    text-align: center;
}
