css:
@-moz-document url-prefix("https://slickdeals.net/deal-alerts/") {
.dealAlertHeader {
	height: auto;
	padding-top:    .3em;
	padding-bottom: .3em;
}
#myDealAlerts h4, .dAStatus {
	float: none;
	margin-top: 0;
}
.dAStatus {line-height: 1}

.dealAlertHeader .icon.icon-arrow-down,
.dealAlertHeader .icon.icon-arrow-up,
.icon.pause {
	margin-top: .3rem;
}
.dealAlertHeader .button {
	height: auto;
	line-height: 1.7;
}
}
