css:
@-moz-document url-prefix(http://www.linuxfromscratch.org) {

/*text&background*/
html,
body,
tbody,
thead,
th,
tr,
td,
blockquote,
li,
ul,
h1,
h2,
h3,
h4,
h5,
h6,
font,
strong,
p,
form,
footer {
    color: #333 !important;
    background: #DDD !important;
    font-family: Lato, Arial, San-serif;
}
select,
button {
    background: #DDD !important;
}
select,
span,
pre,
label,
div,
dd {
    color: #333 !important;
}

#banner img {
    display: block;
    background: url(https://s15.postimg.cc/nqd339b7f/Screenshot_1.png);
    max-width: 0px;
    height: 90px;
    padding-left: 265px;
    
}

/*links*/
a {
    color: #333 !important;
    font-style: oblique;
}
a:visited {
    color: #333 !important;
    font-style: italic;
}

input#sbb {
    background-color:#DDD;
    color:#333;
    border: outset 1px;
    border-radius: 15px;
}

input[type="text"],
textarea {
    color: #DDD;
    border-radius: 15px;
}
/*highlight*/
textarea,
pre,
code {
    font-family: "Cutive Mono","Lucida Console", monospace;
    color: #DDD;
    background: #333 !important;
}
.book .titlepage {
    background-color: #DDD;
}
div.navheader,
div.navfooter {
    background: #DDD;
    border: 0;
}
div.sect1 {
    width: 70%;
    padding: 20px;
    margin-left: auto;
    margin-right: auto;
}
div.navbar {
    color: #DDD;
    text-align: left;
    margin: -1px;
    background-color: #777;
    border: solid 1px;
    border-color: #DDD;
}
.menu {
    color: #DDD;
}
}
