css:
@-moz-document domain("fefe.de") {
/* Layers */

layer-0,
body,
body > h2:first-child > a {
	background-color: hsl(0, 0%, 5%) !important;
	color: hsl(0, 0%, 95%) !important;
	border-color: hsl(0, 0%, 0%) !important;
}

layer-1,
h3,
ul {
	background-color: hsl(0, 0%, 10%) !important;
	color: hsl(0, 0%, 95%) !important;
	border-color: hsl(0, 0%, 2.5%) !important;	
}

layer-2,
ul > li {
	background-color: hsl(0, 0%, 15%) !important;
	color: hsl(0, 0%, 95%) !important;
	border-color: hsl(0, 0%, 7.5%) !important;	
}

layer-3,
ul > li blockquote {
	background-color: hsl(0, 0%, 20%) !important;
	color: hsl(0, 0%, 95%) !important;
	border-color: hsl(0, 0%, 12.5%) !important;	
}

/* Elements */

* {
	font-family: "Open Sans", "Roboto", sans-serif, serif !important;
	font-size: 10pt !important;
}

a,
a:visited {
	color: hsl(210, 100%, 70%) !important;
}
a:hover,
a:active,
a:focus {
	color: hsl(210, 100%, 80%) !important;
}

/* Site Title */
h2 {
	font-family: "Impact", monospace !important;
	font-weight: bolder !important;
	font-size: 32pt !important;
	line-height: 1.5em !important;
	margin: 0px !important;
}
h2 > a {
	font-size: inherit !important;
	display: inline-block;
}

/* Site Subtext */
h2 + b {
}

/* Group Header */
h3 {
	background: white;
	margin-bottom: 0px !important;
	
	padding: 5pt 15pt;
	
	border-width: 1px;
	border-style: solid;
	border-bottom-style: none;
	
	font-size: 14pt !important;
}

/* Group Content */
ul {
	list-style-type: none !important;
	-webkit-margin-before: 0px;
	-webkit-margin-after: 0px;
	-webkit-padding-start: 0px;
	
	padding-left: 5pt;
	padding-right: 5pt;
	padding-bottom: 5pt;
	
	border-width: 1px;
	border-style: solid;
	border-top-style: none;
}

/* Group Entry */
ul > li {
	padding: 5pt;
	border-width: 1px;
	border-style: solid;
	margin: 0px;
}

ul > li + li {
	margin-top: 20px;
}

ul > li > p {
	margin: 0px;
}

ul > li > p + p {
	margin-top: 1.0em;
}

ul > li blockquote  {
	border-width: 1px;
	border-style: solid;
	padding: 5pt;
	margin: 5pt;
	margin-left: 1em;
}

}
