css:
@-moz-document domain("www.chatwork.com") {
/*リンク色*/
a
{
    color: #00afaf !important
}

div {
   color: #AAAAAA !important
}

#_wrapper
{
    background: #343434 !important;
}

::-webkit-scrollbar
{
    background-color: #000 !important;
    box-shadow: inset 0px 0px 3px #999;
}

::-webkit-scrollbar-thumb
{
    border-radius: 10px;
    border-color: transparent !important;
    background-color: #6a6a6a !important;
}

::-webkit-scrollbar-thumb:hover
{
    background-color: #FFFFFF !important;
}

::-webkit-scrollbar-thumb:active
{
    background-color: #AAAAAA;
}
/*ヘッダ*/
.chatRoomHeader__titleContainer,
.chatRoomHeader,
.chatInput__tools,
.chatSendArea,
.chatInput__textarea,
.roomListHeader
{
	    border-left: 0px solid #000000;
    border-bottom: 0px solid #000000;
	background: #4F5151;
    color: #B1B1B1;
}

/*プロフィールのとこ*/
.myStatusButtonHover,
.myStatusButtonSelected,
.helpMenuSelected,
.helpMenu:hover
{
    background: #000000 !important;
}

/*基本的な文字色*/
.roomDescription__headerText,
.roomTask__headerText,
.timelineMessage__message,
.timelineMessage__timeStamp,
.limitedOver
{
    color: #AcAcAc;
}

/*背景*/
.roomTask,
.roomTask__header,
.roomDescription__header,
.timeLine,
.roomInformationContent,
.roomListItem 
{
        background: #343434;
}


.timelineMessage:hover,
._message:hover,
.chatTimeLineMessageHover:hover,
.timelineMessage.chatTimeLineMessageHover,
.timelineMessage--jumpMessage
{
    background: #414141;
    border-top: 1px solid #006666;
    border-bottom: 1px solid #006666;
}


.timelineMessage .actionNav,
.actionNav__item
{
	background: #595959;
	color: #FFFFFF;
    border-right: #595959;
    border-bottom: #595959;
    border-left: #595959;
    border-radius: 0 0 3px 3px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
}

.actionNav {
    height: 25px;
    padding: 0 5px;
    background: #595959;
    border-right: #595959;
    border-bottom: #595959;
    border-left: #595959;
    border-radius: 0 0 3px 3px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    cursor: pointer;
}

.addTaskContainer__formContainer
{
    background: #999999;
}

.timelineMessage--mention,
.chatInput
{
        background: #454545;
	border-top: 1px solid #000000;
    border-bottom: 1px solid #000000;
}

.timelineMessage--border
{
		border-top: 1px solid #000000;
}

.chatInfo > .title,
.chatInfo,
.chatInfoTaskContentArea,
.chatInfoTaskMetaArea,
._task,
.taskMine
{
	   background: #545454;
}


.chatCode
{
	margin: 5px 0;
	color: #A1A1A1;
	border: 1px solid #000;
	border-radius: 3px;
	background: #010101;
}

.roomContent
{
	border-right: 1px solid #000000;
    border-left: 1px solid #000000;
}

.timeLine__dateHead
{
	position: relative;
    margin: 10px 0;
    border-top: 1px solid #000000;
}

.timeLine__dateHeadBody,
.timeLine__dateHeadText
{
		background: #121223;
    color: #A1A1A1;
}

.icoFontClock
{
	display: none;
}

.chatInput
{
border-radius: 3px;
    border: 0px solid #111111;
}

.chatInput__tools
{
	border-radius: 3px 3px 0 0;
	border-left:0px solid #000000;
    border-bottom: 0px solid #000000;
}

.chatInput__textarea
{
	border-left: 0px solid #000000;
    border-bottom: 0px solid #000000;
    background: #4F5151;
    color: #B1B1B1;
}

.timelineLinkAppend {
		display: inline-block;
    padding: 1px 5px;
    border: 1px solid #000000;
    background: #121223;
    font-size: 10px;
    border-radius: 3px;
}

.roomListItem {
		    background: #111111;
}



.roomListItem--selected{
	    background: #414141;
}

.roomListItem__roomName,
.roomListHeader__filterName,
.roomListHeader__unreadRoomNum{
			    color: #B1B1B1;
}

.roomListItem:hover {
    background-color: #595959;
}

.taskListItem {
    position: relative;
    box-sizing: border-box;
    margin-bottom: 10px;
    padding: 8px;
    border: 1px solid #afafaf;
    border-radius: 3px;
    background-color: #595959;
				    color: #B1B1B1;
}

.roomList
{
    background-color: #000000;
}

/* ~~~未読~~~ */
.timeLine__unreadLineBody {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    height: 19px;
    line-height: 19px;
    padding: 0 8px;
    background-color: #111111;
    border-radius: 10px;
    border: 1px solid #00c1c1;
}
.timeLine__unreadLineText {
    color: #00c3c3
}
.timeLine__unreadLineBorder {
    position: relative;
    margin: 9px 0;
    height: 1px;
    background-color: #00FFFF;
}

/* ~~~ルーム概要~~~*/
.roomDescription__descriptionText {
        color: #B1B1B1;
}
pre {
        color: #B1B1B1;
}

/* ~~~グループチャット招待~~~ */
.roomDescription__inviteButton {
    display: block;
    padding: 2px 5px;
    border: 1px solid #00c3c3;
    background: #121212;
    border-radius: 3px;
    text-align: center;
}
.roomDescription__inviteButton:hover {
    background: #128181;
}

/*タスク*/
._cwCBLabel {
color : #b1b1b1;    
}
label._cwCBLabel.cwTextUnselectable.linkStatus{
    color : #b1b1b1;   
}

/*ユーザー名*/
.timelineMessage__userName {
    display: inline;
    font-weight: 300;
    cursor: pointer;
    color: #F1F1F1;
}

/*チャットナビ*/
.actionNav {
    height: 25px;
    padding: 0 5px;
    background: #000000;
    border-right: 1px solid #AAAAAA;
    border-bottom: 1px solid #FFFFFF;
    border-left: 1px solid #FFFFFF;
    border-radius: 0 0 3px 3px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    cursor: pointer;
}

/*謎広告off*/
.commercialAnnounceBanner--open {
    top: 1
}

/*タイムラインクリック時*/
.timeLine {
        border: none;
}

/* 招待メッセージ */
.timelineMessage__systemMessage {
    padding: 8px;
    background-color: #595959;
}

/* 概要hover */
.roomDescription__description.roomDescription__description--overflow:hover {
        background-color: #595959;
}

/* チャット選択 */
.roomListHeader__roomFilterListDropDownLabel {
    background-color: #595959;
}
.roomListHeader__roomFilterListDropDownLabel:hover {
    background-color: #595959;
}

/* チャット人数 */
._showDescription.chatRoomHeaderMemberList__detail {
    background-color: #000000;
}
}
