css:
@-moz-document url-prefix("https://scholar.google.com"),regexp("https?://scholar.google.(com|([a-z]{2}))(.[a-z]{2})?.")

{#gs_top {
    background-color: #000000;
    color: #ffffff ;
  }
  
  .gs_rt a:link, .gs_rt a:link b, .gs_rt2 a:link, .gs_rt2 a:link b {
    color: #00acee;
  }

  .gs_rt a:visited, .gs_rt a:visited b, .gs_rt2 a:visited, .gs_rt2 a:visited b, a:visited, a.gs_fl:visited, .gs_fl a:visited {
    color: #c39cc9;
  }
  
  a.gs_fl:link, .gs_fl a:link, .gs_ctc, a:link, #gs_n a:link, #gs_n a:visited, #gs_ftr a:link, #gs_ftr a:visited {
    color: #00acee;
  }
  
  .gs_rs b, .gs_rt b, .gs_rt2 b {
    color: #ffffff;
  }
   
  #gs_lnv a:link, #gs_lnv a:visited {
    color: #ffffff;
  }
  
  .gs_a, .gs_a a:link, .gs_a a:visited {
    color: #47855b;
  }

  #gs_hdr {
    background-color: #1f1f1f;
  }
  
  .gs_md_se, .gs_md_wn, .gs_md_wm {
    background: #1f1f1f;
  }
  .gs_bdy_sb_sec a:link, .gs_bdy_sb_sec a:visited {
	color: #ffffff;
  }
  button:not(.gs_btn_act) {
    filter: invert(90%);
  }
  #gs_ab {
	background-color: #000000;
  }
  #gs_ylo_md, #gs_ad_md {
    filter: invert(90%);
    border: black;
  }

  th.gsc_a_x, th.gsc_a_t, th.gsc_a_c, th.gsc_a_y {
    box-sizing: border-box;
    background: #0d0d0d;
  }

  .gsc_rsb_f:link, .gsc_rsb_f:visited {
    color: #ccc;
  }

  #gs_hp_nav a:link, #gs_hp_nav a:visited {
    color: hsla(0, 0%, 60%, 1);
  }

  a.gs_in_ib .gs_ico {
    filter: invert(100%);
  }

  a.gs_in_ib.gs_md_li .gs_ico {
    filter: unset;
  }

  .gs_rt a:link b, .gs_rt2 a:link b {
  	color: #66d4ff;
  }

  .gs_chk {
    filter: invert(100%);
  }

