css:
@-moz-document url-prefix('https://www.masterani.me'), url-prefix('http://www.masterani.me')
{
  #navigation .ui.inverted.menu .item.logo .primary {
      color: #09e5b8;
  }

  #navigation .ui.inverted.menu .item.logo svg.circle {
      fill: #09e5b8;
  }

  #navigation .ui.inverted.menu .item.search .menu {
      border-bottom: 2px solid #09e5b8;
  }

  .ui.master.header>.border {
      border-bottom: solid 1px #09e5b8;
  }

  .ui.master.header {
      color: #000000;
  }

  body.dark {
      color: #000000;
  }

  div.ui.toggle.checkbox label {
      color: #000000;
  }

  div.four.wide.field label {
      color: #000000;
  }

  div.twelve.wide.field label {
      color: #000000;
  }

  div.ui.toggle.checkbox.small label {
      color: #000000;
  }

  div.limit:hover {
      color: #09e5b8;
  }

  .ui.thumbnails>.thumbnail>.holder>.overlay>.play svg {
      fill: #09e5b8;
  }

  div.limit {
      color: #000;
  }

  div.title:hover {
      color: #09e5b8;
  }

  div.title {
      color: #b8fff0;
  }

  a.item:hover {
      color: #09e5b8;
  }

  .ui.master.table .body>.row:hover>.cell.cover {
      box-shadow: 0px 5px 10px rgba(0,0,0,.2);
  }

  div.links a {
      color: #000;
  }

  div.links a:hover {
      color: #09e5b8;
  }

  div.cell.info a:hover {
      color: #09e5b8;
  }

  div.row:hover {
      background-color: rgba(0,0,0,0);
  }

  svg.s.n-m.yellow.fix path {
      fill: #b8fff0;
  }

  div.cell.info a {
      color: #000;
  }

  body {
      background: #FFFFFF;
  }

  ::selection {
      background-color: #B4D5FE;
  }

  div.item {
      color: #000;
  }

  .ui.dropdown.master.button .master.menu {
      background: #b8fff0;
  }

  .ui.tag.horizontal.list .item {
      border-bottom-width: 1px;
  }

  .ui.sections.list .item {
      border: none;
  }

  .ui.dropdown.master.button .master.menu .item svg {
      fill: #000;
  }

  div.post-body .comment__footer {
      background-color: #fff;
      color: #b9b9b9;
      box-shadow: none;
  }

  .dark .tab-conversation.active>a {
      color: #000;
  }

  .dark .nav-tab>a {
      color: #b9b9b9;
  }

  .dark .nav-tab>a:hover {
      color: #09e5b8;
  }

  .dark .icon-cog, .dark .icon-export, .dark .label {
      color: #000;
  }

  .dark .icon-cog, .dark .icon-export, .dark .label:hover {
      color: #09e5b8;
  }

  .dark .comment__footer .vote-down, .dark .comment__footer a, .dark .comment__header .vote-down, .dark .comment__header a {
      color: #b9b9b9;
  }

  .dark .comment__footer .author, .dark .comment__header .author:hover {
      color: #000;
  }

  .publisher-anchor-color a:hover {
      color: #09e5b8;
  }

  .publisher-anchor-color a {
      color: #b8fff0;
  }

  .publisher-anchor-color a:visited {
      color: #5e97f6;
  }

  html body footer {
      background-color: #121a21;
      border-top: none;
      box-shadow: 0 -1px 2px 0 rgba(0,0,0,.25);
  }

  .badge {
      color: #09e5b8;
  }

  div.info h1 a:hover {
      color: #09e5b8;
  }

  div.info h1 a {
      color: #000;
  }

  .ui.basic.button:focus, .ui.basic.button:hover, .ui.basic.buttons .button:focus, .ui.basic.buttons .button:hover {
      background-color: #304049
  ;
  }
  footer .item.brand img {
      height: auto;
      width: 2.25em;
      padding-left: 36px;
      padding-top: 36px;
      background: url(https://i.imgur.com/NUxAx7v.png) no-repeat;
  }

  .ui.pagination.menu.master .item.active:hover {
      background-color: #221f1f;
  }

  .ui.pagination.menu.master .item.icon svg:hover {
      fill: #09e5b8;
      color: #09e5b8;
  }

  .ui.pagination.menu.master .item.icon {
      fill: #09e5b8;
      color: #09e5b8;
  }
}
