css:
@-moz-document domain("independent.co.uk") {
.i-amphtml-layout-size-defined {
    overflow: hidden !important;
    display: none;
}

.top-container.video.sticky:not(.closed) .hero-wrapper {
    display: none;
}

.body-content .inline-block, .body-content .inline-media, .body-content figure {
    display: none;
}

.sidebar {
    display: block;
    display: none;
}

.life .full-article .gs-article {
    display: none;
}

.life .full-article .share-bar-wrapper {
    padding: 0;
    display: none;
}

.hero-aside-video {
    Display: none;
}

.full-article .main {
    clear: both;
}

.full-article .main {
    justify-content: space-around;
    max-width: 10000px;
}

.top-container.video {
    background: #fff;
    color: #fff;
    margin-bottom: 20px;
}

a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video {
    color: black;
}
}
