css:
@-moz-document domain("52pojie.cn") {
div[id="nv"] {
    display:none
}
div[class="comiis_nav"] {
    display:none
}
div[class="z"] {
    display:none
}
div[class="y"] {
    display:none
}
h2 {
    display:none
}
div[class="bm bml pbn"] {
    display:none
}
td[class="scbar_hot_td"] {
    display:none
}
#scbar {
    float: left;
    padding: 0 0 0 733px;
    width: 272px;
    position: absolute;
    overflow: hidden;
    line-height: 42px;
    border-top: 0;
    border-bottom: 0;
    background: #e8eff500;
}
.scbar_type_td {
    display: none;
}
.scbar_btn_td {
    padding: 9px;
}
#pgt .pg, #pgt .pgb {
    position: fixed;
    float: left;
    padding-left: 21%;
    bottom: 20px;
    z-index: 1;
}
.pg a, .pg strong, .pgb a, .pg label {
    float: left;
    display: -webkit-inline-box;
}
#pt {
    display: none;
}
#scbar_txt {
    width: 168px;
    height: 25px;
    margin-top: 4px;
    border: 1px solid #FFF!important;
    outline: 1px solid #f2f2f2;
    padding-left: 20px;
}
.avt img {
    border-radius: 50%;
    border: 1px solid;
    border-color: #F2F2F2!important;
}
.scbar_narrow #scbar_txt {
    width: 109px;
    height: 26px;
    margin-top: 2px;
    background: #ffffff;
    border: 1px solid #FAFAFA;
}
#hd .wp {
    padding: 0;
}
.scbar_icon_td {
    display: none;
}
.wp {
    margin: 0 auto;
    width: 960px;
}
body {
    background: #fcfcfc;
}
.mn {
    overflow: hidden;
    padding-top: 5px;
}
#pgt .pg, #pgt .pgb {
    margin-top: 3px;
}
}
