css:
@-moz-document url-prefix("https://2sucres.org/") {
.navbar {
    display: none;
}

.list-topics:not(#coincoin) {
    border: 1px solid rgb(204, 204, 204) !important;
    background-color: white !important;
    font-family: tahoma;
    font-size: 11px;
    padding: 2px !important;
    border-collapse: initial !important;
}

tbody > tr:not(#coincoin) {
    background-color: white !important;
    height: 16px !important;
}

thead:not(#coincoin) {
    background-color: white !important;
}

th:not(#coincoin) {
    background-color: #DFDFDF !important;
    border-bottom: 2px solid white !important;
    font-family: tahoma;
    font-weight: 700;
    line-height: 26px;
    height: 28px;
    color: #424251;
    text-transform: uppercase;
    padding: 0 !important;
    text-align: center;
    border: none;
}

th.topic-name:not(#coincoin) {
    color: #DFDFDF;
}

th.topic-name:not(#coincoin)::before {
    content: "Sujet";
    position: relative;
    left: -21px;
    color: #424251;
}

th.topic-date:not(#coincoin) {
    padding-right: 10px !important;
}

.topic-name:not(#coincoin), .topic-user:not(#coincoin) {
    text-align: start;
}

td:not(#coincoin) {
    padding: 0 !important;
}

td.topic-name:not(#coincoin) {
    font-weight: 700;
    padding: 0 !important;
    max-width: 0 !important;
}

.topic-name-truncate:not(#coincoin) {
    display: flex !important;
    padding-right: 23px;
    padding-left: 4px;
    
}

@media (max-width: 992px) {
    .topic-name-truncate:not(#coincoin) {
        display: table !important;
    }
}

tbody > tr:not(#coincoin):nth-child(odd) {
    background-color: #EEF5FC !important;
}

tbody > tr:not(#coincoin):hover {
    background-color: #F7F7E6 !important;
}

.topic-icon:not(#coincoin) {
    padding-left: 3px !important;
}

.topic-icon > .material-icons {
    position: relative;
    top: 2px;
}

.topic-user:not(#coincoin), .topic-nb-message:not(#coincoin), .topic-connected-count:not(#coincoin), .topic-date:not(#coincoin) {
    color: black;
    vertical-align: middle;
}

.topic-date > a {
    color: #006bd7 !important;
}

.topic-user:not(#coincoin) {
    width: 100px;
}

.topic-nb-message:not(#coincoin), .topic-connected-count:not(#coincoin) {
    width: 45px;
}

.topic-date:not(#coincoin) {
    width: 60px !important;
    text-align: right;
}

.col-lg-8.col-md-9.col-xs-12.no-pr.no-pl {
    margin-top: 30px;
}

.navbar, .message-footer, .user-badge-icon, .message-avatar-dot-container {
    display: none;
}

.message-content {
    min-height: 0px;
    padding-bottom: 0;
}

.message-header.layout-row {
    height: 54px;
}

.message-avatar-container {
    width: 46px;
    height: 46px;
}

.message-avatar.ng-scope {
    height: 42px;
    width: 42px;
}

.message-date.pull-left {
    margin-top: 0px !important;
}

.row.topic-message.mt-2.no-ml.no-mr {
    margin-top: 1.5rem !important;
}
}
