css:
@-moz-document url-prefix("http://anitokyo.tv/") {
body {
    font-size: 12px;
    font-family: 'Segoe ui', sans-serif;
    background: #000000 url(http://images.vfl.ru/ii/1530748533/4a633ae7/22360562.jpg) no-repeat center top;
    color: #000000;
    background-color: black;
}
body::-webkit-scrollbar-thumb {
    cursor: pointer;
    background: #2158d6;
}
body::-webkit-scrollbar-track {
    background: transparent;
    opacity: 0;
}
body::-webkit-scrollbar-thumb {
    cursor: pointer;
    background: #121ce5;
    border-radius: 8px;
}
body header .logo {
    background: url("http://images.vfl.ru/ii/1530793337/39230c3b/22367180.png") no-repeat;
}
body .soc_icons li a {
    background: url(http://images.vfl.ru/ii/1530793922/f7c6df8a/22367339.png) no-repeat;
}
body #header_h {
    background: url(http://images.vfl.ru/ii/1530802457/ee9bc9ba/22369258.png) no-repeat;
}
.relizes-mess {
    width: 186px;
    overflow: hidden;
    height: 103px;
    background: #0e0b15 !important;
    padding: 6px 0 0 0;
}
body .soc_icons {
    background: url(http://images.vfl.ru/ii/1530797969/6f7bff9a/22368320.png) no-repeat;
}
body .header_search {
    background: url(http://images.vfl.ru/ii/1530798857/7e4b7cfa/22368552.png) no-repeat;
}
.header_search form input[type="text"] {
    color: #000;
    text-shadow: 0px 0px 0 rgb(0, 0, 0);
}
body .header_search form input[type="submit"] {
    background: url(http://images.vfl.ru/ii/1530836496/61e1347e/22374066.png) no-repeat;
}
.header_bg_2 {
    background: url(http://images.vfl.ru/ii/1530801549/550a7e0c/22369073.png) no-repeat;
}
.header_bg_1 {
    background: url(http://images.vfl.ru/ii/1530803619/1c8ba7f5/22369447.png) no-repeat;
}
.relizes-a {
    background: url(http://images.vfl.ru/ii/1530815870/e028825b/22372041.png) no-repeat;
}
.rss_ico {
    background: url(http://images.vfl.ru/ii/1530816585/b6b422fa/22372139.png) no-repeat;
}
#header_b {
    background: #1d2b2f;
    background: -moz-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #8bd2e6), color-stop(100%, #162023));
    background: -webkit-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: -o-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: -ms-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: linear-gradient(to bottom, rgba(14, 11, 21, 1) 0%, rgb(7, 5, 43) 100%);
    height: 54px;
    padding: 1px 0 0 0;
    -webkit-box-shadow: inset 1px 1px 0px 0px rgb(222, 230, 234);
    -moz-box-shadow: inset 1px 1px 0px 0px rgba(9, 23, 28, 1);
    box-shadow: inset 1px 1px 0px 0px rgb(0, 0, 0);
}
#h_flow > li {
    background: url(http://images.vfl.ru/ii/1530818232/8a15310a/22372428.png) no-repeat right 0;
}
section.lcol {
    border-top: 3px solid rgba(14, 11, 21, 1);
}
.relizes-menu {
    background: linear-gradient(to bottom, rgba(14, 11, 21, 1) 0%, rgb(7, 5, 43) 100%);
    /*url(http://images.vfl.ru/ii/1530820246/87a746e7/22372733.png) no-repeat left top;*/
}
.relizes-menu li span:hover,
.relizes-menu li.sp_menu_active span {
    background: #1d2b2f;
    background: -moz-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #8bd2e6), color-stop(100%, #162023));
    background: -webkit-linear-gradient(top, rgb(29, 43, 47) 0%, #162023 100%);
    background: -o-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: -ms-linear-gradient(top, #1d2b2f 0%, #162023 100%);
    background: linear-gradient(to bottom, rgb(20, 6, 51) 0%, rgb(7, 5, 43) 100%);
}
.relizes-menu li span {
    background: linear-gradient(to bottom, rgba(14, 11, 21, 1) 0%, rgb(7, 5, 43) 100%);
    text-decoration: none;
}
.relizes-menu::before {
    background: url(http://images.vfl.ru/ii/1530821044/8fd7bfae/22372847.png) no-repeat;
}
.login_block_c {
    background: transparent;
    padding: 0 6px 0 0;
    overflow: hidden;
}
.login_block {
    padding: 4px 5px;
    width: 266px;
    position: relative;
    background: transparent;
    background: linear-gradient(to bottom, rgb(7, 5, 43) 0%, rgba(14, 11, 21, 1) 100%);
}
.is_login .ava {
    background: rgba(14, 11, 21, 1);
    padding: 4px;
    -webkit-box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
    -moz-box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
    box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
}
.login_block_h div {
    height: 39px;
    padding: 0 0 0 29px;
    overflow: hidden;
    background: url(http://images.vfl.ru/ii/1530829831/98a0f527/22373828.png) no-repeat 8px -118px;
}
aside.rcol .block .block_h span {
    padding: 0 0 0 38px;
    height: 50px;
    display: block;
    background: url(http://images.vfl.ru/ii/1530829831/98a0f527/22373828.png) no-repeat 14px -80px;
    line-height: 44px;
}
aside.rcol .block .block_h span.ico_10 {
    background: url(http://images.vfl.ru/ii/1530829831/98a0f527/22373828.png) no-repeat 14px -174px;
}
aside.rcol .block .block_h span.ico_8 {
    background: url(http://images.vfl.ru/ii/1530829831/98a0f527/22373828.png) no-repeat 14px -206px;
}
.login_block_h {
    height: 35px;
    font-weight: bold;
    font-size: 12px;
    color: rgb(0, 255, 255);
}
aside.lcol .block .block_h {
    background: url(http://images.vfl.ru/ii/1530833183/435d5da9/22373987.png) no-repeat;
    height: 51px;
    width: 229px;
    margin: 0 auto;
    overflow: hidden;
    line-height: 47px;
    font-weight: bold;
    color: #fff;
    padding: 0;
    text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.15);
}
.profile .title {
    background: rgb(14, 11, 21);
}
.btn,
.bbcodes,
.btn-border {
    background-color: rgb(14, 11, 21);
}
}
