css:
@-moz-document domain("smoj.nhedu.net"), domain("10.3.35.134") {
.navbar,
.dropdown-menu,
.panel,
.btn,
.form-control,
.edui-default,
.modal-content,
.pagination,
.well,
.label,
.pagination > li:first-child > a,
.pagination > li:first-child > span,
.pagination > li:last-child > a,
.pagination > li:last-child > span {
    border-radius: 0 !important;
}

.panel > .table:first-child > tbody:first-child > tr:first-child td:first-child,
.panel > .table:first-child > tbody:first-child > tr:first-child td:last-child,
.panel > .table:last-child > tbody:last-child > tr:last-child td:first-child,
.panel > .table:last-child > tbody:last-child > tr:last-child td:last-child {
    border-radius: 0;
}

nav.navbar {
    box-shadow: inset 0 0px 0 #FFFFFF;
    background-color: #fff;
    background-image: none;
    min-height: 0px;
}

ul.nav > li > a {
    height: 42px;
    font-size: 16px;
}

ul.nav > li:last-child {
    float: right;
}

ul.nav > li:nth-child(2),

/* GoC专栏 & C++进阶专栏*/
ul.nav > li:nth-child(3) {
    display: none;
}

ul.nav > li:nth-child(4) > a {
    /* "其它题库" */
    text-indent: -99999px;
    line-height: 0;
}

ul.nav > li:nth-child(4) > a:after {
    /* "其它题库" */
    content: '题库';
    display: block;
    text-indent: 0;
    line-height: initial;
}

/*
ul.nav > li:first-child { // 首页
    background-color: #f0f0f0;
}
*/

.nav > li > a:hover,
.nav > li > a:focus {
    border-radius: 0;
}

.dropdown-menu > li > a:focus,
.dropdown-menu > li > a:hover {
    background-color: #f0f0f0;
    background-image: none;
}

.dropdown-menu {
    left: auto;
    right: 0;
}

.dropdown-menu > li > a {
    line-height: 1.7;
    padding: 4px 16px;
    font-size: 15px;
}

.btn-primary {
    background-color: #428bca !important;
    background-image: none;
    box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border: 0;
}

.pagination > li > a,
.pagination > li > span {
    margin-top: -1px;
}

a {
    text-decoration: none !important;
}

@media (min-width: 768px) {
    .modal-dialog {
        width: 700px;
    }
}

* {
    font-family: 'Microsoft Yahei', '微软雅黑';
}
}

@-moz-document url-prefix("http://10.3.35.134/showproblem"), url-prefix("http://smoj.nhedu.net/showproblem") {
.btn-primary {
    width: 100%;
}

.panel-body > table {
    margin-bottom: 0;
}

.edui-editor-toolbarbox.edui-default {
    display: none;
}

body > .container:nth-child(2) {
    font-size: 18px;
    padding-bottom: 20px;
}

.container > font[color='red'] {
    font-size: 16px;
    font-family: 'Consolas', 'Microsoft Yahei', '微软雅黑';
}

div.panel-heading {
    padding: 0;
}

div.panel-heading > h4 > a {
    display: flex;
    padding: 10px 15px;
}

.container > .edui-default {
    padding-top: 3px;
}

/* Inside <iframe> */
.edui-popup-content {
    display: none;
}

.view * {
    font-size: 16px !important;
    font-family: 'Consolas', 'Microsoft Yahei', '微软雅黑' !important;
}

}

@-moz-document url-prefix("http://10.3.35.134/showcontest"), url-prefix("http://smoj.nhedu.net/showcontest") {
body > div:nth-last-child(2) > a {
    display: inline-block;
    padding: 6px 12px;
}
}

@-moz-document url-prefix("http://10.3.35.134/ListAllProblem"), url-prefix("http://smoj.nhedu.net/ListAllProblem") {
table > tbody > tr > td:last-child {
    text-align: right;
    padding-right: 16px;
}
}

@-moz-document url-prefix("http://10.3.35.134/listcontest"), url-prefix("http://smoj.nhedu.net/listcontest") {
@media (min-width: 0px) {
    table > tbody > tr > td:nth-child(1) {
        width: 46px;
    }

    table > tbody > tr > td:nth-child(2) {
        max-width: 314px;
        width: 314px;
    }

    table > tbody > tr > td:nth-child(3) {
        width: 60px;
        min-width: 60px;
    }

    table > tbody > tr > td:nth-child(4),
    table > tbody > tr > td:nth-child(5) {
        max-width: 120px;
        min-width: 100px;
    }

    table > tbody > tr > td:nth-child(6) {
        max-width: 48px;
    }

    table > tbody > tr > td:nth-child(7) {
        max-width: 60px;
        min-width: 60px;
        width: 60px;
    }
}

@media (min-width: 768px) {
    table > tbody > tr > td:nth-child(1) {
        width: 46px;
    }

    table > tbody > tr > td:nth-child(2) {
        max-width: 314px;
        width: 314px;
    }

    table > tbody > tr > td:nth-child(3) {
        width: 60px;
    }

    table > tbody > tr > td:nth-child(4),
    table > tbody > tr > td:nth-child(5) {
        max-width: 120px;
    }

    table > tbody > tr > td:nth-child(6) {
        max-width: 48px;
    }

    table > tbody > tr > td:nth-child(7) {
        max-width: 90px;
        width: 90px;
    }
}

@media (min-width: 992px) {
    table > tbody > tr > td:nth-child(1) {
        width: 60px;
    }

    table > tbody > tr > td:nth-child(2) {
        max-width: 345px;
    }

    table > tbody > tr > td:nth-child(6) {
        max-width: 73px;
        width: 73px;
    }
}

@media (min-width: 1200px) {
    table > tbody > tr > td:nth-child(2) {
        max-width: 550px;
    }
}

td {
    border-left-color: #ececec !important;
    border-right-color: #ececec !important;
}
}

@-moz-document url-prefix("http://10.3.35.134/addnote"), url-prefix("http://smoj.nhedu.net/addnote") {
.col-xs-6 {
    width: auto;
}

input[type="file"]:after {
    position: relative;
    content: "选择文件：";
    height: 100%;
    display: block;
    text-indent: 0;
    line-height: initial;
    top: -23px;
}

input[type="file"] {
    cursor: pointer;
    text-indent: -99999px;
}

/* Inside editor */
.edui-editor-toolbarboxouter.edui-default {
    background-image: none;
}

/* 有序列表下拉框 */
#edui30 .edui-arrow {
    display: none !important;
}

/* 无序列表下拉框中 */
#edui44_content #edui48,
#edui44_content #edui49 {
    display: none;
}

#edui128, #edui135, #edui136, #edui141, #edui179, #edui186, #edui203, #edui206 {
    display: none !important;
}

}

@-moz-document url-prefix("http://10.3.35.134/ShowAllSubmits"), url-prefix("http://smoj.nhedu.net/ShowAllSubmits"), url-prefix("http://10.3.35.134/allmysubmits"), url-prefix("http://smoj.nhedu.net/allmysubmits"), url-prefix("http://10.3.35.134/submit_problem"), url-prefix("http://smoj.nhedu.net/submit_problem") {
textarea {
    font-family: 'Consolas', 'Microsoft Yahei', '微软雅黑';
    tab-size: 4;
    white-space: pre;
    height: 540px !important;
}

}

@-moz-document url-prefix("http://10.3.35.134/ShowAllSubmits"), url-prefix("http://smoj.nhedu.net/ShowAllSubmits"), url-prefix("http://10.3.35.134/allmysubmits"), url-prefix("http://smoj.nhedu.net/allmysubmits"), url-prefix("http://10.3.35.134/showcontest"), url-prefix("http://smoj.nhedu.net/showcontest"), url-prefix("http://10.3.35.134/ListAllProblem"), url-prefix("http://smoj.nhedu.net/ListAllProblem") {
td {
    border-left: none !important;
    border-right: none !important;
}
}
