css:
@-moz-document regexp("https://material.io(/(?!tools/(?!devices).+).*)") {
:root {
  --text-color: rgba(255, 255, 255, .87);
  --text-color-light: rgba(255, 255, 255, .54);
  --text-color-head: #fff;
  --bg-color: #202124;
  --bg-color-lighter: #303134;
  --bg-color-darker: #17181c;
  --bg-color-darkest: #101114;
  --border-color: rgba(255, 255, 255, 0.15);
}

.header,
.breadcrumbs {
  position: absolute;
}

body, .article-card, .search-form, .search-open {
  background: var(--bg-color);
}

#info-panel,
#device-preview,
.mdc-menu,
.toggle-button,
.section:not(.section--dark), 
.side-nav {
  background: var(--bg-color-lighter);
}

#main-table .tr.selected, 
#main-table .tr.selected .grid-wrapper,
.section--light {
  background: var(--bg-color-lighter) !important;
}

#filterPanel,
#footer,
.footer,
.object-card,
article .s-tag-table tr:first-child,
article .s-tag-table tr:first-child td p {
  background: var(--bg-color-darker);
}

#main-table .tr:hover, 
#main-table .tr:hover .grid-wrapper {
  background: var(--bg-color-darker) !important;
}

.CodeMirror {
  background: var(--text-color);
}

body, p,
figcaption,
a:hover,
.section,
.article-card,
.object-card,
.search-form,
.search-open,
.sort,
.toggle-button::before,
article .s-tag-table tr:first-child,
article .s-tag-table tr:first-child td p {
  color: var(--text-color) !important;
}

h4, h5, figcaption, a,
.footer,
.section,
.article-card,
.object-card,
.search-form,
.search-open,
.side-nav__article, 
.side-nav__link, 
.side-nav__toggle,
.side-nav__menu-item,
article .s-tag-table tr:first-child,
article .s-tag-table tr:first-child td p {
  color: var(--text-color-light) !important;
}

h1, h2, h3,
.headline-1, .headline-2, .headline-3, .headline-4, .headline-5,
.header__nav-item--active,
.header__nav-item--active a,
.article code,
.article th,
.article__chapter-title,
.side-nav__section-title,
.side-nav__article:hover,
.side-nav__article.active,
.side-nav__link:hover,
.side-nav__link--active,
.side-nav__toggle:hover,
.side-nav__toggle.active,
.side-nav .side-nav__link--active .article-card__structure-title,
.sort:hover,
.unit:hover,
.collapsible__heading,
.hero .col-list a,
.rotator__intro,
.rotator__title,
.search-form__input,
.section--404 .s404__headline,
.section--404 .s404__lead,
.section--404 .s404__popular-links-item,
.brand-lockup__title,
.object-card__title,
.toolbar-platform-chooser__button,
article .s-tag-table td,
.footer-block__social-nav-link {
  color: var(--text-color-head) !important;
}

hr,
.material-io-nav-1, .material-io-nav-2,
.material-io-nav-3, .material-io-nav-4,
.material-io-nav-5, .material-io-nav-6,
.material-io-nav-7, .material-io-nav-8,
.material-io-nav-9, .material-io-nav-10,
.material-io-nav-11,.material-io-nav-12,
.group,
.page__drawer,
.side-nav__menu,
.side-nav .brand-lockup,
.footer-block--about,
article .s-tag-table tr td,
.collapsible ~ .collapsible {
  border-color: var(--border-color) !important;
}

.object-card:focus,
.object-card:hover {
  box-shadow: 0 0 0 2px var(--border-color);
}

#google-logo {
  background: url(https://www.gstatic.com/images/branding/googlelogo/2x/googlelogo_light_color_92x36dp.png) no-repeat;
  background-size: cover;
}

.toolbar-platform-chooser__button svg {
    fill: #fff;
}

.side-nav::-webkit-scrollbar {
	width: 10px;
}

.side-nav::-webkit-scrollbar-track {
	background-color: var(--bg-color-lighter);
}

.side-nav::-webkit-scrollbar-track:hover {
	background-color: var(--border-color);
}

.side-nav::-webkit-scrollbar-thumb {
	background-color: var(--bg-color-darker);
}

.side-nav::-webkit-scrollbar-thumb:hover {
	background-color: var(--bg-color-darkest);
}
}
