css:
@-moz-document domain("elchat.net") {
/** PAGINA INICIAL **/

/** Fila Titulo **/
.topcmm-123flashchat-common-room-list-panel-block-single-room-name {
    color: black;
}

/** Fila Descripcion **/
.topcmm-123flashchat-roomlist-container-one-room-block-roomname-text, .topcmm-123flashchat-common-room-list-panel-block-single-room-description {
    color: #ccc;
}

.topcmm-123flashchat-common-main-div-outline {
    background: #23292f;
    border: 1px solid #dbeaf9;
}

/** PAGINA DEL CHAT **/
/** Sidebar Right Header **/
.topcmm-123flashchat-common-userlist-top, .topcmm-123flashchat-side-common-top {
    background: #201919;
    background: -webkit-gradient(linear,0 0,0 bottom,from(#201919),to(#d5dfe9));
    background: -webkit-linear-gradient(#201919,#d5dfe9);
    background: -moz-linear-gradient(#201919,#d5dfe9);
    background: -ms-linear-gradient(#201919,#d5dfe9);
    background: -o-linear-gradient(#201919,#d5dfe9);
    background: linear-gradient(#201919,#d5dfe9);
    border-bottom: 1px solid #fff;
}
/** Sidebar Right Content **/
.topcmm-123flashchat-common-userlist-block-single-user-block, .topcmm-123flashchat-common-userlist-block-single-user-block-over-color {
    background: #201919;
    background: -webkit-gradient(linear,0 0,0 bottom,from(#201919),to(#d5dfe9));
    background: -webkit-linear-gradient(#201919,#d5dfe9);
    background: -moz-linear-gradient(#201919,#d5dfe9);
    background: -ms-linear-gradient(#201919,#d5dfe9);
    background: -o-linear-gradient(#201919,#d5dfe9);
    background: linear-gradient(#201919,#d5dfe9);
    border-bottom: 1px solid #fff;
}

/** Chat Historial Content **/
.topcmm-123flashchat-common-chat-panel-chat-box {
    border: 1px solid #a2b4c7;
    background: #201919;
    background: -webkit-gradient(linear,0 0,0 bottom,from(#201919),to(#d5dfe9));
    background: -webkit-linear-gradient(#201919,#d5dfe9);
    background: -moz-linear-gradient(#201919,#d5dfe9);
    background: -ms-linear-gradient(#201919,#d5dfe9);
    background: -o-linear-gradient(#201919,#d5dfe9);
    background: linear-gradient(#201919,#d5dfe9);
    border-bottom: 1px solid #fff;
}

/** Chat Input **/

.topcmm-123flashchat-main-input-panel-message-input-area-wrap {
    border: 1px solid #d5dfe9;
        border-bottom-color: rgb(162, 180, 199);
        border-bottom-style: solid;
        border-bottom-width: 1px;
    background: #201919;
    background: -webkit-gradient(linear,0 0,0 bottom,from(#d5dfe9),to(#201919));
    background: -webkit-linear-gradient(#d5dfe9,#201919);
    background: -moz-linear-gradient(#d5dfe9,#201919);
    background: -ms-linear-gradient(#d5dfe9,#201919);
    background: -o-linear-gradient(#d5dfe9,#201919);
    background: linear-gradient(#d5dfe9,#201919);
    border-bottom: 1px solid #fff;
}
}
