css:
@-moz-document url-prefix("https://forums.daybreakgames.com/eq/") {
body {
    background-color: #262626;
    color: #f2f2f2;
}
nav {
    background-color: #262626;
    color: #f2f2f2;
}
span {
    background-color: #262626;
    color: #f2f2f2;
}
abbr, acronym {
    color: #f2f2f2;      
}
.titleBar h1 {
    color: #f2f2f2;
}
div {
    background-color: #262626;
    color: #f2f2f2;
}
#loginBarHandle a {
    background: none;
}
.message .messageText {
    color: #f2f2f2;
}
.message .signature {
    color: #f2f2f2;
}
.messageList .message {
    border-bottom: 1px solid grey;
}
/* if a thread has more than one page, this is the color of the page numbers background and border color */
.itemPageNav a {
    background: #262626;
    border: 1px solid #808080;
}
.itemPageNav a:hover {
    background: #f2f2f2;
    color: #000000;
}
.itemPageNav a:visited {
    background: #262626;
    border: 1px solid #808080;
    color: #f2f2f2;
}
.itemPageNav a:link {
    background: #262626;
    border: 1px solid #808080;
    color: #f2f2f2;
}
/* Background color of the box that quoted post text is in */
.bbCodeQuote blockquote {
    background-color: #000000;
}
/* Background color of the bar the quoted username and up arrow is located in */
.bbCodeQuote .attribution {
    background-color: #808080;
}
/* Background color of the actual "" symbols in a quoted post */
.ae_quote_symbol {
    background-color: #000000;
}
/* Background color of the text that is being quoted in the quote box */
.ae_quote_content {
    background-color: #000000;
}
/* Color of the little up arrow that is to the right of the username of the person you are quoting in a post */
.ugc a:link {
    color: #a6a6a6;
    background: none;
}
.titleText a {
    color: #f2f2f2;
}
ol {
    background-color: #262626;
    color: #f2f2f2;
}
.messageMeta .control {
    background-color: #262626;
    color: #f2f2f2;
}
.message .privateControls {
    background-color: #262626 !important;
}
/* Border color of the area surrounding who likes posts */
.message .likesSummary {
    border-color: #808080;
}
.messageList .message {
    border-top: 1px solid #808080;
}
a:link {
    background-color: #262626;
    color: #e6e6e6;
}
a:visited {
    color: #808080;
}
.PreviewTooltip {
    color: #f2f2f2;
}
.messageUserBlock a.username {
    color: #f2f2f2;
}
#pageDescription {
    color: #f2f2f2;
}
.navTabs {
    background-image: none;
    background-color: #262626;
}
#content .pageContent {
    background-image: none;
}
#content {
    background-image: none;
    background: #1a1a1a;
}
#headerMover {
    background: #1a1a1a;
}
#headerMover
#header {
    background: #1a1a1a;
}
#headerMover
#headerProxy {
    background: #1a1a1a;
    height: 223px;
}
#header_bar {
    background: #1a1a1a;
}
#logoBlock {
    background: #1a1a1a;
}
.breadBoxTop {
    width: 981px;
}
/* Where the title, start date, replies, views, last message headers are for the forums */
.discussionList .sectionHeaders {
    background-image: none;
    border-bottom: 1px solid grey;
}
/* Border color of the Show threads box at the bottom of a forum */
.discussionList .sectionFooter {
    border-color: #808080;
}
/* Color of the text and border color of the box surrounding it down at the bottom of forums where it says Thread Disply Options */
#DiscussionListOptionsHandle a {
    color: #f2f2f2;
    border-color: #808080;
}
.navTabs .navTab.selected .navLink {
    background-image: none;
}
/* Grey border under the pagination under the forum titles */
.PageNav {
    border-bottom: 1px solid grey;
}
.PageNav a.currentPage {
    color: #000000;
}
.PageNav a {
    color: #FFFFFF;
}
#titleBar_Helper {
    border-top: 1px solid grey;
}
.discussionList .sectionFooter {
    background-color: #262626;
}
.discussionListItem {
    border-bottom: 1px solid grey;
}
footer {
    background: #1a1a1a;
}
.footer {
    background-image: none;
    background-color: #1a1a1a;
}
.footer .pageContent {
    background: #1a1a1a;
}
.footer #footer_wrapper {
    background: #1a1a1a;
}
.footerLegal .pageContent {
    background: #1a1a1a;
}
.footerLegal {
    background: #1a1a1a;
}
.sectionFooter {
    background-color: #262626;
}
#copyright {
    background-color: #1a1a1a;
}
.event .content .snippet {
    color: #f2f2f2;
}
.event .content .description {
    color: #f2f2f2;
}
/* Border between dev posts on the staff post tracker */
.event,.event.forceBorder .node .node .nodeLastPost {
    border-bottom: 1px solid grey;
}
.node .nodeLastPost {
    color: #f2f2f2;
}
/* Change color of small border between forums on the main forum listing */
.nodeList .node {
    border-bottom: 1px solid grey;
}
/* Text color of the description for the Guide Events and Player Studio forums */
.node .nodeDescription {
    color: #f2f2f2;
}
.Popup .arrowWidget {
    background-color: #808080;
}
label {
    color: #f2f2f2;
}
/* Top header bar of the site */
#header_bar .pageContent {
    background-color: #1a1a1a;
}
/* Color of your username, Inbox, Alerts, and Log Out up top when you are logged in */
#header_bar a {
    color: #f2f2f2;
}
/* Color of the background behind your username and etc.. at the top of the site. */
#header_bar .visitorTabs .navLink {
    background-color: #1a1a1a;
}
/* Border color of the top of the quick reply area */
.quickReply {
    border-top: none;
}
/* Color of the text when using the search feature */
#QuickSearch label {
    color: #000000;
}
/* Color of post text when you have searched for something */
.searchResult .snippet a {
    color: #f2f2f2;
}
/* Background color of the popup when you click on a user */
.xenOverlay.memberCard {
    background: none;
    background-color: #262626;
}
/* Color of the username on that same popup */
.xenOverlay.memberCard .userInfo h3 a {
    color: #f2f2f2;
}
/* Color of the rank of the user on the same popup */
.xenOverlay.memberCard .userInfo h4 {
    color: #f2f2f2;
}
/* Color of the links such as Profile Page and such on that popup */
.xenOverlay.memberCard .userLinks a {
    color: #f2f2f2;
}
/* Background color of the post control buttons */
.xenForoSkin span.mceIcon, .xenForoSkin img.mceIcon {
    background-color: #808080;
}
/* Background color of disabled post control buttons */
.xenForoSkin .mceButtonDisabled .mceIcon {
    background-color: #FFFFFF;
}
/* Background color of the post control area */
.xenForoSkin table.mceLayout tr.mceFirst td {
    background-color: #808080;
}
/* Border color of post controls */
.xenForoSkin a:hover, .xenForoSkin a:link, .xenForoSkin a:visited, .xenForoSkin a:active {
    border-color: #808080;   
}
/* Border color of the Use BB Code Editor button on the far right of the post controls */
.xenForoSkin a.bbCodeEditorButton {
    border-color: #808080;   
}
/* Background color of the font color selector button in the post controls */
.xenForoSkin .mce_forecolor span.mceAction {
    background-color: #f2f2f2;
}
/* Background color of the smilie post control button */
.xenForoSkin .mce_xenforo_smilies span.mceAction {
    background-color: #f2f2f2;
}
/* Background color of the Font Family and Font Size boxes before you click on them */
.xenForoSkin .mceListBox .mceText {
    background: #f2f2f2;
}
/* Background color of the drop down menu options in the Font Family and Font Size post controls */
.xenForoSkin .mceMenu span.mceText {
    background: #f2f2f2;
}
/* Text color of the text within previewing posts by hovering over a post */
.previewTooltip blockquote {
    color: #f2f2f2;
}
/* Color of text for Member Since, Messages, Likes Received, and Trophy Points */
.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
    color: #f2f2f2;
}
/* Stats that show the date on the popup for Member Since, Messages Likes Received, Trophy Points */
.xenOverlay.memberCard .userInfo .userStats dd {
    color: #808080;
}
/* Username text on the right where it shows you Messages, Likes and Points */
.sidebar .visitorPanel .username {
    color: #f2f2f2;
}
/* Forum Statistics h3 header text and the facebook and tiwtter Share This Page header text. */
h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock {
    color: #f2f2f2;
}
/* Twitter feed header text */
.timeline-Header-title {
    color: #f2f2f2;
}
}
