css:
@-moz-document url-prefix(http://live2.nicovideo.jp/watch/), url-prefix(https://live2.nicovideo.jp/watch/) {
/* ----------------------------------------
    ニコニコ生放送
    ダークテーマ
    ここから
---------------------------------------- */

/* ========================================
  20200301
======================================== */

/* コメントオーバーレイ */
[class*=___comment-post-form___][aria-disabled=true] [class^=___overlay___] { background-color: hsla(0, 0%, 9%, 0.8); }

/* 関連コンテンツ */
[class^=___program-information-side-area___] [class^=___heading___] { color: #fff; }
[class*=___related-content-card___] [class^=___content-title___] { color: #fff; }
[class*=___related-content-card___]:hover { background-color: #252525; }
[class^=___content-card-list___] [class^=___item___] { border-top: 1px solid #404040; }

/* ========================================
  20191205
======================================== */

/* コメントタブ */
[class^=___tab-list] { background-color: #191919; }
[class^=___select-button][aria-selected=true] { background-color: #303030; }
[class^=___select-button]:not([aria-selected=true]) { color: #707070; background-color: #202020; }

[class^=___program-recommend-tab][aria-selected=true] { background-color: #303030; }
[class^=___program-recommend-tab]:not([aria-selected=true]) { color: #707070; background-color: #202020; }

/* ========================================
  20190411
======================================== */

/* 検索ボックス */
[class*=___site-header___] [class*=___search-form___] { background-color: #252525; }
[class*=___search-text-box___] [class*=___text-box___] { color: #252525; }

/* 番組終了｜フォローしよう */
[class*=___social-group-menu___][data-kind=community] [class*=___description___] { color: #252525; }
[class*=___social-group-menu___][data-kind=community] [class*=___follow-anchor___] { color: #08f !important; background-color: #f2f2f2 !important; }
[class*=___social-group-menu___][data-kind=community] [class*=___title___] { color: #252525; }

/* ログインしないとコメント */
[class*=___broadcast-participation-area___] { background-color: #000; border-color: #000; }
[class*=___disabled-message___] { color: #bbb; }
[class*=___broadcast-participation-area-disabled-message___] { color: #bbb; }
[class*=___item-placeholder___] { border: 2px solid #171717; }
[class*=___add-button___]:disabled { background-color: hsla(0, 0%, 25%, 0.25); }


/* 部屋情報下線 */
[class*=___room-information___] { border-bottom: inherit; }
[class^=___program-statistics___] { border-top: none !important; }
[class^=___program-panel___] [class*=___header-area___] { border-bottom: none; }


/* 案内ボタン */
[class*=___conductor___] [class*=___content___] { color: #eee; background-color: #303030; }

/* カウンター */
[class*=___statistics-item___]>svg>path { fill: #fff; }
[class*=___nicoad-count___]>span>button, [class*=___gift-count___]>span>button { color: #fff; }

/* タイトル */
[class*=___program-summary___] {
    border-bottom: 1px solid #404040;
    box-shadow: 0 4px 8px 0 #000;
    background-color: #000;
}
[class*=___title___] { color: #eee; }
[class*=___onair-time___] { color: #bbb; }
[class*=___user-name-anchor___] { color: #eee; }
[class*=___channel-name-anchor___] { color: #eee; }
[class*=___level___] { color: #bbb; }

/* ソーシャルボタン */
[class*=___popup-toggle-button___] { border: 1px solid #404040; }
[class*=___extra-popup-control___] [class*=___list___],
[class*=___allegation-popup-control___] [class*=___list___] { box-shadow: 0 2px 6px rgba(255, 255, 255,.25); background-color: #000; border: 1px solid #303030; }

/* セパレータ */
[class*=___user-summary___] { border-left: 1px solid #404040; }

/* タイトル外枠 */
[class*=___program-information-header-area___] { background-color: #000; }

/* タグボタン */
[class*=___name-anchor___] { color: #eee; }
[class*=___tag-widget___] [class*=___tag___] { background-color: #202020; }
[class*=___tag-widget___] [class*=___edit-button___] { color: #bbb; border: 2px solid #404040; }
[class*=___tag-widget___]:before { box-shadow: 0 2px 16px 0 rgba(255, 255, 255, 0.75); background-color: #000000; }
[class*=___tag-widget___] [class*=___tag-register-form___] [class*=___faq-anchor___]:after { color: #eee; }

/* コミュニティ欄 */
[class*=___social-group-information___] [data-group-type=community] [class*=___footer-area___] { background-color: #121212; }

[class*=___social-group-information___] { border: 1px solid #404040; }
[class*=___header-area___] { border-bottom: 1px solid #404040; }

[class*=___group-name-anchor___] { color: #eee; }

/* すべて表示 */
[class*=___description___] [aria-expanded=false] [class*=___expand-button___] { background: linear-gradient(90deg,hsla(0,0%,96%,0),#202020 16%); }

/* コミュフォローボタン */
[class*=row___][data-comment-type=nicoad] [class*=___comment-text___] { color: #fff; }

/* すべて表示 */
[class*=___description___][aria-expanded=false] [class*=___expand-button___] { background: linear-gradient(90deg,hsla(0,0%,96%,0),#202020 16%); }

/* コミュニティ欄 */
[class*=___social-group-information___][data-group-type=community] [class*=___footer-area___] { background-color: #121212; }

[class*=___social-group-information___] { border: 1px solid #404040; }
[class*=___header-area___] { border-bottom: 1px solid #404040; }

[class*=___group-name-anchor___] { color: #eee; }

/* コミュフォローボタン */
[class*=___channel-page-anchor___], [class*=___community-page-anchor___], [class*=___follow-anchor___] { background-color: #000; border: 1px solid #404040; color: #bbb; }

/* ========================================
  番組下部エリア
======================================== */

/* ニコニコ市場 */
.___nico-ichiba___30cyF { color: #333; background: #f4f4f4; }

/* アンカー */
[class*=___page-top-anchor___] { color: #999; background-color: inherit; }
[class*=___feedback-anchor___] { background: #333; }

/* 不具合報告ボタン transparent */
[class*=___report-button___] { color: #bcd; border-color: #444; background-color: #000; }
[class*=___report-button___]:after { border-color: transparent #bcd #bcd transparent; }
[class^=___feedback-anchor___] { right: 10px; }

/* 通報プルダウン？ */
[class*=___report-menu___] { background: #000; border-color: #444; }
[class*=___report-menu___]>li { border-color: #333; }
[class*=___program-report-anchor___],
[class*=___comment-report-anchor___],
[class*=___tag-report-anchor___] { color: #ccc; }

/* ========================================
  番組情報エリア
======================================== */

/* エリア全体
---------------------------------------- */
/* 背景色 */
[class*=___program-information-area___] { border-color: #222; background: #000; }

/* 番組情報欄
---------------------------------------- */
/* 開始時間 */
[class*=___on-air-time___] { color: #89a; }

/* タイトル */
[class*=___program-title___] { color: #def; }

/* 本文｜依存有___descriptionより下に */
[class*=___description___] { color: #ccc; }

/* 視聴者お便り */
[class*=___audience-mail-widget___] [class*=___body___] { border-color: #222; background-color: inherit; }
[class*=___audience-mail-widget___] [class*=___title___] { background-color: #07f; }

/* コンテンツツリーを見るボタン */
[class*=___contents-tree-anchor___] { color: #d9a300; border-color: #1a1a1a; background-color: inherit ; }
[class*=___contents-tree-anchor___]:hover { color: #e9b300; border-color: #e9b300; }

/* コミュニティ情報
---------------------------------------- */

/* タイトル */
[class*=___section-title___] { color: #bbb; background-color: #121212; }

/* コミュ名・レベル */
[class*=___social-group-anchor___] { color: #bbb; }
[class*=___level-counter___] { color: #bbb; }

/* 生放送履歴を見るボタン */
[class*=___broadcast-history-anchor___] { color: #bbb; border-color: #1a1a1a; background-color: #0b0b0b; }
[class*=___broadcast-history-anchor___]:hover { background-color: #1a1a1a; }

/* 本文-skin */
[class*=___description___] { color: #fff; }
[class*=___description___][aria-expanded=false] { color: #999; }

/* もっと見る */
[class*=___description-expand-toggle-button___] { color: #0080ff; background: linear-gradient(90deg,hsla(0,0%,100%,0),#121212 16%); }


/* ========================================
  プレイヤー下エリア
======================================== */
[class*=___zapping-widget___] { background: #1a1a1a; }
[class*=___paddle-button___] { background-color: #333; }
[class*=___paddle-button___]:hover { background-color: #444; }
[class*=___paddle-button___]:disabled { background-color: #333; }
[class*=___paddle-button___]:disabled>svg { fill: #555; }
[class*=___refresh-button___] >svg { fill: #dadada; }
[class*=___refresh-button___] { color: #dadada; }

/* ========================================
  プレイヤーエリア
======================================== */

/* プレイヤー左コンテナ
---------------------------------------- */

/* プレイヤー上部｜終了まであと○分 */
[class*=___announcement-renderer___] { color: #fff; background-color: #000; }

/* 次回の放送を見るならフォローしよう！ */
[class*=___social-group-menu___][data-kind=community] [class*=___follow-anchor___] { color: #fff; background-color: #c00; }
[class*=___social-group-menu___][data-kind=community] [class*=___follow-anchor___]:hover { color: #fff; background-color: #e00; }

/* ニコニ広告テロップ */
[class^=___telop-marquee][data-kind=userad] { display: none; }

/* コメント入力 */
[class*=___text-box___] { color: #fff; background-color: #000; border: 1px solid #202020; }
[class*=___text-box___]::-webkit-input-placeholder { color: hsl(0,0%,53.3%); }
[class*=___text-box___]::-moz-placeholder { color: #777; opacity: 1; }


/* コメント欄
---------------------------------------- */

/* 背景色・幅384px 303030 121212 */
[class^=___player-status] { border-color: #121212; background-color: #191919; min-width: 384px; }

/* コメント・来場者数・コメント数 */
[class*=___watch-page___] { color: #fff; }

/* 来場者数・上線色 */
[class^=___program-statistics___] { border-top: 1px solid #404040; }

/* ルーム名 */
[class^=___room-information]>[class^=___room-name] { color: #bcd; }

/* パネル・下線色 */
[class*=___program-panel___] { border-bottom: 1px solid #353535; }

/* ヘッダー・上線色 */
[class*=___header-cell___] { color: #ccc; border-color: #333; background-color: #202020; }

/* ヘッダー・下線色 */
[class*=___header___] { border-color: #252525; }

/* 上・下線色 */
[class*=___body___] { border-top: 1px solid #171717; border-bottom: 1px solid #222; }

/* コメント・閲覧総数アイコン */
[class^=___comment-count-icon___],
[class^=___watch-count-icon___] { fill: #b2bac2; }

/* NGボタン */
[class*=___ng-button___] { background-color: #202020; }
[class*=___ng-button___] path { fill: #bcd; }

/* コメント番号 */
[class^=___comment-number___] { color: #999; }

/* ホバー */
[class*=___table-row___]:hover,
[class*=___table-row___][aria-selected=true] {background-color:#282828}

/* 右クリック */
[class*=___comment-context-menu___] { background-color: #232323; }

/* 右クリック｜罫線色 */
[class*=___comment-context-menu___]>hr { background-color: #444; }

/* 右クリック */
[class^=___list]>li>a { color: #fff; }

/* 右クリック｜ホバー */
[class^=___list]>li>a:hover { background-color: #555; }

/* オペレーター */
[class^=___table-row][data-comment-type=operator] { color: #f13; }
/* ニコ生｜コメ欄｜BSP：バックステージパス｜文字色 */
[class^=___table-row][data-comment-type=bsp]{color: #0cc; }
/* ニコ生｜コメ欄｜トライアル｜文字色 */
[class^=___table-row][data-comment-type=trialWatch]{color:#999; }
/* ニコ生｜コメ欄｜ニコアド｜文字色da0・d90 */
[class^=___table-row][data-comment-type=nicoad] { color: #fff; background-color: #d90; }

/* NGリスト */
[class^=___ng-delete-form___] [class*=___table-row___]:hover { background-color: #ccc; }

/* ヘッダー・セパレータ */
[class*=___header-cell-separator___] { background-color: #333; }
[class*=___header-cell-separator___][data-resizable=true] { width: 4px; }

/* セパレータ */
[class*=___table-row___] { border-color: #222; border-right-width: 1px; }

/* スクロールバー色 */
[class*=___body___]::-webkit-scrollbar { width: 8px; height: 8px; background: #000; }
[class*=___body___]::-webkit-scrollbar-thumb { background: #303030; }
[class*=___body___]::-webkit-scrollbar-track-piece { background: #000; }

/* ========================================
  プレイヤーヘッドエリア
======================================== */

/* ニコニ広告 */
[class^=___nicoad-bar___],
[class*=___ranking-list___] { background-color: inherit; }

/* ニコニ広告｜エフェクト */
[class^=___effect] { position: absolute; } /* 不具合 */


/* ========================================
  番組ヘッドエリア
======================================== */

/* 番組概要
---------------------------------------- */

/* 番組名 */
[class*=___title___] { color: #fff; }

/* 放送者 */
[class*=___provider___],
[class^=___provider]>a,
[class*=___broadcaster___],
[class^=___broadcaster]>a { color: #999; }

/* レベルカウンター */
[class*=___level-counter___] { color: #999; }

/* タグ編集
---------------------------------------- */

/* 背景色 121212 */
[class*=___tag-widget___] { background-color: inherit; }

/* タグボタン */
[class*=___name-anchor___] { color: #eee; }
[class*=___tag___] { background: #202020; box-shadow: 0 1px 0 #232323; }

/* もっと見る・編集 ? */
[class^=___expand-button] { color: #fff; }
[class^=___edit-button] { color: #fff; }

/* 入力フォーム ? */
[class*=___title___] { color: #bbb; }
[class*=___tag-widget___][data-editing=true] { border: 1px solid #333; background-color: inherit; }
[class*=___tag-register-form___] { border-color: #222; background-color: #000; }
[class*=___score___] { color: #ccc; }

/* フォロー */
[class*=___follow-toggle-form___][data-social-group-type=community]>[class*=___toggle-button___] { border-color: #08f; background-color: #252525; }
[class*=___follow-toggle-form___][data-social-group-type=community]>[class*=___toggle-button___]:hover { border-color: #08f; background-color: #252525; }

/* フォロー中 */
[class*=___follow-toggle-form___][data-social-group-type=community]>[class*=___follow-status___] { color: #08f; border-color: #232323; background-color: #202020; }

/* フォロー中アイコン → 削除 */
[class*=___follow-toggle-form___][data-social-group-type=community]>[class*=___follow-status___]:before { content: initial; }

/* 共有ボタン */
[class*=___text-area-switcher___] { border-color: #111; background-color: #252525; }
[class*=___text-area-switcher___]:before { background: #252525; box-shadow: 2px -2px 2px rgba(0,0,0,.2); }
[class*=___title-radio-group___] { color: #acf; }
[class*=___radio-group-control___]>input[type=radio]:checked+label:after { background-color: #6080ff; }


/* ========================================
  ページヘッドエリア
======================================== */

/* 運営からの告知
---------------------------------------- */
[class^=___notification-bar] { background-color: #000; border-bottom: 1px solid #171717; } /* HTML5対応の */

/* 検索窓
---------------------------------------- */
/* 背景 121212 */
[class*=___site-header___] { background-color: inherit; }

/* ボタン */
[class*=___search-button___] { color:#fff; background-color: #202020; }
[class*=___search-button___]:hover { background-color: #303030; }
[class*=___search-button-icon___]>path { fill: #ccc; }

/* 検索ボタン横の広告枠 */
[class*=___banner-ad___] { border: 0px } /* 不具合 */

/* 公式ビルボード
---------------------------------------- */
[class*=___billboard-banner___] { background-color: #000; } /* 公式将棋CH */
/* 閉じる */
[class*=___close-button___] { color: #999; }


/* ========================================
  サイトヘッダーエリア
======================================== */

/* ナビゲーションバー */
#siteHeader { background: #232323; }
#siteHeader #siteHeaderInner { background: inherit; }

/* ========================================
  ルートエリア
======================================== */

/* 背景色 */
[class*=___watch-page___] { background: #121212; }


/* ========================================
  レスポンシブ
======================================== */

/* 高さが低いときの不具合対策
---------------------------------------- */
@media screen and (max-width: 1664px) and (min-width: 1024px) {

[class*=___local-side-margin___],
[class*=___operator-comment-tool___],
[class*=___operator-tool___] { max-width: initial; }

    }

/* デフォルト｜スクリーン幅 0～1248px
---------------------------------------- */
@media screen and (max-width: 1248px) {

/* 640x360 */
[class*=___local-side-margin___] { width: 1024px; }

/* 番組情報｜オフセット */
[class*=___local-side-padding___]:before,
[class*=___local-side-padding___]:after,
[class^=___private-operator-tool___]:before,
[class^=___private-operator-tool]:after { width: calc(50% - 512px); }

    }

/* スクリーン幅 1249px～1408px
---------------------------------------- */
@media screen and (min-width: 1249px) {

/* 800x450 */
[class*=___local-side-margin___] { width: 1184px; }

/* 番組情報｜オフセット */
[class*=___local-side-padding___]:before,
[class*=___local-side-padding___]:after,
[class^=___private-operator-tool]:before,
[class^=___private-operator-tool]:after { width: calc(50% - 592px); }

    }

/* スクリーン幅 1409px～1728px
---------------------------------------- */
@media screen and (min-width: 1409px) {

/* 960x540 */
[class*=___local-side-margin___] { width: 1344px; }

/* 番組情報｜オフセット */
[class*=___local-side-padding___]:before,
[class*=___local-side-padding___]:after,
[class^=___private-operator-tool]:before,
[class^=___private-operator-tool]:after { width: calc(50% - 682px); }

    }

/* スクリーン幅 1729px～
---------------------------------------- */
@media screen and (min-width: 1729px) {

/* 1280x720 */
[class*=___local-side-margin___] { width: 1664px; }

/* 番組情報｜オフセット */
[class*=___local-side-padding___]:before,
[class*=___local-side-padding___]:after,
[class^=___private-operator-tool]:before,
[class^=___private-operator-tool]:after { width: calc(50% - 832px); }  

    }


/* ========================================
  非表示フィルター
======================================== */

aside[class^=___banner-panel],
[class^=___zapping-widget],
[class^=___nico-ichiba] { display: none !important; }

/* ========================================
  配置｜カラム
======================================== */

[class^=___watch-page] {display: flex; flex-direction: column; }
#page-top { order:-6; } /* ナビバーにめり込む不具合 */
#siteHeader { order:-5; } /* ナビゲーションバー */
[class^=___page-header-area] { order:-4; } /* 検索バー */
[class^=___program-head-area] { order: -2; } /* 番組名＋タグ一覧 */
[class^=___player-head-area] { order: -1; } /* ニコニ広告 */
[class^=___player-body-area] { order: -3; margin-top: 0px; margin-bottom: 0px; } /* プレイヤー */

    }


@-moz-document url-prefix(http://www.nicovideo.jp/watch/), url-prefix(https://www.nicovideo.jp/watch/) {
/* ----------------------------------------
    ニコニコ動画
    ダークテーマ
    ここから
---------------------------------------- */

/* ========================================
  20200301
======================================== */

/* 次の動画 */
.VideoListPanelContainer .NextPlayVideoContainer { border-bottom: 1px solid #404040; }

/* ========================================
  20191205
======================================== */

/* 新機能の告知 */
.PlayerContainer-nicoruBetaBalloon { display: none !important; }

/* ========================================
  20190725
======================================== */

/* ニコる */
.CommentPanelContainer [data-row-nicoru-rank="1"]{background:#d90}
.CommentPanelContainer [data-row-nicoru-rank="2"]{background:#ea1}
.CommentPanelContainer [data-row-nicoru-rank="3"]{background:#fb0}
.CommentPanelContainer [data-row-nicoru-rank="4"]{background:#fb1}

/* ========================================
  20190725
======================================== */

/* コメント欄 */
.CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell { background: #252525; border-right: 1px solid #353535; }
.CommentPanelDataGrid .CommentPanelDataGrid-TableCell { border-right: 1px solid #252525; }
.CommentPanelDataGrid .CommentPanelDataGrid-TableRow:hover { background: #252525; }
.CommentPanelDataGrid .CommentPanelDataGrid-Body::-webkit-scrollbar, .CommentPanelDataGrid .CommentPanelDataGrid-getScrollbarWidth::-webkit-scrollbar { background: #000; }
.CommentPanelDataGrid .CommentPanelDataGrid-Body::-webkit-scrollbar-thumb { background: #252525; }

/* ========================================
  20190613
======================================== */

/* コメント欄 */
.MainContainer-playerPanel { background: #191919; }
.PlayerPanelContainer { background: inherit; }
.CommentPanelContainer { background: inherit; }

/* ========================================
  Flash版で視聴するコンテナ
======================================== */

.SwitchToFlashLead { background: #232323; }

/* ========================================
  ボトムコンテナ
======================================== */

/* ボトムコンテナ全体
---------------------------------------- */
/* 背景色 121212 */
.BottomContainer { background: inherit; }

/* カード全体
---------------------------------------- */

/* 背景 */
.BottomContainer-card {
    background-color: #1a1a1a;
    border-radius: 8px;
    box-shadow: 0 1px 8px rgba(0,0,0,.05);
}

/* タイトル */
.BottomContainer-cardTitle  { color: #fff; }


/* おすすめリスト
---------------------------------------- */
/* 背景 */
.BottomContainer-card .PlaylistContainer { background-color: #121212; }
.PlaylistContainer-inner { background-color: #121212; }
/* アイテム背景 */
.PlaylistItemContent { background: #1a1a1a; }
/* アローアイコン */
.PlaylistPager-arrow:hover { fill: #09f; }
.PlaylistPager-arrow { background-color: #1a1a1a; fill: #07f; }
.PlaylistPager-arrow.is-disabled { fill: #121212; }
/* 連続再生ボタン */
.BottomContainer-playlistShuffleButton { background-color: #333; }
.BottomContainer-cardButton-inactive { background-color: #333; }
/* ？ */
.PlaylistCardItem.is-playing .PlaylistItemContent { background-color: #05c; }
/* 20170720 */
.Card { background-color: #1a1a1a; }
.Card-title { color: #c0d8ff; }
.InView.PlaylistContainer { background-color: #121212; }

/* コンテンツツリー・親作品
---------------------------------------- */
.ContentTree-parentSummary .Link { color: #07f; background-color: #121212; }
.ContentTree-parentSummary .Link:hover { color: #08f; background-color: #333; }

/* ニコニコ市場
---------------------------------------- */
.GridCell.BottomContainer-main>div:nth-last-of-type(1) { background-color: #f4f4f4; }

/* 矩形広告
---------------------------------------- */
.RectangleAdContainer .Advertisement { background: #1a1a1a; }

/* 不具合報告
---------------------------------------- */
.BottomContainer .FeedbackLink { background: #282828; }


/* ========================================
  プレイヤー
======================================== */

/* プレイヤー左コンテナ
---------------------------------------- */

/* ログインするとコメントできます */
.CommentShutter.CommentShutter--frost { background-color: hsla(0, 0%, 12%, 0.8); }

/* コメント入力 */
.CommentPostContainer input, textarea { color: #fff; }
.CommentPostContainer-commandInput { border-right: 1px solid #252525; background-color: #191919; }
.CommentPostContainer-commentInput { background-color: #191919; }

/* コメント入力｜シャッター */
.CommentShutter { background-color: #191919; }

/* コメント欄
---------------------------------------- */
/* 背景色・幅384px */
.MainContainer-commentPanel { width: 384px; background: #191919; }

/* メニューアイコン欄 */
.VideoMenuContainer-areaLeft,
.VideoMenuContainer-areaRight { background: inherit; }
.VideoMenuContainer:after { border-bottom: 1px solid #333; }
/* メニューアイコン色 */
.VideoMenuContainer-areaLeft .VideoMenuContainer-button { fill: #ccc; }

/* プルダウン */
.GridCell.CommentPanelMenuContainer-mainMenuArea select.DropDownMenu {
    border-radius: 0px;
    border-color: #4c4c4c;
    background: #121212;
    color: #fff;
}

/* NGボタン */
.CommentPanelMenuContainer svg { fill: #cde; }

/* ヘッダー｜背景・スプリッター */
.CommentPanelDataGrid .CommentPanelDataGrid-headerCell { background: #222; border-right: 1px solid #333; }

/* 文字色 */
.CommentPanelContainer .CommentPanelDataGrid { color: #fff; }

/* コメントスプリッター */
.CommentPanelDataGrid [class*=___table-cell___] { border-right: 1px solid #222; }

/* ホバー色 */
.CommentPanelContainer [class*=___table-row___]:hover { background: #333;  color: #fff;}

/* 右クリック */
.CommentPanelContextMenuContainer { border: 1px solid #282828; background: #232323; }

/* 右クリック｜ホバー */
.CommentPanelContextMenuContainer-menuHeading:hover,
.CommentPanelContextMenuContainer-menuItem:hover { background: #06f; }

/* NGリスト文字色 */
.ViewerNGList-item { color: #000; }

/* NG追加文字色 */
.ViewerNGRegister-title { color: #000; }

/* スクロールバー色 */
.CommentPanelDataGrid-getScrollbarWidth::-webkit-scrollbar,
.CommentPanelDataGrid [class*=___body___]::-webkit-scrollbar-corner,
.CommentPanelDataGrid [class*=___body___]::-webkit-scrollbar { width: 8px; height: 8px; background: #000; }
.CommentPanelDataGrid [class*=___body___]::-webkit-scrollbar-thumb { background: #404040; }

/* コメ欄下｜再生を押すと出る広告 */
.CommentPanelContainer { bottom: 0; }
.MiniBannerAdContainer { display: none; border-top: 0px solid #e5e5e5; }


/* ========================================
  ヘッダーコンテナ
======================================== */

/* コンテナ全体
---------------------------------------- */

/* スペース調整 */
.HeaderContainer-row { margin-top: 8px; }

/* 背景色 121212 */
.HeaderContainer { background: inherit; }

/* セパレータ色 */
.VideoMetaContainer>*, .VideoMetaOverflowMenuContainer { border-left: 1px solid #000; box-shadow: -1px 0 0 0 #252525; }

/* コンテナ1
---------------------------------------- */
/* 動画タイトル */
.VideoTitle { color: #fff; }

/* 動画概要欄 */
.VideoDescription-plain { color: #a9b3bd; }

/* 検索窓 121212 */
.HeaderContainer .SearchBox { border: 1px solid #222; background: inherit; }
.HeaderContainer .SearchBox-input { border: none; box-shadow: inset 0 0 8px 0 #252525; }
.HeaderContainer input { background-color: #121212; color: #fff }

/* 検索窓｜POPUP */
.HeaderContainer .SearchBox-optionMenu { background: #202020; border: 1px solid #111; }
.HeaderContainer .SearchBox-optionMenuItem:hover { background: #333; }

/* コンテナ2
---------------------------------------- */
/* 動画説明文を開く｜色 0be・08f */
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand { background: linear-gradient(90deg,hsla(0,0%,96%,0),#202020 16%); }
.VideoDescriptionExpander .VideoDescriptionExpander-switch { color: #08f; }
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand:before { border-top: 8px solid #08f; }
.VideoDescriptionExpander .VideoDescriptionExpander-switchCollapse:before { border-bottom: 8px solid #08f; }

/* コンテナ3
---------------------------------------- */
/* チャンネル・投稿動画ボタン */
.VideoOwnerChannelLink,
.VideoOwnerUploadedLink { border: 1px solid #06f; }
.VideoOwnerChannelLink-button:visited,
.VideoOwnerUploadedLink-button:visited { color: #07f; }

/* 投稿日時｜上段 */
.VideoUploadDateMeta-title,
.VideoViewCountMeta-title,
.CommentCountMeta-title,
.MylistCountMeta-title,
.CategoryRankMeta-categoryName,
.CategoryRankMeta-title { color: #abc; }

/* 投稿日時｜下段 */
.VideoUploadDateMeta-dateTimeLabel,
.VideoViewCountMeta-counter,
.CommentCountMeta-counter,
.MylistCountMeta-counter,
.CategoryRankMeta-yesterdayRank { color: #cde; }

/* …この動画の記事 */
.VideoMetaOverflowMenuContainer-menu { border: 1px solid #111; background: #202020; }
.VideoMetaOverflowMenuContainer-menuBlock:not(:first-of-type) { border-top: 1px solid #121212; }
/* ボタン？アイコン */
.HeaderContainer .NicoDicIcon.is-unavailable { background-color: #111; }

/* コンテナ4
---------------------------------------- */
/* 投稿者が生放送中 */
.OwnerNicoliveBanner { background: #202020; }
.OwnerNicoliveBanner-time { color: #999; }
a.Link.OwnerNicoliveBanner-lead { border: 2px solid red; color: #c00; }

/* コンテナ5
---------------------------------------- */
/* タグ｜スペース調整 */
.TagContainer { margin-top: 4px; }

/* タグ｜ボタン色 */
.HeaderContainer .TagItem { background-color: #202020; border: 1px solid #232323; border-radius: 12px; }

/* タグ｜ボタン幅｜未登録用語→縮小 */
.TagItem:not(.is-nicodicAvailable) { padding-right: 12px !important; }

/* タグ｜編集 */
.TagContainer-editButton { background: linear-gradient(90deg,hsla(0,0%,100%,0),#222 16%); border-radius: 4px; }
.TagContainer-editButton:hover { background: #05e; }

/* タグ編集｜ログイン */
.TagContainer .LoginRequirer.is-noLogin .TagContainer-editButton { background: linear-gradient(90deg,hsla(0,0%,100%,0),#222 16%); border-radius: 4px; }
.TagContainer .LoginRequirer.is-noLogin .TagContainer-editButton:hover { background: #05e; }
/* タグ編集｜ログイン｜POPUP */
.LoginLeadContainer { background: #202020; }
.LoginLeadContainer p { border-bottom: 1px solid #111; }
.LoginLeadContainer-cancel { color: #06f; }

/* タグ｜編集窓｜背景色 */
.TagContainer.is-editing { background-color: #121212; border: 1px solid #333; }
/* タグ｜ボタン｜文字色 */
.TagItem-name { color: #07f; }

/* タグ｜入力 */
.TagSuggestionInput-input { background-color: #121212; color: #fff; border: 1px solid #333; border-top-left-radius: 4px; border-bottom-left-radius: 4px; }
/* タグ｜登録 */
.TagEditForm .TagEditForm-submitButton { border-top-right-radius: 4px; border-bottom-right-radius: 4px; }
/* タグ｜注意 */
.TagContainer-editInformation { color: #abc; }


/* ========================================
  ルートコンテナ
======================================== */

/* 背景・文字色 */
.WatchAppContainer { background: #121212; color: #eee}

/* リンク色 0ad・07f */
.WatchAppContainer a { color: #07f; }

/* ニコ動運営からの緊急告知 */
#PAGEURGENT { background: #000; border-bottom: 1px solid #222; }

/* ========================================
  非表示フィルター
======================================== */

.MarqueeContainer,
.CommentPanelBannerAd,
.VideoMenuContainer-uadBalloonWrapper,
.GridCell.BottomContainer-main>div:nth-last-of-type(1),
.GridCell.BottomContainer-main>div:nth-last-of-type(2) { display: none; }

/* ========================================
  配置｜カラム
======================================== */

.WatchAppContainer-main { display: flex; flex-direction: column; }
.HeaderContainer {order: -1; padding-top: 0; }
.MainContainer {order: -2; margin-top: 24px; }
.HeaderContainer {display: flex; flex-direction: column; }
