css:
@-moz-document url-prefix("https://blog.csdn.net/") {
#csdn-toolbar {
    background-color: #FFF;
    position: fixed;
    top: 0px;
    z-index: 999;
    opacity: 0.9
}
body {
    padding-top: 50px!important
}
.grade-detail-box {
    display: none!important;
}
#mainBox {
    padding: 0!important;
}
.jump_article,.toolbar-advert,.csdn-redpack-lottery-btn-box {
    display: none!important
}
.bdshare-button-style0-16 {
    display: none;
}
#article_content {
    height: auto!important
}
.hidearticle-box {
    display: none
}
#adContent {
    visibility: hidden;
}
#csdn-toolbar {
    background-color: #FFF;
    position: fixed!important;
    top: 0px;
    z-index: 999;
    opacity: 0.9
}
body {
    padding-top: 50px!important
}
.hide-article-box {
    display:none!important;
}
#article_content {
    display:block!important;
    height:auto!important;
    overflow:visible!important;
}
.container#mainBox {
    width: calc(100% - 200px)!important;
    margin-left: 100px;
    height: auto!important;
    padding: 0!important;
}
.container#mainBox main {
    float: right!important;
    width: 100%!important;
}
aside {
    z-index:9999!important;
    position:fixed!important;
    width:250px!important;
    box-shadow:1px 1px 1px #DDD;
    height:70px!important;
    left:40px!important;
    top:60px!important;
    opacity:0.7;                  
    background:rgba(200, 200, 200, 0.4);
    overflow:hidden;
    border-radius:5px;
}
aside:hover {
    padding:35px!important;
    overflow-y:scroll;
    display:block;
    position:fixed;
    opacity:1;    
	min-width: 550px;
    width:50vw!important;
	height: calc(100vh - 100px)!important;
    left:40px;
    top:60px;
}
.recommend-box, .article-list {
    margin-top: 30px;
}
.comment-list {
    padding:15px;
}
.login-mark {
    display:none!important;
}
#passportbox {
    display:none!important;
}
#dmp_ad_58{
    display:none;
}
.recommend-recommend-box{
    display:none;
}
#footerRightAds{
    display:none;
}
.mb8{
    display:none;
}
td:hover{
	cursor:pointer;
	padding:20px!important;
}
.aside-box{
	width: 100%!important;
}
.aside-box:hover,.aside-box:active,.aside-box:focus{
	width: calc(100% + 2px)!important;
	margin-left: -1px;
	border-radius: 4px;
    border:1px solid rgba(255,0,0,.5);
}
*{
    transition: all 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
}
.groupfile {
    width: 100%!important;
}
}
