css:
@-moz-document domain("skillshare.com") {
:root {
    --color_bg1: #15161a;
    --color_bg2: #1b1c21;
    --color_bg3: #212329;
    --color_bg4: #272a30;
    --color_bg5: #2d3238;
    --color_bg6: #343940;
    --color_bg7: #3a4147;
    --color_bg8: #40484f;
    
    --color_bg_up: rgba(255, 255, 255, 0.03);
    --color_bg_up2: rgba(255, 255, 255, 0.06);
    --color_bg_up3: rgba(255, 255, 255, 0.09);
    
    --color_border0: rgba(255, 255, 255, 0.02);
    --color_border1: rgba(255, 255, 255, 0.05);
    --color_border2: rgba(255, 255, 255, 0.1);
    --color_border3: rgba(255, 255, 255, 0.2);
                                            
    --color_accent1: #e5ae5c;
    --color_accent2: #f2bd61;
    --color_accent3: #ffd18c;
    
    --color_fg1: #e9f2f2;
    --color_fg2: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg4: #c4cacc;
    --color_fg5: #b8bdbf;
    --color_fg6: #abb0b3;
}

/* BACKGROUNDS */

html, body, #class-details.new-layout #video-region, #site-content, #class-details.new-layout #video-region .video-player-bottom .video-watchers, #profile .section, #groups .group-grid, #groups .group-grid .group-center {
	background-color: var(--color_bg1);
}

.site-header.sticky, #class-details #main-region .underline-tabs-wrapper, #footer-tall, #class-details.new-layout #video-region .video-player-wrapper .playlist, .replies, .white-content-container, #profile .header-grid, #profile .empty-section, #groups .group-grid .group-sidebar-left, #groups .group-grid .group-sidebar-right, .site-header, .group-item-view, #abstract-popup-view {
	background-color: var(--color_bg2);
}

.replies .arrow:before, .popover.bottom .arrow:after, .dialog.bottom .arrow:after {
	border-bottom-color: var(--color_bg2);
}

.video-playlist-module .session-list .session-item.active .selection, .video-playlist-module .session-list .session-item:hover .selection, input, .popover.shadow .inner-popover, .dialog.shadow .inner-popover, .popover.shadow, .dialog.shadow, .groups-discover-bar, #home-index .gray-section, .thumbnail-custom-lists, select, .ss-select-wrapper .ss-select, .table-legend tr.hovered, #settings-payments .credit-cards-container .credit-cards .credit-card, #classes-index .side-sticky-menu-wrapper .side-sticky-menu .content .content-body .tag-link-wrapper .flyover, .filters-dropdown-wrappers {
	background-color: var(--color_bg3);
}

.filters-dropdown-wrapper {
	background-color: var(--color_bg3) !important;
}

textarea, .group-item-view .group-buttons .btn-preview, #groups .group-grid .group-center .group-content .tag-select .tag-select-button, #groups .group-grid .group-sidebar-right, .tag.light, .thumbnail-custom-lists-empty-image, .filters-tags-wrapper .top-tags label {
	background-color: var(--color_bg_up);
}

.discussions-wrapper .cta .discussion-form-container .discussion-form .discussion-types .discussion-type, .filters-dropdown-wrapper .checkbox-item:hover {
	background-color: var(--color_bg_up2);
}

.discussions-wrapper .cta .discussion-form-container .discussion-form .discussion-types .discussion-type:hover, .btn-follow, .btn.bordered, .tag-cloud .tag-cloud-container .cloud-tag .tag, .group-item-view .group-border, .group-item-view:hover .group-buttons .btn-preview:hover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button:hover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button.selected, a.tag.light:hover, #classes-index .side-sticky-menu-wrapper .side-sticky-menu .content .content-body .tag-link-wrapper.primary-tag-link-wrapper:hover, #classes-index .side-sticky-menu-wrapper .side-sticky-menu .content .content-body .tag-link-wrapper.reset-tag-link-wrapper:hover {
	background-color: var(--color_bg_up3);
}

.carousel .carousel-bar-content {
	background-color: var(--color_bg8);
}
.carousel .carousel-bar-active .carousel-bar-content {
	background-color: var(--color_fg6);
}

.right-sidebar-btn, #classes-index .side-sticky-menu-wrapper .side-sticky-menu {
	background-color: transparent;
}

/* BORDERS */

.discussions-wrapper .cta .discussion-form-container .discussion-form .discussion-types, .popover.shadow.bottom .arrow:before, .dialog.shadow.bottom .arrow:before {
	border-color: transparent;
}

#classes-index .side-sticky-menu-wrapper .side-sticky-menu .content .content-body .tag-link-wrapper .flyover {
	box-shadow: none;
}

#class-details #main-region .underline-tabs-wrapper, #home-index .gray-section, #profile .header-grid {
	border-top: 1px solid var(--color_border0);
    border-bottom: 1px solid var(--color_border0);
}

.replies, .thumbnail-custom-lists, #profile .empty-section, #settings-payments .credit-cards-container .credit-cards .credit-card, #classes-index .side-sticky-menu-wrapper .side-sticky-menu {
	border: 1px solid var(--color_border0);
}

.video-playlist-module .playlist .summary {
	border-bottom: 1px solid var(--color_border1);
}

.popover.shadow, .dialog.shadow, .discussions-wrapper .cta .discussion-form-container .discussion-form .discussion-types .discussion-type, .table-legend tr.hovered, .group-item-view {
	border: 1px solid var(--color_border1);
}

textarea, .tag.light, .white-content-container, #groups .group-grid .group-center .group-content .tag-select .tag-select-button, .underline-tabs .underline {
	border-color: var(--color_border1);
}

#class-details.new-layout #video-region .video-player-wrapper .playlist {
	border: 1px solid var(--color_border1);
}

.groups-discover-bar {
	border-top: var(--color_border1);
}

.site-header, .site-footer, #autocomplete-wrapper, .replies .replies-list li:last-child, .discussions-wrapper .cta, .discussions-wrapper .inline-discussion, .replies .show-all, #footer-tall .base-footer, .btn.bordered, .white-content-container .container-header, input[type=text], input[type=password], input[type=number], .ss-select-wrapper .ss-select, #profile .sidebar-content .follow-stats, .user-stats-information, .group-item-view .group-buttons .btn-preview, #groups .group-grid .group-header-content.discover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button:hover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button.selected, #groups .group-grid .group-sidebar-right .right-sidebar-btn, #classes-index .page-content .featured-class-wrapper, #classes-index .page-content .top-classes-wrapper, #classes-index .side-sticky-menu-wrapper .side-sticky-menu .content .content-body .tag-link-wrapper .flyover, .empty-state-style, .empty-state, .empty-area, .placeholder-area, .user-generated-list-header, .filters-menu, .filters-tags-wrapper .top-tags label, .filters-dropdown-wrapper {
	border-color: var(--color_border2);
}

.btn.bordered:hover, .no-touch .underline-tabs .tab a:hover, .popover.shadow .inner-popover, .dialog.shadow .inner-popover, .site-header .nav-menu-browse .nav-menu-other, .site-header .nav-menu-delimiter, .google-account-connect, #settings-payments .layout-col-right .section, .group-item-view:hover .group-buttons .btn-preview {
	border-color: var(--color_border3);
}

/* FOREGROUND */

.group-item-view:after {
	box-shadow: none;
}

.site-header .nav-item-link:hover, a.secondary:hover, .sort-order a:hover, a.link-main:hover, .sort-order .active a:hover, a.primary:hover {
	color: var(--color_fg1);
}


.site-header .nav-item-link, .video-player-container .video-player-layout .class-details-header-teacher-link, #class-details.new-layout #video-region .video-player-bottom .video-watchers, a.secondary, .sort-order a, .ss-icon-play:before, .video-player-module .vjs-video .vjs-control-bar .vjs-play-control:before, .video-player-module .vjs-video .vjs-big-play-button:before, .video-playlist-module .session-list .session-item.active .selection .session-item-title, .video-playlist-module .session-list .session-item.active .selection .completion-button, .video-playlist-module .session-list .session-item.active .selection .playback-control-wrapper, .video-playlist-module .session-list .session-item:hover .selection .session-item-title, .video-playlist-module .session-list .session-item:hover .selection .completion-button, .video-playlist-module .session-list .session-item:hover .selection .playback-control-wrapper, #class-details .btn-save-container.icon-menu-button a.iconed-btn.btn.bordered.small, .btn.bordered, a.link-main, .sort-order .active a, a.primary, a:hover, .tag-cloud .tag-cloud-container .cloud-tag .tag:hover, .site-header .nav-menu-notifications-icon-dark:before, .site-header .nav-menu-browse .nav-menu-subcategory a:hover, .profile-info .no-headline:hover, .sidebar-nav .nav-list li a:hover, .group-item-view:hover .group-buttons .btn-preview:hover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button:hover, #groups .group-grid .group-center .group-content .tag-select .tag-select-button.selected, a.tag.light:hover, .class-stats {
	color: var(--color_fg3);
}

input, .video-playlist-module .playlist .summary, .discussions-wrapper .cta .discussion-form-container .discussion-form .discussion-types .discussion-type a {
	color: var(--color_fg4);
}

body, .video-playlist-module .session-list .session-item .selection, .underline-tabs .tab a, .tag.light, .group-item-view:hover .group-buttons .btn-preview, .group-item-view .group-buttons .btn-preview, #groups .group-grid .group-center .group-content .tag-select .tag-select-button {
	color: var(--color_fg5);
}

.secondary-type, a, .user-snippet p, .thumbnail-secondary-text, .site-header .nav-menu-browse .nav-menu-subcategory a, .profile-info .no-headline, .user-stat-row-wrapper .user-stat, .user-stat-row-wrapper, .sidebar-nav .nav-list li a, .user-stats-information, #groups .group-grid, .group-item-view, #groups .group-grid .group-header-text, #groups .group-grid .group-header-discover-text, #groups .group-grid .group-sidebar-right .right-sidebar-btn, #classes-index .page-content .featured-class-wrapper .featured-class .class-data-wrapper .class-name, #classes-index .page-content .featured-class-wrapper .featured-class .class-data-wrapper .class-name:hover {
	color: var(--color_fg6);
}

/* LAYOUT */

.replies .arrow:after {
    width: 10px;
    height: 10px;
    background: #fff;
    border: 1px solid #fff;
}

img[src="https://static.skillshare.com/assets/images/default-profile-lrg.jpg"], .site-header-logo-image {
	filter: invert(0.89) hue-rotate(180deg);
}

p {
	font-weight: 300 !important;
}
}
