css:
@-moz-document url-prefix("https://www.shanbay.com/") {
/****首页****/
/*首页背景颜色*/
.navbar-fixed-top, body {
    background-color: #293134
}

.checkin-container-outer {
    background-color: #3F4B4E ! important;
}

/*首页 container*/
body .main-body #notification-container .noty_theme_default, body .main-body .tasks-outer-container .tasks-container .task, body .main-body .tasks-outer-container .tasks-container .task .task-top .task-name, .daily-quote-container .quote, body .main-body .tasks-outer-container .checkin-daily-promotion .span2 .promotion-item-container, body .main-body .tasks-outer-container .checkin-container #checkin-container-inner, body .main-body .tasks-outer-container .checkin-container #associate-promotion {
    background-color: #3F4B4E;
    color: #E0E2E4
}
/*打卡*/
body .main-body .tasks-outer-container .checkin-container .checkin-note, body .main-body .tasks-outer-container .checkin-container .checkin_user_note_container {
    background-color: #293134;
}
body .main-body .tasks-outer-container .checkin-container .note .user-checkin-note-angle {
    border-right: 10px solid #293134;
}

/*次级导航栏*/
#main-navbar.navbar > .sub-menu.active {
    background-color: #293134;
}

/****听力****/
/*听力container背景颜色*/

/*听力学习*/
div.container.main-body,body .main-body.container .modal.book-detail-modal .modal-header {
    background-color: #293134;
    color: #E0E2E4
}

.navigate .entry .book ul.articles li.article.finished, body .main-body.container .modal.book-detail-modal .nav-tabs li a {
    color: #E0E2E4
}

body .main-body.container .modal.book-detail-modal .nav-tabs li a:hover {
    color: #3F4B4E;
}

/*学习预备界面*/
div.container.main-body.review, .train-summary-container .white-background, .train-summary-container,.train-summary-container .share-container, body .main-body.container .modal.book-detail-modal .tab-content, body .main-body.container .modal.book-detail-modal .modal-body, body .main-body.container .modal.book-detail-modal .modal-footer {
    background-color: #3F4B4E;
}
.section.sentence:hover, .section.article:hover {
    background-color: #209E85;
}
/*重听、听懂了文字颜色*/
.operations-container p {
    color: #E0E2E4
}

/*填入单词界面*/
input[disabled], select[disabled], textarea[disabled], input[readonly], select[readonly], textarea[readonly] {
    background-color: #2b3335;
}
.sentence-word-input.focus {
    color: #E0E2E4;
}
/*笔记*/
.notes-row .tabbable, .notes-row .tabbable ul li.active a {
    background-color: #293134;   
}
.notes-row .listen-tabs span.listen-tab:after {
    border-bottom: 18px solid #293134;
}

/*听力课程右侧文字*/
body .main-body.container .left-nav-container .nav-list a {
    color: #E0E2E4
}
body .main-body.container .left-nav-container .nav-list .active a {
    background-color: #209E85
}
.nav-list > li > a, .nav-list .nav-header {
    text-shadow: none
}

body .main-body.container .right-content-container .sentence-books-header-row h3 {
    color: #E0E2E4
}
/*听力计划*/
.plans .plan-block, #join-plan-modal, #join-plan-modal .modal-footer {
    background-color: #3F4B4E
}

/*购买提示: bug*/
modal hide book-detail-modal in {
    background-color: #3F4B4E
}

/*听力设置*/
.nav.nav-tabs.nav-stacked li a {
    color: #E0E2E4
}

/****口语****/
.intro {
    background: #293134
}
.intro-courses-content {
    background-color: #3F4B4E;

}
.intro-courses-content .category-item h4, .intro-courses-content .course-item h5 {
    color: #E0E2E4   
}

/****新闻****/
.sb-nav .nav-sub, .page-container {
    background-color: #293134;
}

.sideBar .weekly-pop-news-title, .news li .news-title, .sideBar li .hot-news-title, .page-content, .reply-item .reply-user-name {
    color: #E0E2E4   
}
.news li:hover, .sideBar li:hover {
    background-color: #209E85;    
}

.article-body .article-content .sentence, .reply-item .reply-content, .news li .news-summary {
    color: #a4a6a8 
}

.word-card, .article-body .notes-modal {
     background-color: #3F4B4E;  
}

/****单词****/
#review .hero-unit.start-review.new-start-review, #review .hero-unit.checkin.new-start-review, .well, #review .test-box .spelling-shortcuts-hint p, #review #learning-box .spelling-shortcuts-hint p, #review .test-box .spelling-shortcuts-hint, #review #learning-box .spelling-shortcuts-hint, #review #summary-box .spelling-shortcuts-hint, #review .test-box .learning-hint .span10 .hint-content, #review #learning-box .learning-hint .span10 .hint-content, #review #summary-box .learning-hint .span10 .hint-content,#review #preview .sentence.hint-content, #review .hero-unit.start-review.overview, #review .hero-unit.checkin.overview, #review .promotion .span6, .nav.nav-tabs.nav-horizon, #learnings-library .learning {
    background-color: #3F4B4E;
}
#review .hero-unit.start-review small, #review .hero-unit.checkin small, #review .test-answers a, #review .cndf, #review #learning-box #learning-examples-box ol li .body .enex .vocab, .holder a, #review .promotion .title, .nav.nav-tabs.nav-horizon li a, #learnings-library .learning .wordinfo .definition, .welcome-text h2 {
    color:#E0E2E4
}

.nav-tabs > li > a {
    color: #666
}

#review .test-box .learning-hint .span10 .vocab, #review #learning-box .learning-hint .span10 .vocab, #review #summary-box .learning-hint .span10 .vocab, #review #preview .sentence.hint-content span.vocab{
    color:  #209E85;
}

.nav.nav-tabs.nav-horizon li a:hover {
    background-color: #209E85;
    color: #E0E2E4;
}
.nav.nav-tabs.nav-horizon li a, table tbody .notification-name, table tbody .checked span {
    color: #E0E2E4;
}
}
