    position:relative;
    width:48.5em ;
    left:0em;
    z-index:9999;
}
    
.filter-toolbar.hs8 > div {
    flex-grow:0 !important;
}
    
.filter-toolbar {
    display:flex;
    position:relative;
    flex-flow: row wrap;
    justify-content: flex-start;
    left:-0.5em;
}
    
.filter-toolbar .TextBox_text-box__2OcST {
    position: fixed;
    left: 13em;
    top:4em;
    width:6.3em;
    height:3.07em !important;
    z-index:30001;
}
    
.filter-toolbar .TextBox_clear-button__A1eCv {
    position: fixed;
    height: 3em;
    width: 2em;
    top:4em;
    left: 19.3em;
    z-index:30001;
    outline:solid;
    outline-width:0.01em;
    outline-color:#555;
}
    
.filter-toolbar.hs8 > .Stack_stack__lkMj0.Stack_hs8__17p-8, .flex.hs8 {
    position: fixed;
    right: 0.2em;
    top:6em;
    width:11.5em;
    z-index:20000;
}
    
.low-confidence-box  {
    position: fixed;
    top:9em;
    right:0.2em;
    z-index:20000;
    width:11em;
}
}
}

