css:
@-moz-document domain("yandex.ru") {
.news__header {
    width: 600px;
    margin-bottom: 10px;
    line-height: 20px;
}

.news__agency-icon {
    display: none !important;
}

.list__item.news__animated-item.list__item_fade_in > a[aria-label*="Украин"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 29px;
    margin-bottom: -5px;
}


.list__item.news__animated-item.list__item_fade_in > a[aria-label*="Путин"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 45px;
    margin-bottom: -5px;
}


.list__item.news__animated-item.list__item_fade_in > a[aria-label*="Крым"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 30px;
    margin-bottom: -5px;
}



ol[class="list news__list"] > .list__item > a[aria-label*="Украин"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 29px;
    margin-bottom: -5px;
}

ol[class="list news__list"] > .list__item > a[aria-label*="Крым"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 30px;
    margin-bottom: -5px;
}

ol[class="list news__list"] > .list__item > a[aria-label*="Путин"]{
    background: url('data:image/png;base64,') no-repeat;
    color: transparent;
    display: inline-block;
    overflow: hidden;
    padding-left: 45px;
    margin-bottom: -5px;
}

a[aria-label*="Крым"] > .news__agency-icon{
    display: none !important;
}

a[aria-label*="Украин"] > .news__agency-icon{
    display: none !important;
}

a[aria-label*="Путин"] > .news__agency-icon{
    display: none !important;
}

}
