css:
@-moz-document domain("aypierre.fr") {

/* header */
.header-theme-switch input:checked ~ label {background: #201c2b;}
.header-navbar-item.live.isLive::after {background-color: greenyellow;}

/* sidebar gauche */
.sidebar-header {background: initial;}
.sidebar {
    background: #0e0c13;
    background: -moz-linear-gradient(left, #0e0c13 0%, #2b3243 100%);
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #0e0c13), color-stop(100%, #2b3243));
    background: -webkit-linear-gradient(left, #0e0c13 0%, #2b3243 100%);
    background: -o-linear-gradient(left, #0e0c13 0%, #2b3243 100%);
    background: linear-gradient(left, #0e0c13 0%, #2b3243 100%);
}

.sidebar-navbar-item{
    color: #aab1c5;
}

.sidebar-navbar-item.active{
    color: #fff;
}

.sidebar-navbar-item-icon{
    opacity: 0.6;
}

.sidebar-navbar-item.active .sidebar-navbar-item-icon{
    opacity: 1;
}

.sidebar-navbar-item:hover,
footer a:hover,
footer span:hover {
    color: #0091ff;
}
.sidebar-footer-item.important {color: #FF002D;}

/* boutons */
.button,
.highlight {background: #0e0c13;}

.button:hover,
.highlight:hover {background: #161121;}

.page-header-logo img{
  filter: grayscale(.5);
  -webkit-filter: grayscale(.5);
}

.header-theme-icon,
.header-socials-item-icon,
.header-hamburger-icon img:hover{
  filter: grayscale(.8);
  -webkit-filter: grayscale(.8);
}

/* planning */
.planning-mode-item-icon:hover{
    cursor: pointer;
}

.planning-line-hours-event {
    background-color: #45506b;
    border-color: #2b3243;
}

.planning-line-hours-event[style="left: 69.2308%; width: 2.44017%;"],
.planning-line-hours-event[style="left: 69.2308%; width: 2.51282%;"],
.planning-line-hours-event[style="left: 69.2308%; width: 0.0641026%;"]{
    font-size: 5px !important;
}

.planning-line-hours-event-tooltip {
    background: #0e0c13;
    background: -moz-linear-gradient(top, #0e0c13 0%, #030305 100%);
    background: -webkit-linear-gradient(top, #0e0c13 0%,#030305 100%);
    background: linear-gradient(to bottom, #0e0c13 0%,#030305 100%);
}

.planning-line-hours-event-tooltip::before {
    border-color: #0e0c13 transparent;
}

.button:disabled {
    background-color: #ffad05;
}

/* en ligne */
div.highlight[ng-show="live"]{
    background: #4aba21;
}

/* extensions */
.button_image {
    background-color: #0e0c13;
    color: #d7d7d7;
}

.button_image:hover {
    background-color: #161121;
}

}
