css:
@-moz-document domain(qwant.com) {
body.v4 .results-page--web .result--web--title {
 color:#ff3b4a;
}

body.v4 .results-page--web .result--web--link:visited .result--web--title {
 color:#B20070
}

body.v4.device .results-page--web .result--web--title:visited {
 color:#B20070
}
body.v4.device .results-page--web .result--web--link:visited .result--web--title {
 color:#B20070
}

body.v4 .results-page--shopping .product_info_column_right h3 a:visited {
 color:#B20070
}

.result h3 a:visited {
 color:#B20070
}

.result__extensions__item__title--link:visited {
 color:#B20070
}

.results-page--web .results-column--smart .result-smart__video__item:visited .result-smart__item__title,.results-page--web .results-column--smart .result-smart__news__item:visited .result-smart__item__title,.results-page--web .results-column--smart .result-smart__social__item:visited .result-smart__item__title {
 color:#B20070
}

.results-column--shopping .product_info_column_right .product_title:visited {
 color:#0064be
}

body.v4 .results-page--web .result__url__long {
 color:#4ba2ea;
}

.sidebar__item.sidebar__item--web:hover a, .sidebar__item.sidebar__item--web:hover span, .sidebar__item.sidebar__item--web.sidebar__item--current a, .sidebar__item.sidebar__item--web.sidebar__item--current span {
    color: #65cee7 !important;
}

.sidebar__item.sidebar__item--news:hover a, .sidebar__item.sidebar__item--news:hover span, .sidebar__item.sidebar__item--news.sidebar__item--current a, .sidebar__item.sidebar__item--news.sidebar__item--current span {
    color: #4ba2ea !important;
}

.sidebar__item.sidebar__item--social:hover a, .sidebar__item.sidebar__item--social:hover span, .sidebar__item.sidebar__item--social.sidebar__item--current a, .sidebar__item.sidebar__item--social.sidebar__item--current span {
    color: #86c765 !important;
}

.sidebar__item.sidebar__item--images:hover a, .sidebar__item.sidebar__item--images:hover span, .sidebar__item.sidebar__item--images.sidebar__item--current a, .sidebar__item.sidebar__item--images.sidebar__item--current span {
    color: #ffe600 !important;
}

.sidebar__item.sidebar__item--videos:hover a, .sidebar__item.sidebar__item--videos:hover span, .sidebar__item.sidebar__item--videos.sidebar__item--current a, .sidebar__item.sidebar__item--videos.sidebar__item--current span {
    color: #ff3b4a !important;
}

.sidebar__item.sidebar__item--shopping:hover a, .sidebar__item.sidebar__item--shopping:hover span, .sidebar__item.sidebar__item--shopping.sidebar__item--current a, .sidebar__item.sidebar__item--shopping.sidebar__item--current span {
    color: #af27cc !important;
}
}
