css:
@-moz-document domain("praha7.cz") {
/*
Theme Name: praha7
Theme URI: http://praha7.cz
Author: inQool a.s.
Author URI: https://inqool.cz/
Description: Description
Version: 1.0.0
License: GNU General Public License v2 or later
License URI: LICENSE
Text Domain: praha7
Tags:

This theme, like WordPress, is licensed under the GPL.
Use it to make something cool, have fun, and share what you've learned with others.

Normalizing styles have been helped along thanks to the fine work of
Nicolas Gallagher and Jonathan Neal http://necolas.github.io/normalize.css/

Changed colors back to Prague 7 corporate by MP Studio
*/

/*
# Normalize
*/

html {
  font-family: "Haas Grot Text Web", Helvetica, Arial, sans-serif;
  -webkit-text-size-adjust: 100%;
  -ms-text-size-adjust: 100%;
}

body {
  margin: 0;
  overflow-y: scroll;
  overflow-x: hidden;
  font-family: "Haas Grot Text Web", Helvetica, Arial, sans-serif;
  -moz-font-feature-settings: "liga", "kern";
  -moz-font-feature-settings: "liga=1, kern=1";
  -ms-font-feature-settings: "liga", "kern";
  -o-font-feature-settings: "liga", "kern";
  -webkit-font-feature-settings: "liga", "kern";
  font-feature-settings: "liga", "kern";
}

article,
aside,
details,
figcaption,
figure,
footer,
header,
main,
menu,
nav,
section,
summary {
  display: block;
  clear: both;
}

audio,
canvas,
progress,
video {
  display: inline-block;
  vertical-align: baseline;
}

audio:not([controls]) {
  display: none;
  height: 0;
}

[hidden],
template {
  display: none;
}

a {
  background-color: transparent;
}

a:active,
a:hover {
  outline: 0;
}

abbr[title] {
  border-bottom: 1px dotted;
}

b,
strong {
  font-weight: bold;
}

dfn {
  font-style: italic;
}

h1 {
  font-size: 2em;
  margin: 0.67em 0;
}

mark {
  background: #ff0;
  color: #000;
}

small {
  font-size: 80%;
}

sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}

sup {
  top: -0.5em;
  margin-left: 1px;
  display: inline-block;
}

sub {
  bottom: -0.25em;
}

img {
  border: 0;
}

svg:not(:root) {
  overflow: hidden;
}

figure {
  margin: 1em 40px;
}

hr {
  box-sizing: content-box;
  height: 0;
}

pre {
  overflow: auto;
}

code,
kbd,
pre,
samp {
  font-family: monospace;
  font-size: 1em;
}

button,
input,
optgroup,
select,
textarea {
  color: inherit;
  font: inherit;
  margin: 0;
}

button {
  overflow: visible;
}

button,
select {
  text-transform: none;
}

button,
html input[type="button"],
input[type="reset"],
input[type="submit"] {
  -webkit-appearance: button;
  cursor: pointer;
}

button[disabled],
html input[disabled] {
  cursor: default;
}

button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

input {
  line-height: normal;
}

::-webkit-input-placeholder {
  font-style: italic;
}

:-moz-placeholder {
  font-style: italic;
}

::-moz-placeholder {
  font-style: italic;
}

:-ms-input-placeholder {
  font-style: italic;
}

input[type="checkbox"],
input[type="radio"] {
  box-sizing: border-box;
  padding: 0;
}

input[type="number"]::-webkit-inner-spin-button,
input[type="number"]::-webkit-outer-spin-button {
  height: auto;
}

input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none;
}

fieldset {
  border: 1px solid #c0c0c0;
  margin: 0 2px;
  padding: 0.35em 0.625em 0.75em;
}

legend {
  border: 0;
  padding: 0;
}

textarea {
  overflow: auto;
  resize: vertical;
}

optgroup {
  font-weight: bold;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

td,
th {
  padding: 0;
}

/*
# Typography
*/

body,
button,
input,
select,
textarea {
  color: #242424;
  font-family: inherit;
  font-size: 14px;
  line-height: 1.2;
  letter-spacing: 0.02em;
  word-spacing: 0.05em;
}

@media only screen and (max-width: 767px) {
  body,
  button,
  input,
  select,
  textarea {
    font-size: 17.5px;
  }
}

@media only screen and (max-width: 760px) {
  body,
  button,
  input,
  select,
  textarea {
    font-size: 9px;
  }
}

h1,
h2,
h3,
h4,
h5,
h6 {
  clear: both;
  font-family: "Haas Grot Disp Web", Helvetica, Arial, sans-serif;
  font-weight: bold;
}

h2,
.h2 {
  font-size: 32px;
  line-height: 38px;
  letter-spacing: 0.005em;
}

.h3,
h3 {
  font-size: 20px;
  letter-spacing: 0.005em;
}

.float-title {
  display: inline-block;
  margin: 25px 10px 25px 0;
  padding-top: 20px;
}

@media only screen and (max-width: 767px) {
  h2,
  .h2 {
    font-size: 25px;
  }

  .h3,
  h3 {
    font-size: 16px;
  }

  .float-title {
    display: block;
  }
}

@media only screen and (max-width: 760px) {
  h2,
  .h2 {
    font-size: 18px;
  }

  .h3,
  h3 {
    font-size: 14px;
  }

  .h4,
  h4 {
    font-size: 11px;
  }

  .h5,
  h5 {
    font-size: 11px;
    font-weight: normal;
  }
}

p {
  margin-bottom: 40px;
}

article p {
  margin: 0 0 10px;
}

dfn,
cite,
em,
i {
  font-style: italic;
}

blockquote {
  margin: 0 1.5em;
}

address {
  margin: 0 0 1.5em;
}

pre {
  background: #eee;
  font-family: "Courier 10 Pitch", Courier, monospace;
  font-size: 15px;
  font-size: 0.9375rem;
  line-height: 1.6;
  margin-bottom: 1.6em;
  max-width: 100%;
  overflow: auto;
  padding: 1.6em;
}

code,
kbd,
tt,
var {
  font-family: Monaco, Consolas, "Andale Mono", "DejaVu Sans Mono", monospace;
  font-size: 15px;
  font-size: 0.9375rem;
}

abbr,
acronym {
  border-bottom: 1px dotted #969696;
  cursor: help;
}

mark,
ins {
  background: #fff9c0;
  text-decoration: none;
}

big {
  font-size: 125%;
}

label {
  font-weight: normal;
  margin-bottom: 10px;
  letter-spacing: 0.01em;
  line-height: 20px;
}

/*
# Elements
*/

*,
*::before,
*::after {
  box-sizing: border-box;
}

body {
  background: #fff;

  /* Fallback for when there is no custom background color defined. */
}

.no-padding {
  padding: 0 !important;
}

.no-right-padding {
  padding-right: 0 !important;
}

blockquote::before,
blockquote::after,
q::before,
q::after {
  content: "";
}

blockquote,
q {
  quotes: "" "";
}

hr {
  background-color: transparent;
  border-top: 1px dotted #969696;
  height: 1px;
  margin-bottom: 1.5em;
}

ul,
ol {
  margin: 0 0 1.5em;
  padding-left: 18px;
}

ul {
  list-style: disc;
}

ol {
  list-style: decimal;
}

li > ul,
li > ol {
  margin-bottom: 0;
  margin-left: 1.5em;
}

dt {
  font-weight: bold;
}

dd {
  margin: 0 1.5em 1.5em;
}

img {
  height: auto;

  /* Make sure images are scaled correctly. */
  max-width: 100%;

  /* Adhere to container width. */
}

figure {
  margin: 1em 0;

  /* Extra wide images within figure tags don't overflow the content area. */
}

table {
  margin: 0 0 1.5em;
  width: 100%;
}

.close {
  position: relative;
  display: inline-block;
  overflow: hidden;
  
}

.close::before, .close::after {
  content: '';
  position: absolute;
  height: 2px;
  width: 100%;
  top: 50%;
  left: 0;
  margin-top: -1px;
  background: #000;
}

.close::before {
  -webkit-transform: rotate(45deg);
  -moz-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  -o-transform: rotate(45deg);
  transform: rotate(45deg);
}

.close::after {
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  -o-transform: rotate(-45deg);
  transform: rotate(-45deg);
}

/*
# Forms
*/

/* Weird chrome grey box-sixing fix */
.modal form,
.modal-header {
  background: #fff;
}

button,
input[type="button"],
input[type="reset"],
input[type="submit"] {
  border: 1px solid;
  border-color: #ccc #ccc #bbb;
  border-radius: 0;
  background: #e6e6e6;
  color: rgba(0, 0, 0, 0.8);
  font-size: 12px;
  font-size: 0.75rem;
  line-height: 1;
  padding: 0.6em 1em 0.4em;
}

.btn {
  border-radius: 0;
}

button:hover,
input[type="button"]:hover,
input[type="reset"]:hover,
input[type="submit"]:hover {
  border-color: #ccc #bbb #aaa;
}

button:focus,
input[type="button"]:focus,
input[type="reset"]:focus,
input[type="submit"]:focus,
button:active,
input[type="button"]:active,
input[type="reset"]:active,
input[type="submit"]:active {
  border-color: #aaa #bbb #bbb;
}

input[type="text"],
input[type="email"],
input[type="url"],
input[type="password"],
input[type="search"],
input[type="number"],
input[type="tel"],
input[type="range"],
input[type="date"],
input[type="month"],
input[type="week"],
input[type="time"],
input[type="datetime"],
input[type="datetime-local"],
input[type="color"],
textarea,
textarea.form-control {
  color: #242424;
  border: 1px solid #1b4e9b;
  border-radius: 0;
  padding: 0 10px;
  letter-spacing: 0.01em;
  height: 40px;
}

input[type="text"]:hover,
input[type="email"]:hover,
input[type="url"]:hover,
input[type="password"]:hover,
input[type="search"]:hover,
input[type="number"]:hover,
input[type="tel"]:hover,
input[type="range"]:hover,
input[type="date"]:hover,
input[type="month"]:hover,
input[type="week"]:hover,
input[type="time"]:hover,
input[type="datetime"]:hover,
input[type="datetime-local"]:hover,
input[type="color"]:hover,
textarea:hover,
textarea.form-control:hover,
input[type="text"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="password"]:focus,
input[type="search"]:focus,
input[type="number"]:focus,
input[type="tel"]:focus,
input[type="range"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="week"]:focus,
input[type="time"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="color"]:focus,
textarea:focus,
textarea.form-control:focus {
  color: #111;
  border-color: #111;
}

textarea,
textarea.form-control {
  padding: 8px;
}

select,
select.form-control {
  color: #242424;
  border: 1px solid #1b4e9b;
  border-radius: 0;
  padding: 3px 10px;
  height: 40px;
  font-size: 16px;
}

textarea {
  width: 100%;
}

/*
# Navigation
*/
.site-header {
  top: 0;
  transition: top 0.2s linear;
  width: 100%;
  z-index: 100;
}

.site-header.nav-topped {
  position: relative;
}

.site-header.nav-scrolled {
  position: fixed;
}

/*
## Links
*/

a {
  color: #1b4e9b;
}

a:visited {
  color: #8132d9;
}

a:hover,
a:focus,
a:active {
  color: midnightblue;
}

a:focus {
  outline: thin dotted;
}

a:hover,
a:active {
  outline: 0;
}

.subtitle-link {
  font-size: 14px;
  color: #fff;
  text-decoration: underline;
  letter-spacing: 0.01em;
}

.subtitle-link:hover,
.subtitle-link:focus {
  color: #000;
}

@media only screen and (max-width: 767px) {
  label {
    line-height: 28px;
    font-size: 20px;
  }

  .subtitle-link {
    display: block;
    font-size: 20px;
    text-align: center;
    margin-bottom: 30px;
  }

  input[type="text"],
  input[type="email"],
  input[type="url"],
  input[type="password"],
  input[type="search"],
  input[type="number"],
  input[type="tel"],
  input[type="range"],
  input[type="date"],
  input[type="month"],
  input[type="week"],
  input[type="time"],
  input[type="datetime"],
  input[type="datetime-local"],
  input[type="color"],
  textarea,
  textarea.form-control {
    height: 50px;
    border-width: 2px;
  }

  .form-control {
    font-size: 20px;
  }

  .form-group {
    margin-bottom: 20px;
  }
  sup{
    font-size: 120%;
    top: 0;
  }
}

@media only screen and (max-width: 760px) {
  .subtitle-link {
    font-size: 10px;
    padding-top: 5px;
    margin-top: -5px;
    padding-bottom: 5px;
    margin-bottom: 15px;
  }

  label {
    font-size: 10px;
    line-height: 14px;
    margin-bottom: 3px;
  }

  input[type="text"],
  input[type="email"],
  input[type="url"],
  input[type="password"],
  input[type="search"],
  input[type="number"],
  input[type="tel"],
  input[type="range"],
  input[type="date"],
  input[type="month"],
  input[type="week"],
  input[type="time"],
  input[type="datetime"],
  input[type="datetime-local"],
  input[type="color"],
  textarea,
  textarea.form-control {
    height: 25px;
    border-width: 1px;
  }

  textarea,
  textarea.form-control {
    height: 40px;
  }

  .form-control {
    font-size: 10px;
  }

  .form-group {
    margin-bottom: 15px;
  }
}

/*
## Menus
*/

/* Main menu - Left */
#site-navigation.navbar-default .navbar-brand {
  height: 69px;
}

#site-navigation.navbar-default .navbar-brand > img {
  height: 42px;
  width: auto;
}

#site-navigation.navbar-default {
  font-size: 18px;
  background: #fff;
  border-radius: 0;
  border: 0;
  border-bottom: 8px solid #1b4e9b;
  height: auto;
  padding-top: 0;
  padding-bottom: 0;
  margin: 0;
}

#site-navigation.navbar-default .navbar-nav > li {
  min-width: 120px;
  height: 69px;
  background: #fff;
  border-left: 1px solid #ededed;
  text-align: center;
}

#site-navigation.navbar-default .navbar-nav > li > a {
  padding: 0 10px;
  color: rgba(36, 36, 36, 1);
  line-height: 69px;
  font-weight: bold;
}

#site-navigation.navbar-default .navbar-nav > li:last-of-type > a {
  border-right: 1px solid #ededed;
}

#site-navigation.navbar-default .navbar-nav > li > a:hover,
#site-navigation.navbar-default .navbar-nav > li > a:focus {
  background: linear-gradient(to bottom, #fff 0%, #fff 89%, #1b4e9b 89%, #1b4e9b 100%);
}

#site-navigation.navbar-default .navbar-nav > li > a.active-navbar-link {
  background: linear-gradient(to bottom, #fff 0%, #fff 89%, #1b4e9b 89%, #1b4e9b 100%);
}

#site-navigation.navbar-default .navbar-nav > .active > a,
#site-navigation.navbar-default .navbar-nav > .active > a:hover,
#site-navigation.navbar-default .navbar-nav > .active > a:focus {
  color: #fff;
  background: #1b4e9b;
}

#site-navigation.navbar-default .navbar-left {
  letter-spacing: 0.005em;
}

/* Mobile menu buttons */
#site-navigation.navbar-default .navbar-toggle {
  height: 72px;
  width: 72px;
  margin: 0;
  padding: 0 20px;
  border: 0;
  border-radius: 0;
  border-left: 1px solid #ededed;
}

#site-navigation.navbar-default .navbar-toggle:hover,
#site-navigation.navbar-default .navbar-toggle:focus {
  background-color: #1b4e9b;
  color: #fff;
}

#site-navigation.navbar-default .navbar-toggle .icon-bar {
  background-color: #818181;
  width: 30px;
  margin-bottom: 12px;
}

#site-navigation.navbar-default .navbar-toggle:hover .icon-bar,
#site-navigation.navbar-default .navbar-toggle:focus .icon-bar {
  background-color: #fff;
}

#site-navigation.navbar-default .navbar-toggle .icon-bar:last-of-type {
  margin-bottom: 0;
}

#site-navigation.navbar-default .search-toggle {
  color: #818181;
  font-size: 30px;
}

/* Main Menu - Right */
#site-navigation.navbar-default .navbar-right {
  letter-spacing: 0.02em;
}

#site-navigation.navbar-default .navbar-right > li {
  min-width: auto;
  max-width: 440px;
  height: 69px;
  border: 0;
}

#site-navigation.navbar-default .navbar-right > li > a {
  line-height: 70px;
  color: #818181;
  font-weight: normal;
  font-size: 14px;
}

#site-navigation.navbar-default .navbar-right > li > a .glyphicon {
  font-size: 20px;
  vertical-align: sub;
}

#site-navigation.navbar-default .navbar-right > li:last-of-type > a {
  border: 0;
}

/* Main menu - Search */

#site-navigation.navbar-default .navbar-form {
  padding: 9px;
}

#site-navigation.navbar-default .navbar-form .input-group .input-group-btn > .btn {
  border-color: #1b4e9b;
  background-color: #1b4e9b;
  color: #fff;
}

#site-navigation.navbar-default .navbar-form .has-feedback #s {
  min-width: 300px;
}

::-ms-clear {
  display: none;
}

#site-navigation.navbar-default .navbar-form .has-feedback .form-control-clear {
  z-index: 10;
  pointer-events: auto;
  cursor: pointer;
  color: #818181;
}

/* Login dropdown */
#site-navigation.navbar-default .login-dropdown {
  display: none;
  width: 260px;
  font-size: 14px;
  background: #fff;
  position: absolute;
  right: 0;
  top: 70px;
  z-index: 2;
  border: 1px solid #1b4e9b;
}

#site-navigation.navbar-default .login-dropdown .closeLogin img {
  width: 20px;
  height: 20px;
  max-width: none;
}

#site-navigation.navbar-default #search-collapse {
  height: 69px;
}

/*
## Scrolled menus
*/

.nav-scrolled #site-navigation.navbar-default {
  font-size: 17px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-brand {
  height: 49px;
  padding-top: 8px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-brand > img {
  height: 36px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-left > li {
  height: 49px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-left > li > a {
  line-height: 49px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-right > li {
  height: 49px;
}

.nav-scrolled #site-navigation.navbar-default .navbar-right > li > a {
  line-height: 50px;
}

.nav-scrolled #site-navigation.navbar-default .login-dropdown {
  top: 50px;
}

/* Top menu responsive */
@media only screen and (max-width: 1199px) {
  #site-navigation.navbar-default .navbar-nav.nav-justified {
    width: 100%;
    border-top: 1px solid #ededed;
  }

  #site-navigation.navbar-default .navbar-left > li {
    float: none;
    min-width: auto;
    height: 48px;
  }

  #site-navigation.navbar-default .navbar-left > li > a {
    line-height: 48px;
  }
}

@media only screen and (min-width: 1200px) {
  #site-navigation.navbar-default .navbar-nav.nav-justified {
    width: auto;
  }

  #site-navigation.navbar-default .navbar-left > li {
    float: left;
    min-width: 120px;
    height: 69px;
  }

  #site-navigation.navbar-default .navbar-left > li > a {
    line-height: 69px;
  }
}

@media only screen and (max-width: 767px) {
  #site-navigation.navbar-default {
    font-size: 16px;
  }

  #site-navigation.navbar-default .navbar-right > li > a {
    font-size: 12px;
  }

  .navbar-header {
    margin-left: -15px !important;
    border-bottom: 3px solid #1b4e9b;
  }

  #site-navigation.navbar-default .navbar-brand {
    height: 72px;
    padding-top: 13px;
  }

  #site-navigation.navbar-default .navbar-brand > img {
    height: 50px;
  }

  .nav-scrolled #site-navigation.navbar-default .navbar-brand {
    height: 72px;
    padding-top: 13px;
  }

  .nav-scrolled #site-navigation.navbar-default .navbar-brand > img {
    height: 50px;
  }

  #site-navigation.navbar-default .login-dropdown {
    font-size: 12px;
    width: 210px;
  }

  #site-navigation.navbar-default .login-dropdown .closeLogin {
    font-size: 16px;
  }

  #site-navigation.navbar-default .navbar-nav > li {
    border: 0;
    border-top: 1px solid #ededed;
  }

  #site-navigation.navbar-default .navbar-nav > li:last-of-type > a {
    border: 0;
  }

  #site-navigation.navbar-default .search-toggle .search-icon::before {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    content: "\f002";
  }
}

@media only screen and (max-width: 760px) {
  .navbar {
    min-height: auto;
  }

  #site-navigation.navbar-default .navbar-toggle {
    width: 36px;
    height: 36px;
    padding: 0 5px;
  }

