/* Select style with custom caret */
.rail-select {
  position: relative;
}

.rail-select select {
  appearance: none;
  -moz-appearance: none;
  -webkit-appearance: none;
}

.rail-select select:focus,
.rail-select select:hover {
  outline: none;
  box-shadow: none;
  border: solid 1px #1b4e9b;
}

.rail-select .select-side {
  width: 40px;
  position: absolute;
  top: 0;
  background-color: #fff;
  height: 100%;
  right: 0;
  border-radius: 0;
  border-right: solid 1px #1b4e9b;
  border-top: solid 1px #1b4e9b;
  border-bottom: solid 1px #1b4e9b;
  pointer-events: none;
}

.rail-select .select-side::before {
  border-left: solid 1px #1b4e9b;
  content: "";
  position: absolute;
  left: 0;
  bottom: 0;
  height: 100%;
  width: 1px;
}

.rail-select .select-side i {
  color: #1b4e9b;
  top: 12px;
  left: 14px;
  position: absolute;
}

.rail-select:hover select,
.rail-select:focus select {
  border: solid 1px #1b4e9b;
  cursor: pointer;
}

