css:
@-moz-document domain("netflix.com") {
.PlayerControls--button-control-row {
    background: #33333366 ;
    height: 4.2em ;
    width: 90% ;
    margin: 0 ;
    margin-left: 5%;
    border-radius:4px;
    
}

.PlayerControls--progress-control-row-hidden{transform:none!important;}

.PlayerControls--progress-control-row-standard{
    height: 0.1em;
    width:90%;
    margin: 10px ;
    margin-left: 5%;
    padding:0;
    
}
.time-remaining--classic .time-remaining__time {
    font-size: 2em;
    width: auto;
    font-weight: 200;
    padding: 10px; 
    position: fixed;
    top: 7px;
    right: 220px;  
    z-index:999;
}
nfp,.PlayerControls--button-control-row .video-title.text-control{border:0!important}
.nfp .legacy-controls-styles .scrubber-container .track { 
    height: 5px;
    border-radius: 3px; 
    margin-top: 2px; 
    -webkit-box-shadow: none; 
    -moz-box-shadow: none; 
    box-shadow: none; 
    background: rgba(255, 255, 255, .4);
}

.nfp .legacy-controls-styles .scrubber-container .current-progress { background: #f12b24; }
.nfp .legacy-controls-styles .scrubber-container .buffered { background: rgba(255, 255, 255, .5); }
.nfp .legacy-controls-styles .scrubber-container .track{border-radius:3px;}
.nfp .legacy-controls-styles .scrubber-container .scrubber-head {  display: none; }

.ReportAProblem--button-icon,.nfp .button-reportAProblem{
    display:none;
    border:0;
}
.ReportAProblemPopupContainer+.nfp-popup-control .nfp-button-control{
    border:0;
}
.PlayerControls--button-control-row .button-volumeLow, .PlayerControls--button-control-row .button-volumeMax, .PlayerControls--button-control-row .button-volumeMedium, .PlayerControls--button-control-row .button-volumeMuted{
border:0;
}
.PlayerControls--button-control-row .button-nfplayerPause, .PlayerControls--button-control-row .button-nfplayerPlay{
border:0;
}
div {
    border: 0;
}
}
