css:
@-moz-document  domain('habr.com') {

  .icon-svg_logo-habrahabr {
      color: #93a1a1;
  }

  .layout, .company-info__author, .post-share, .default-block {
      background: #002b36;
      color: #839496;
  }

  .layout__row_navbar {
      background: #002b36;
      border-color: #073642;
  }

  .user-info__links > a, .user-info__specialization {
      color: #93a1a1;
  }

  #TMpanel {
      background: #073642;
      border-bottom: none;
  }

  .bmenu > a {
      color: #268bd2 !important;
  }

  .bmenu > .current {
      color: #93a1a1 !important;
  }

  .nav-links__item-link {
      color: #93a1a1;
  }

  .nav-links__item-link_current {
      color: #b58900;
  }

  .post__title_link {
      color: #2aa198 !important;
  }

  .post__title_link:visited, .post__title-text, .comments-section__head-title, .default-block__header-title, .post-share__title, .defination-list__label, .checkbox__label, .comment-form__title-text {
      color: #839496 !important;
  }

  .comment__folding-dotholder:before {
      background: url(https://habrastorage.org/webt/yd/ku/cq/ydkucqe0i1sgx_ssbvag9semzco.png) repeat-x 5px 0 !important;
  }

  .post__text, .page-header__info-desc, .comment__message {
      color: #839496;
  }

  .post__text > a, .page-header__info-title {
      color: #268bd2;
  }

  .tabs-menu__item-text {
      color: #839496;
  }

  .tabs-menu__item-text_active {
      color: #b58900 !important;
      border-color: #b58900;
  }

  .btn_inner {
      color: #839496;
  }

  .toggle-menu {
      border: 1px solid #839496;
  }

  .toggle-menu__item-link, .promo-block__header {
      color: #839496;
      background: #073642;

  }

  .toggle-menu__item-link_active {
      border-color: #2aa198;
      color: #2aa198;
  }

  .post__time, .comment__date-time, .post-info__title:visited {
      color: #586e75;
  }

  .user-info__nickname {
      color: #268bd2;
  }

  .hub-link, .content-list > li > a, .promo-block__header > a, .promo-item__title, .promo-item__amount > span, .promo-item__amount::after {
      color: #839496;
  }

  .voting-wjt__counter_positive, .post-stats__comments-count_new, .hub-link_subscribed {
      color: #859900;
  }

  .voting-wjt__counter_negative {
      color: #dc322f;
  }

  .post__habracut-btn {
      border: 1px solid #268bd2;
      color: #268bd2;
  }

  .post-stats, .company-info {
      border: 1px solid #839496;
  }

  .btn_subscribe {
      border: 1px solid #859900;
      color: #859900;
  }

  .content-list__item_devided, .default-block__header, .comments-section__head, .company-info__about {
      border-bottom: 1px solid #586e75;
  }

  .company-info__contacts {
      border-top: 1px solid #586e75;
  }

  .post__translatation {
      background: #268bd2;
  }

  .post__translatation-link {
      color: #ffffff !important;
  }

  .comment__head_topic-author {
      background: #073642;
  }
	
  .profile-section__user-hub {
      background: #073642;
      color: #93a1a1;
  }

  .profile-section__title, .footer-block__title {
      color: #839496;
  }

  .layout__row_footer-links {
      background: #073642;
  }

  .badge_count_navbar {
      background: #dc322f;
  }
}
