css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(golem.de) {
/***** Erstmal die ganze Seite schwärzen *****/
body{background-color:#222;}                /***** den kompletten Seitenhintergrund *****/
#grandwrapper{border-color:#333;}           /***** den Rahmen um die Artikel minimal heller *****/
#screen{background-color:#333;}             /***** den Artikelhintergrund so wie den Rahmen *****/
#screen img{ border:1px solid #777; }       /***** einen schmalen, hellen Rahmen um die Bilder *****/
.golem-gallery2-footer-title{color:#ddd;}   /***** die Navigation in Galerien anpassen *****/

/***** Die Artikelansicht anpassen *****/
h1, h2, h3, h4, h5, h6, .head1, .head2, .head3, .head4, .head5, .head6{ color:#eee !important; }
.dh1 a, ol#list-jtoc.list-pages, p, .list-breadcrumbs, .list-pages{color:#eee;}
p a, .list-breadcrumbs a, .list-pages a{color:#c5c5f5 !important;}
.icon-comments, .icon-video, #atoc_prev, #atoc_next, #list-jtoc a { color:#c5c5f5; }
.toc .toc__list{ background: #222; color:#eee; }
.toc .toc__list a{color: #c5c5f5; }

/***** Die Kommentarübersicht auf Artikelseiten anpassen *****/
#comments{background-color:#111 !important;}
#comments a{ color:#c5c5f5;}
.dh3 {color:#eee;}
.dh3 a{color:#c5c5f5 !important;}

/***** Kommentarseiten *****/
#thread-list{background-color:#222;}
#thread-list ol li h3 a {color:#eee !important;}
#thread-list ol li h3 span.count {color:#aaa !important;}
#thread-list ol li p span.user {color:#aaa !important;}
.threads{background-color:#222 !important;}
ol li h3 a strong{color:#ddd !important;}
#post{background-color:#444 !important;}
label, #subject-label{color:#eee !important;}
.inputText {
    background: #444;
    border: solid 1px #bbb;
    padding: 5px 5px 9px 5px;
    box-shadow:none;
}
#subject{color:#eee !important;}
.inputTextarea{background-color:#444 !important; box-shadow:none;}
textarea.body{background-color:#444 !important; color:#eee !important; width:100%!important; height:auto; }
#username, #password, #lostpass{ color:#eee !important; }

.ulist-article{ color:#ddd; }

table.golem_table{ margin-bottom:0; }
table.golem_table th{ background-color:#555; }
table.golem_table td{ color:#ddd; }
.golem_tabletitle{ background-color:#444; color:#ddd; padding:.25em; }

.text1{ color:#ddd; }
.text1 a{ color:#c5c5f5; }

#search-form .inputText.g2{ border-color:#dda; }
#search-form input{ color:#eee; }
#search-form td{ color:#ddd; }
#search-form select{ background-color:#222; color:#ddd; }

#thread-detail h4 a{ color:#c5c5f5; }
#thread-detail h3 strong{ color:#c5c5f5 !important; }

/***** Baumansicht Forumsübersicht *****/
.table-listthread{background-color:#444;}
td.alt{ border-color: #888 !important; }
td.meta, td.meta{ color: #bba !important; }
.table-listthread td h3 a{ color:#eee; }

.indent img{border:0 !important;}


/****** Forum Profilseiten *****/
.forum #cc_index{ background-color:#454545; }
.forum #profile{ color:#f9f9f9; }
.forum a{ color:#6691f6; }

.list-messages h3 a{ color:#28f; }

.formatted a {
	color: #9190f7;
   line-height: 120%;
	margin-bottom: 1em;
}



/****** Ticker *****/
ol.list-tickers li h3 a{ color:#c5c5f5!important; }
ol.list-tickers li h3 a span{ color:#ccc!important; }
ol.list-tickers li p { color:#aaa!important; }

}
