css:
@-moz-document domain("movs4u.tv") {
#dt_contenedor{
	float: right;
}
header.main{
    float: right;
}
header.main .hbox .logo{
    float: right;
}
.head-main-nav{
    float: right;
}
.headitems{
    float: left;
    margin-left: inherit;
}
.module .content header{
    float: right;
}
.module .content header h1, .module .content header h2{
    float: right;
}
.module .content header span{
    float: left;
}
.module .content{
    float: right;
    margin-left: 340px;
    margin-right: inherit;
    border-left: rgba(255,255,255,.08) solid 1px; 
    border-right: 0;
}
.module .sidebar{
    float: left;
    right: inherit;
    left: 0;
}
.module .content .items .item{
    float: right;
}
.head-main-nav ul.main-header li.menu-item-has-children>a:after{
    float: left;
    margin-right: 5px;
    margin-left: inherit;
}
.head-main-nav ul.main-header li{
    float: right;
}
body{
    font-family: 'Droid Arabic Kufi', sans-serif;
}
#single, .box_links, .links_table, .links_table table{
    float: right;
}
.dtsingle .content{
    float: right;
}
.dtsingle .sidebar{
    right: initial;
    left: 0;
}
.player_sist{
    float: right;
}
.player_sist .playex{
    float: right;
}
.player_sist .control{
    float: right;
}
.player_sist .control nav.player{
    float: right;
}
.player_sist .control span.qualityx{
    float: right;
}
.player_sist .control nav.controles{
    float: left;
}
.player_sist .control span.qualityx{
    border-right: solid 1px #000;
    border-left: 0;
}
.player_sist .control nav.player ul.options li a b{
    font-size: 8px;
}
.single_tabs{
    float: right;
}
ul.smenu{
    float: right;
}
ul.smenu li{
    float: right;
}
#single .content .sbox{
    float: right;
}
.sbox h2, .sbox h1{
    float: right;
}
.wp-content{
    float: right;
}
footer.main .fbox .copy{
    float: right;
}
span.top-page{
    float: left;
}
footer.main .fbox .fmenu{
    float: left;
}
}
