css:
@charset "UTF-8";
/*!
Theme Name: Susam Creative Purple
Theme URI: http://susamcreative.com/susam-creative-trello-theme
Author: Mustafa Türksavaş
Description: A creative purple theme for Trello
Version: 1.0
*/
@-moz-document domain("trello.com") {
  /* ----------------------- */
  /* ------- GENERAL ------- */
  /* ----------------------- */
  ::-moz-selection {
    color: #fff !important;
    background: #B240DF !important; }
  ::selection {
    color: #fff !important;
    background: #B240DF !important; }
  ::-moz-selection {
    color: #fff !important;
    background: #B240DF !important; }
  a:hover {
    color: #4d4d4d; }
  .board-menu,
  .pop-over.mod-notifications,
  .pop-over.mod-search-over {
    background-color: #fff; }
  /*[[board-background]]*/
  /* Scrollbar */
  .u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical {
    background-color: #fff; }
  .u-fancy-scrollbar::-webkit-scrollbar,
  .u-fancy-scrollbar::-webkit-scrollbar-track-piece,
  .u-fancy-scrollbar::-webkit-scrollbar-thumb,
  .attachment-viewer-frame-preview-wrapper::-webkit-scrollbar,
  .attachment-viewer-frame-preview-wrapper::-webkit-scrollbar-track-piece,
  .attachment-viewer-frame-preview-wrapper::-webkit-scrollbar-thumb {
    border-radius: 0 !important;
    background-color: transparent;
    width: 5px; }
  /* Menu */
  .board-menu-navigation-item-link-count,
  .board-menu-section-header-count {
    background-color: #B240DF;
    color: #fff; }
  /* Activity Window */
  .window-main-col.window-main-col-full {
    background-color: #fff; }
  /* Border Radius 0 for All Elements */
  .board-header-btn,
  input[type=text].header-search-input,
  #search-text,
  .header-btn,
  .member,
  .header-btn.header-member .member,
  .member img,
  .card-detail-item-add-button,
  .list,
  .list-card,
  .pop-over,
  button,
  input:not([type=file]),
  textarea,
  .button,
  .button-link,
  .window,
  .description-content-fade-button-text,
  pre,
  .card-label,
  .card-detail-item-labels .card-label,
  .badge.is-due-past,
  .badge.is-complete,
  .card-detail-badge,
  .checklist-progress-bar,
  .checklist-progress-bar-current,
  .checklist-item-checkbox,
  .checklist-item,
  .comment-frame,
  .action-comment,
  input.js-search-boards,
  .compact-board-tile-link-thumbnail,
  .pop-over-member-list li a,
  .board-menu-section-header,
  .board-menu-navigation-item-link,
  .quiet-button,
  .comment-box-options-item,
  .open-card-composer,
  .list-wrapper.mod-add,
  .attachment-viewer-frame-preview-image,
  .attachment-thumbnail,
  .attachment-thumbnail-details,
  .attachment-thumbnail-preview,
  .attachment-image-preview,
  .list-card.is-covered .list-card-cover,
  .quick-card-editor-buttons-item,
  .add-controls .option,
  .edit-controls .option,
  .show-more,
  .helper,
  .pika-single .pika-label,
  .pika-single .pika-next,
  .pika-single .pika-prev,
  .member-large .av-btn,
  .compact-board-tile-link,
  .compact-board-tile-background,
  .compact-board-tile-fade,
  .boards-drawer,
  .badge.is-due-soon,
  .badge.is-due-now,
  .badge.is-due-complete,
  .member-large,
  .member-large .member-avatar,
  .saved-search-delete,
  .saved-search-item,
  .checklist-item.placeholder,
  .pop-over-list.inset li > a,
  .known-service-link,
  .list-wrapper.placeholder,
  .tip,
  .tip-image,
  .tip-image-img,
  .next-tip-link,
  .meta-link-list-item-link,
  .segmented-control-item,
  .icon-lg.dark-background-hover:hover,
  textarea.mod-list-name,
  .list-header-extras-menu:hover,
  .checklist-add-controls-option,
  .empty,
  .empty-list,
  .card-label.mod-selectable,
  .calendar-day,
  .calendar-wrapper,
  .button-link .on,
  .label-list-item-link,
  .board-menu-navigation-item.mod-background .board-menu-navigation-item-link-icon,
  .avatar-option,
  .avatar-option .pic,
  .board-menu-navigation-item-link-count,
  .board-menu-section-header-count,
  .board-background-select .background-box,
  .pop-over-emoji-list li a,
  .app-alert-item,
  .keyboard-key,
  .app-alert-item-button,
  .power-ups-explainer,
  .badge.is-unread-notification,
  .card-detail-fake-text-area,
  .checklist-item-details .checklist-item-menu-wrapper .checklist-item-menu {
    border-radius: 0; }
  /* ----------------------- */
  /* -------- HEADER ------- */
  /* ----------------------- */
  #header,
  .board-header {
    background: #B240DF !important; }
  .board-header {
    margin-bottom: 10px !important; }
  .header-woof {
    display: none !important; }
  .saved-search-delete:hover,
  .saved-search-item:hover {
    background: #B240DF;
    color: #fff; }
  .board-header-btn-text,
  .board-header-btn:not(.board-header-btn-filter-indicator):not(.board-header-btn-enabled) .board-header-btn-icon,
  .body-light-board-background .board-header-btn:not(.board-header-btn-filter-indicator):not(.board-header-btn-enabled) .icon-sm {
    color: #fff; }
  /* ----------------------- */
  /* -------- LISTS -------- */
  /* ----------------------- */
  .list {
    background: none; }
  .list-card {
    border: 1px solid #fff !important;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3); }
  .active-card {
    background: #fff !important;
    border: 1px solid #B240DF !important;
    -webkit-transition: border 0.3s ease-out !important;
    transition: border 0.3s ease-out !important; }
  .list-card.ui-sortable-helper {
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.8) !important; }
  .list-card.ui-sortable-helper {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg); }
  .list-header textarea {
    color: #fff; }
  .list-header .icon-lg {
    color: #fff; }
  .icon-sm.icon-overflow-menu-horizontal {
    color: #fff; }
  textarea.mod-list-name.is-editing,
  textarea.mod-list-name:focus {
    background-color: rgba(255, 255, 255, 0.25); }
  /* List Name Colors */
  div#board div.js-list.list-wrapper:nth-child(5n+1) .list-header {
    background: #F9A200 !important; }
  div#board div.js-list.list-wrapper:nth-child(5n+2) .list-header {
    background: #D23E4F !important; }
  div#board div.js-list.list-wrapper:nth-child(5n+3) .list-header {
    background: #8E7EEB !important; }
  div#board div.js-list.list-wrapper:nth-child(5n+4) .list-header {
    background: #75923C !important; }
  div#board div.js-list.list-wrapper:nth-child(5n+5) .list-header {
    background: #99AAB4 !important; }
  .list-header {
    margin-bottom: 10px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3); }
  .list-header-name,
  .list-header-menu-icon {
    color: #fff !important; }
  textarea.mod-list-name:disabled {
    -webkit-text-fill-color: #fff; }
  textarea.mod-card-back-title:focus, textarea.mod-list-name:focus {
    border: 1px solid #B240DF;
    box-shadow: 0 0 2px 0 #BF5AE7; }
  /* display total cards count */
  .list-header.js-list-header textarea {
    display: inline-block;
    width: 60%;
    vertical-align: bottom; }
  body .list-header-num-cards {
    display: inline-block;
    font-size: 13px;
    color: #fff;
    width: 30%;
    text-align: right; }
  /* display label names */
  body .list-card-labels .card-label {
    width: auto;
    height: 16px;
    line-height: 16px;
    padding: 0 3px;
    margin: 0 0 2px 2px;
    font-size: 10px;
    overflow: visible;
    text-shadow: none !important; }
  /* full-width labels */
  .card-label.mod-card-front {
    max-width: -moz-fit-content;
    max-width: fit-content;
    min-width: -moz-fit-content;
    min-width: fit-content; }
  .card-label.mod-card-detail {
    min-width: -moz-fit-content;
    min-width: fit-content; }
  /* ----------------------- */
  /* -------- CARDS -------- */
  /* ----------------------- */
  .open-card-composer,
  .list-cards .icon-lg, .icon-sm.icon-add {
    color: #fff; }
  .open-card-composer:hover {
    background: rgba(0, 0, 0, 0.12);
    cursor: pointer;
    color: #fff;
    text-decoration: none; }
  .icon-sm,
  .card-detail-item-add-button {
    background-color: transparent !important; }
  .list-cards .placeholder {
    background: none !important;
    border: none !important;
    box-shadow: none !important; }
  .list-wrapper.mod-add {
    background-color: #fff; }
  /* Window Overlay */
  .window-overlay {
    background: rgba(255, 255, 255, 0.8) !important; }
  .window-wrapper {
    background: #fff; }
  .window {
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3);
    background: #B240DF; }
  .known-service-link {
    background: #B240DF;
    color: #fff; }
  .known-service-link:hover {
    background: #BF5AE7;
    color: #fff; }
  .checklist-new-item.focus .checklist-new-item-text,
  .checklist-new-item.focus .checklist-new-item-text:focus,
  .checklist-new-item.focus .checklist-new-item-text:hover,
  input[type=text]:focus,
  input[type=password]:focus,
  input[type=email]:focus,
  textarea:focus {
    border-color: #B240DF;
    box-shadow: none; }
  .checklist-item-details .checklist-item-menu-wrapper .checklist-item-menu:hover {
    background-color: none; }
  .checklist-item:hover > .checklist-item-details > .edit > .edit-controls > a.icon-lg,
  .checklist-item:hover > .checklist-item-details > .edit > .edit-controls > a.option > .icon-sm {
    color: #fff; }
  .checklist-item:hover > .checklist-item-details > .edit > .edit-controls > a.icon-lg:hover,
  .checklist-item:hover > .checklist-item-details > .edit > .edit-controls > a.option > .icon-sm:hover {
    color: #4d4d4d; }
  .editing .edit .field,
  .editing .edit .field:active {
    border-color: #B240DF; }
  .description-content-fade-button {
    background: linear-gradient(to bottom, rgba(39, 78, 116, 0), rgba(39, 78, 116, 0) 370px, #fff 450px); }
  .description-content-fade-button:hover {
    background: linear-gradient(to bottom, rgba(83, 163, 243, 0), rgba(83, 163, 243, 0) 370px, #fff 450px); }
  .description-content-fade-button-text {
    background: #B240DF;
    color: #fff !important;
    text-decoration: none; }
  .description-content-fade-button-text:hover {
    background: #BF5AE7;
    color: #fff !important;
    text-decoration: none; }
  .comment-box-options-item:hover {
    background: none; }
  code,
  pre,
  .helper {
    background: #B240DF; }
  code,
  pre code {
    border: 1px solid #B240DF;
    color: #fff; }
  .helper:hover {
    background: #BF5AE7; }
  a.helper,
  a.helper:hover,
  .attachment-thumbnail:hover .attachment-thumbnail-details-title,
  .attachment-thumbnail:hover span.u-block,
  .attachment-thumbnail:hover .attachment-thumbnail-details,
  .js-open-editing-menu:hover .icon-lg,
  .js-open-editing-menu:hover .icon-sm,
  .button-link:hover .icon-sm,
  .icon-lg.js-open-editing-menu:hover,
  .icon-sm.js-open-editing-menu:hover,
  .quiet-button:hover,
  .quiet-button:hover .icon-sm,
  .add-controls .option:hover,
  .edit-controls .option:hover,
  .uploader:hover .fakefile,
  .pika-single .pika-label:hover,
  .checklist-item:hover,
  .checklist-item:hover .checklist-item-details-text a,
  .checklist-add-controls-option:hover,
  .label-list-item.is-active.selected .label-list-item-link,
  .label-list-item.selected .label-list-item-link,
  .pop-over-list.navigable li.selected > a {
    color: #fff !important; }
  .attachment-thumbnail:hover a.attachment-thumbnail-details-options-item,
  .attachment-thumbnail:hover span.attachment-thumbnail-details-options-item-text,
  .attachment-thumbnail:hover span.icon-sm {
    color: #e2e2e2 !important; }
  .attachment-thumbnail:hover a.attachment-thumbnail-details-options-item:hover,
  .attachment-thumbnail:hover a.attachment-thumbnail-details-options-item:hover span.icon-sm,
  .attachment-thumbnail:hover span.attachment-thumbnail-details-options-item-text:hover,
  .attachment-thumbnail:hover span.icon-sm:hover {
    color: #fff !important; }
  .quiet-button:hover,
  .quiet-button:hover .icon-sm,
  .add-controls .option:hover,
  .edit-controls .option:hover,
  .uploader:hover .fakefile,
  .pika-single .pika-label:hover,
  .attachment-thumbnail:hover .attachment-thumbnail-details,
  .checklist-item:hover,
  .checklist-add-controls-option:hover,
  .label-list-item.is-active.selected .label-list-item-link,
  .label-list-item.selected .label-list-item-link,
  .pop-over-list.navigable li.selected > a,
  li.item.selected .js-select-emoji,
  .pop-over-emoji-list:not(.collapsed) li.selected a,
  .pop-over-member-list:not(.collapsed) li.selected a {
    background: #B240DF; }
  a.attachment-thumbnail-details-options-item:hover,
  a.attachment-thumbnail-details-options-item:hover span.icon-sm,
  span.attachment-thumbnail-details-options-item-text:hover {
    color: #999 !important; }
  .comment-box-options .icon-sm:hover,
  .window-wrapper .icon-lg:hover,
  .icon-lg.dark-background-hover:hover,
  .list-header-extras-menu:hover {
    color: #4d4d4d; }
  .pika-single .is-today .pika-button {
    background-color: #fff !important;
    color: #B240DF !important; }
  /* ----------------------- */
  /* ------- BUTTONS ------- */
  /* ----------------------- */
  .button,
  button,
  input[type=button],
  input[type=submit],
  .button:hover,
  button:hover,
  input[type=button]:hover,
  input[type=submit]:hover {
    box-shadow: none !important; }
  .button,
  button,
  input[type=button],
  input[type=submit],
  .button-link:hover,
  .pop-over-member-list li.selected a,
  .pop-over-list li > a:hover,
  .pop-over-list li.uploader > a:hover,
  .board-menu-navigation-item-link:hover,
  .board-menu-navigation-item-link:hover .icon-sm,
  .board-menu-section-header:hover,
  .board-menu-section-header:hover .icon-lg,
  .js-fill-tip:hover .tip-text,
  .js-new-tip:hover,
  .meta-link-list-item:hover,
  .meta-link-list-item-link:hover,
  .meta-link-list-item:hover .meta-link-list-item-link,
  .show-more:hover,
  .segmented-control-item.is-active,
  .avatar-option:hover {
    background: #B240DF !important;
    color: #fff !important; }
  .uploader:hover .fakefile:not(.button-link) {
    background: #B240DF !important; }
  .card-detail-badge {
    background: #B240DF;
    color: #fff; }
  .button:hover,
  button:hover,
  input[type=button]:hover,
  input[type=submit]:hover,
  .segmented-control-item:hover,
  .card-detail-badge:hover {
    background: #BF5AE7 !important; }
  .header-btn,
  .button-link,
  .icon-lg.dark-background-hover:hover,
  .list-header-extras-menu:hover {
    background: none; }
  .button-link {
    border: none;
    box-shadow: none; }
  .button-link:hover,
  .pop-over-member-list li.selected a,
  .pop-over-list li > a:hover,
  .board-menu-navigation-item-link:hover,
  .board-menu-navigation-item-link:hover .icon-sm,
  .board-menu-section-header:hover,
  .board-menu-section-header:hover .icon-lg {
    border: none;
    box-shadow: none; }
  .header-btn:hover {
    background: rgba(0, 0, 0, 0.12); } }
