css:
@-moz-document regexp("http(s)?://((.*enc\\.)|(www\\.))?gamersyde\\.com.*") {
body,
blockquote,
.span-button,
.buttongsy,
.forumline,
.forumline tr th,
.quotearea,
.video-download,
.tabs .title,
.account,
.game-sheet,
.news-panel .news-body .highlighted-features,
.news-panel .news-comments .comment-highlighted-features,
.game-teasers .game-teaser .game-teaser-bottomline {
    background-color: #212121;
    color: #ccc;
}

body[style*="background:#fff;"] {
    background: #212121 !important;
}

.search .search-box-wrapper,
.news-panel .news-panel-title h1,
.news-panel .news-panel-title h2,
.news-panel .news-panel-title h3,
.news-panel .news-panel-title h4,
.main-wrapper .main-layout {
    background-color: #373737;
}


a,
a:hover,
a.bluebold:visited,
a.bluebold:link,
.shoutbox-messages a,
.news-meta a,
.bluebold,
.news-panel .news-panel-title,
.video-download ul h1,
span.blue,
.game-sheet h1,
.comment-text {
    color: #cc7700;
}

.search .search-box {
    background-color: transparent;
    color: #bbb;
}

.search .activate-button {
    opacity: 0.3;
}

.news-videos .video-content .video-content-download,
.news-videos .video-content .video-content-firstline,
.login-box .login-box-username-normal,
#maxicomments a.author,
#maxicomments p.msgfooter a,
.video-download ul h2,
.news-panel .news-comments .comment-author a,
.news-panel .news-comments .comment .comment-text  {
    color: #ccc;
}

.game-teasers .game-teaser .game-teaser-bottomline a,
.news-videos .video-content .video-content-bottom .video-content-view a,
.news-videos .video-content .video-content-download a {
    color: #eee;
}


.main-menu .main-menu-item .main-menu-item-link {
    background-blend-mode: exclusion;
    background-color: #d4d4d4;
}

.main-menu .main-menu-item.forum .main-menu-item-link {
    width: 59px;
}

.arrow {
    border-left-color: #343434;
    border-right-color: #343434;
    border-top-color: #212121;
}

.comment-count-bubble {
    color: #212121;
    background-color: #373737;
    background-blend-mode: exclusion;
}

#flvplayerContainer,
.quotearea,
#maxicomments .tabCol1,
#maxicomments .tabCol2,
.tableforum,
.tableline1,
.tableline2,
.forumline {
    border-color: #212121;
}

#maxicomments .tabHeader,
#maxicomments .tabHeader .tabCol1,
#maxicomments .tabHeader .tabCol2,
.forumline .forumline-odd {
    background-color: #212121;
}

#maxicomments .tabRow1,
#maxicomments .tabCol2,
.forumline .forumline-even {
    background-color: #313131;
}

.tableline1 {
    color: #bbb;
}

.tableline2 {
    color: #aaa;
}

tr[bgcolor="#A0A0A0"] {
    background-color: #191919;
}

img[src="imgv2/stick.gif"],
img[src="imgv2/nonew.gif"],
img[src="imgv2/new.gif"] {
    border-radius: 50%;
    opacity: 0.75;
}

span.bluebold[style*="color:#000000"] {
    color: #ccc !important;
}

.quotearea .small,
#maxicomments p.msgsignature,
#maxicomments p.msgheader {
    color: #aaa;
}

#maxicomments img.smallicons {
    border-radius: 3px;
}

#selectcartouche form select,
.form,
fieldset input[type=text],
fieldset textarea,
.shoutbox-quote .shoutbox-textbox-wrapper,
.shoutbox-quote .shoutbox-textbox-wrapper form,
.shoutbox-quote .shoutbox-textbox-wrapper input,
.news-panel .news-comments .comment-textarea,
textarea.forumtxt {
    color: #ccc;
    background-color: #444;
    border-color: #222;
}

fieldset input[type=text],
fieldset textarea,
input.form {
    border-style: solid;
    border-width: 1px;
}

input.formsubmit {
    border-style: solid;
    border-width: 1px;
    border-color: #212121;
    background-color: #212121;
    color: #cc7700;
}

.shoutbox-messages {
    background-color: #212121;
}

.shout {
    background-color: #212121;
    border-color: #212121;
}


.shoutbox-quote .shoutbox-textbox-wrapper input {
    padding-bottom: 7px;
}

.shoutbox-quote .shoutbox-textbox-button {
    color: #ccc;
    background-color: #212121;
    background-blend-mode: soft-light;
}

.shoutbox-messages li div {
    color: #bbb;
}

.shoutbox-messages li div .posted {
    color: #777;
}


a.shoutbox-navbutton.navbutton-left,
a.shoutbox-navbutton.navbutton-right {
    background-blend-mode: soft-light;
    background-color: #212121;
}

#ongletHeader {
    background-color: #373737;
    background-blend-mode: overlay;
}

#ongletHeader li,
#ongletHeader a {
    background-blend-mode: overlay;
    background-color: #373737;
    color: #ccc;
}

#ongletHeader a:hover,
#ongletHeader .current a,
#ongletHeader .current a:hover {
    color: #cc7700;
    text-shadow: 1px 1px 0px #212121;
}


#ongletHeader .new-message,
#ongletHeader .new-message a {
    background-color: #5e5e5e;
    background-blend-mode: difference;
}



.message {
    background-color: #212121;
    background-blend-mode: soft-light;
    color: #ccc;
    border-color: #212121;
}
.message div {
    background-color: #373737;
}
.message div a {
    color: #ccc;
}


ol.video-list,
.express-links ul li,
.news-panel .social-bookmarks,
.news-panel .news-comments,
.news-panel,
.news-panel .news-comments .comment {
    border-color: #777;
}


.main-menu .main-menu-item .menu-item-pointer,
.main-menu .main-menu-separator {
    background-blend-mode: overlay;
    background-color: #373737;
}

.main-menu .main-menu-item .menu-item-pointer {
    z-index: -1;
}


#filter-show {
    background-color: #999;
    padding: 1px;
}

.filter {
    background-color: #212121;
    border-color: #212121;
    color: #bbb;
    z-index: 10;
}

.filter ul li.tout-rien,
.filter ul li.tout-rien a {
    color: #999;
}

.filter .selected {
    background-color: #555;
    color: #ccc;
}

.filter .highlight {
    background-color: #999;
    color: #212121;
}

.filter-button {
    color: #bbb;
    border-color: #bbb;
}

.filter-init {
    background-color: #bbb;
}



.table {
    border-color: #212121;
}

.tabHeader {
    color: #cc7700;
    background-color: #212121;
}

.tabRow1 {
    background-color: #313131;
    border-color: #212121;
    color: #bbb;
}

#listfiches .table a {
    color: #cc7700 !important;
}

div.plateforme img {
    border-radius: 50%;
}

p.video-thumbnail,
ol.video-list li {
    background-color: #212121;
    border-color: #212121;
}



div.stream-tabs,
div.account-tabs {
    margin-top: 15px;
}

div.stream-tab.active,
div.account-tab.active {
    background-color: #373737;
    border-color: #212121;
    color: #cc7700;
}

div.stream-tab,
div.account-tab {
    background-color: #212121;
    border-color: #212121;
    color: #ccc;
}

div.stream-tab-div,
div.account-tab-div {
    background-color: #373737;
    border-color: #212121;
    color: #ccc;
}

.account-tab-div .shoutbox-messages {
    background-color: #373737;
}

.shoutbox-messages.big li {
    background-color: #212121;
    background-blend-mode: darken;
    border-color: #212121;
}

.shoutbox-messages.big li .shoutbox-message-date {
    color: #999;
}

.shoutbox-messages.big li .shoutbox-arrow-border {
    border-left-color: #373737;
    border-right-color: #373737;
    border-top-color: #212121;
}

.shoutbox-messages.big li .shoutbox-arrow {
    border-top-color: #212121;
}
    

table.myvideos {
    border-color: #212121;
    margin-top: 15px;
}

table.myvideos th {
    border-color: #212121;
    color: #bbb;
}

table.myvideos tr:nth-child(even) {
    background-color: #212121;
}


fieldset {
    background-color: #212121;
    border-color: #212121;
}



.game-sheet-related-news {
    background-color: #212121;
    border-color: #212121;
    color: #bbb;
}

.game-sheet-related-news.active {
    background-color: #373737;
    border-color: #212121;
    color: #cc7700;
}

.game-sheet-related-news a,
.game-sheet-related-news a:hover,
.game-sheet-related-news a:active,
.game-sheet-related-news a:visited {
    color: #bbb;
    text-decoration: none;
}

.game-sheet-related-news.active a,
.game-sheet-related-news.active a:hover,
.game-sheet-related-news.active a:active,
.game-sheet-related-news.active a:visited {
    color: #cc7700;
    text-decoration: none;
}

.game-sheet-related ~ div.clear {
    border-color: #212121 !important;
}

.news-videos-date {
    background-color: #373737;
    background-blend-mode: overlay;
}

.news-videos-date span {
    background-color: #373737;
}


td[bgcolor="#A0A0A0"] {
    background-color: #212121;
}

tr[bgcolor="#DFDFDF"] {
    background-color: #3d3d3d;
}

tr[bgcolor="#EFEFEF"] {
    background-color: #313131;
}

img[src="images/msgsend.gif"] {
    border-radius: 3px;
}


}
