css:
/* i really want this to be global */
:root {
      --main-font-family: "Fira Code";
      --main-bg-color: #fcfcfd;

      --atom-one-dark-cyan : #56B6C2;
      --atom-one-dark-blue : #61AFEF;
      --atom-one-dark-bg-1 : #121417;
      --atom-one-dark-mono-2 : #828997;
      --atom-one-dark-red-1 : #E06C75;
      --atom-one-dark-mono-1 : #ABB2BF;
      --atom-one-dark-silver : #666;
      --atom-one-dark-purple : #C678DD;
      --atom-one-dark-orange-2 : #E5C07B;
      --atom-one-dark-bg-hl : #2F343D;
      --atom-one-dark-black : #0F1011;
      --atom-one-dark-gutter : #666D7A;
      --atom-one-dark-fg : #ABB2BF;
      --atom-one-dark-green : #98C379;
      --atom-one-dark-red-2 : #BE5046;
      --atom-one-dark-orange-1 : #D19A66;
      --atom-one-dark-mono-3 : #5C6370;
      --atom-one-dark-accent : #AEB9F5;
      --atom-one-dark-bg : #EEE;
      --atom-one-dark-gray : #3E4451;
  }
/* Intro page  */
    body {
        background-color: #282c34 !important;
        color : var(--atom-one-dark-silver);
    }
    
    .list_header {
        background-color: var(--atom-one-dark-bg );
    }

    div#notebook{
        background-color:  var(--main-bg-color);
    }

/* Dropdown menus */

    .dropdown-menu {
        background: var(--main-bg-color);
        color: #000;
    }

    .dropdown-menu>li>a, .dropdown-submenu>a:after, .dropdown-menu>li>a:visited:after {
        color: #444;
    }

    .dropdown-menu .divider {
        background-color: #444;
    }

    .dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
        color: #fff;
        background-color: #444;
    }

    .dropdown-menu>li>a:hover:after, .dropdown-menu>li>a:focus:after, .dropdown-menu>li>a:active:after {
        color: #fff;
    }
    
    div#header{
        background-color:  var(--main-bg-color);
    }
    
    div#menus{
        background-color:  var(--main-bg-color); 
    }

    div#maintoolbar{
        background-color:  var(--main-bg-color); 
    }
    
    div#notebook-container{
        background-color:  var(--main-bg-color);
    }
    
    body > #header .header-bar {
        background: #757575;
    }

/* Help panel */
    div#pager {
        background-color: #282c34;
    }
    
    div#pager .ui-resizable-handle {
        top: 0px;
        height: 8px;
        background: #282c34;
        border-top: 1px solid #757575;
        border-bottom: 1px solid #757575;
    }
    div#pager pre {
        color: #9ac2c9;
        background-color: #282c34;
    }
    

    .btn-default {
        color: var(--atom-one-dark-silver);
        background-color: var(--atom-one-dark-bg);
        border-color: var(--atom-one-dark-gray);
    }
    
    .notification_widget {
        color: #777;
        background: rgba(240, 240, 240, 0.5);
        color: #333;
        background-color: #aaa;
        border-color: #757575;
    }
    
    span#login_widget > .button, #logout {
        color: #333;
        background-color: #aaa;
        border-color: #757575;
    }
    
    .form-control {
        color: #333;
        background-color: #aaa;
        border: 1px solid #757575;
    }
    
    .input_area {
        border: 1px solid #757575 !important;
    }
    
    .notebook_app div.input_prompt {
        color: #AAA;
    }
    
    .notebook_app div.output_prompt {
        color: #AAF;
    }
    
    .notebok_app div.output_area pre {
/*         color: whitesmoke !important; */
        color: var(--atom-one-dark-silver) !important;
    }
    
    .notebook_app div.output_subarea pre {
/*         color: whitesmoke !important; */
        color: var(--atom-one-dark-silver) !important;
    }

    .notebook_app div.text_cell_render, .notebook_app div.output_html pre {
        color: #979797;
    }

/* Tooltips */
    .ipython_tooltip {

        background-color:  var(--main-bg-color) !important;
    }
    .ipython_tooltip .tooltiptext pre {
        font-size: 100%;
        color:lightgrey;
        background-color:var(--main-bg-color) !important;
    }

/*Terminal text coloring*/

    span.ansiblack {
        color: #323537;
    }

    span.ansiblue {
        color: #afc4db;
    }

    span.ansigray {
        color: #838184;
    }

    span.ansigreen {
        color: #8f9d6a;
    }

    span.ansipurple {
        color: #9b859d;
    }

    span.ansired {
        color: #cf6a4c;
    }

    span.ansiyellow {
        color: #c2bc15;
    }

/*INPUT CELLS*/

  .CodeMirror {
/*Background color of input cell*/
/*       background: #141414; */
      background-color: #fcfcfd;
      color: var(--atom-one-dark-mono-1);
/*       border-radius: .35em; */
      /*box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);*/
  }

  div.CodeMirror-selected {
      background: #000 !important;
/*       background: beige!important; */
  }

  #texteditor-backdrop #texteditor-container .CodeMirror-gutter, .CodeMirror-gutters {
      background: #fcfcfd;
      color: #fcfcfd;
  }

  .CodeMirror-linenumber {
      color: #7C7C7C;
      font-size: 12px !important;
  }

  .CodeMirror-cursor {
      border-left: 1px solid #A7A7A7 !important;
      margin-top: -2px;
      min-height: 18px;
  }

  .cm-s-ipython.CodeMirror {
      background-color: #FAFAFA;
      /*background: #282C34;*/
      /*background: #2c303c;*/
      /* The color below is all of the text not spoken for by the code below. */
      color: #999;
  }

  .cm-s-ipython div.CodeMirror-selected {
      background: #343d46 !important;
  }

  .cm-s-ipython .CodeMirror-gutters {
      background: #2b303b;
      border-right: 0px;
  }

  .cm-s-ipython .CodeMirror-linenumber {
      color: #fff;
      font-size: 12px;
  }

  .cm-s-ipython .CodeMirror-cursor {
      border-left: 3px solid #a7adba !important;
  }

  .cm-s-ipython span.cm-comment {
      color: #9ca2a2;
  }

  .cm-s-ipython span.cm-atom {
      color: #88F;
  }

  .cm-s-ipython span.cm-number {
      color: #de611e;
  }

  .cm-s-ipython span.cm-property {
      color: #05a;
  }

  .cm-s-ipython span.cm-attribute {
      color: #5a86dc;
  }

  .cm-s-ipython span.cm-keyword {
      color: #4c8bc3;
  }

  .cm-s-ipython span.cm-string {
      color: #4a99f3;
  }

  .cm-s-ipython span.cm-meta {
      color: #ff228f;
  }

  .cm-s-ipython span.cm-operator {
      color: #bb90d6;
  }

  .cm-s-ipython span.cm-builtin {
      color: #cc0000;
  }

  .cm-s-ipython span.cm-variable {
      color: #212121;
  }

  .cm-s-ipython span.cm-variable-2 {
      color: #37474F;
  }

  .cm-s-ipython span.cm-variable-3 {
      color: #455A64;
  }

  .cm-s-ipython span.cm-def {
	  color: #8c4ace;
  }

  .cm-s-ipython span.cm-error {
      background: #bf616a;
      color: #e627b5;
  }

  .cm-s-ipython span.cm-bracket {
      color: #997;
      font-weight: bold;
  }
  
  .cm-s-ipython span.cm-tag {
      color: #170;
  }

  .cm-s-ipython span.cm-link {
      color: #00c;
  }

  .cm-s-ipython span.cm-storage {
      color: #61afef;
  }

  .cm-s-ipython span.cm-entity {
      color: #61afef;
  }

  .cm-s-ipython span.cm-latex {
      color: #8fb36a;
  }

  .cm-s-ipython .CodeMirror-matchingbracket {
      text-decoration: underline;
      -moz-text-decoration-color: #37d6f2;
      text-decoration-color: #37d6f2;
      color: #B5C2D9 !important;
  }


  /*Markdown editing*/

  .CodeMirror.cm-s-default {
      background: #2C2C2C;
      color: #787474;
      border-radius: .35em;
      box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);
  }

  .cm-s-default span.cm-bracket {
      color: #c0c5ce;
  }

  .cm-s-default span.cm-header {
      color: #CF4A4C;
  }

  .cm-s-default span.cm-variable-2 {
      color: #7587A6;
  }

  .cm-s-default span.cm-comment {
      color: #7F7A80;
  }
