css:
@-moz-document domain("twitter.com") {

    /* COLORS */

    /** DESCRIPTION : set the colors **/
    :root {
        --body-bg: #192633 !important;
        --card-bg: #202C3A !important;
        --card-bg-2: #121A23 !important;
        --op-bg: rgba(32, 44, 58, .9) !important;
        --border-color: #0A0F14 !important;
        --primary-color: #FFF !important;
        --secondary-color: #EBEBEB !important;
        --invert: invert(1) !important;
    }

    /* GENERAL */

    /** DESCRIPTION : set main font and background, plus change/remove elements from all page **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド", "BM YEONSUNG" !important;
    }

    *::-moz-focus-inner {
        border: 0 !important;
    }

    input::-moz-placeholder {
        color: #FFF !important;
    }

    select {
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

    html,
    body,
    #page-container,
    .MomentMakerPage-content,
    .ProfilePage.is-editing .ProfilePage-editingOverlay {
        background: var(--body-bg) !important;
    }

    button:focus {

        /*** BUTTONS ***/
        box-shadow: none !important;
    }

    .plain-btn:focus,
    .plain-btn.focus,
    .plain-btn.active,
    .plain-btn:active,
    .plain-btn.open,
    .dropdown.open .user-dropdown.plain-btn,
    .open > .plain-btn {
        border: none;
    }

    .photo-selector:hover .btn,
    .icon-btn:hover,
    .icon-btn:active,
    .icon-btn.active,
    .icon-btn.enabled {
        background: transparent;
        border-color: transparent;
    }

    .EdgeButton--primary:hover,
    .EdgeButton--primary:active {
        border-color: transparent;
    }

    .icon-btn,
    .ProfileCard-screennameLink,
    .username,
    .username *,
    .twitter-timeline-link *,
    .twitter-atreply.pretty-link,
    .twitter-atreply.pretty-link * {
        color: #189EF3 !important;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .u-linkComplex-target {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .stream-end-item,
    .stream-end,
    .stream-loading,
    .stream-placeholder {

        /*** LOADING CONTAINER ***/
        border-color: transparent !important;
    }

    .NotificationsEmptyModule,
    .SearchEmptyTimeline,
    .stream-end-inner {

        /*** EMPTY CONTAINER ***/
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        margin-top: 15px;
    }

    .NotificationsEmptyModule,
    .SearchEmptyTimeline {
        padding: 30px 30px 40px 30px;
    }

    .NotificationsEmptyModule *,
    .SearchEmptyTimeline *,
    .stream-end-inner * {
        color: var(--primary-color) !important;
        font-stretch: condensed;
    }

    .NotificationsEmptyModule-body,
    .SearchEmptyTimeline-emptyDescription,
    .MutedKeywordsFooter-emptyMessage,
    .stream-end-inner * {
        font-size: 14pt !important;
        font-stretch: condensed;
        line-height: 20pt;
    }

    .btn-link.translate-button {

        /*** TRANSLATION ***/
        color: #189EF3 !important;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .btn-link.translate-button .Icon--translator {
        color: #189EF3 !important;
        top: -1px !important;
    }

    .tweet-translation {
        border-top: 1px dotted var(--border-color);
        margin-top: 10px;
    }

    hr {

        /*** SEPARATOR ***/
        border-color: var(--border-color) !important;
    }

    .AdaptiveRelatedSearches,
    .blocked-account-nav,
    .dashboard.dashboard-left,
    .dashboard.dashboard-right,
    .DashboardProfileCard.DashboardProfileCard--withoutStats.module,
    .dropdown-divider,
    .dropdown-menu li a[href*="ads"],
    .dropdown-menu li a[href*="analytic"],
    .dropdown-menu li a[href*="support"],
    .EmojiPicker-content.Caret::after,
    .EmojiPicker-content.Caret::before,
    .Footer,
    .has-recap,
    .hover-offset,
    .js-keyboard-shortcut-trigger,
    li[id*="who_to_follow"],
    .module.Trends.trends,
    .MomentMakerRecommendations,
    .MomentsPermalinkPage-related,
    .MoveableModule,
    #notifications-optout-all-top,
    #notifications-optout-all-bottom,
    .permalink .stream-end-inner,
    .ProfileCard-socialProof,
    .ProfileListsTimeline .GridTimeline-end:not(.has-items),
    .RadialCounter,
    .SidebarCommonModules,
    .SidebarFilterModule,
    #sms-phone-delete-form #unsupported-carrier-alert,
    .ThreadedConversation--header,
    .translation-attribution {
        display: none !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .global-nav-inner {
        background: #189EF3 !important;
        padding-bottom: 4px;
    }

    .Icon.Icon--bird.bird-topbar-etched {

        /*** HEADER LOGO ***/
        filter: brightness(0) invert(1) !important;
    }

    #global-actions > li * {

        /*** HEADER LINK ***/
        border: none !important;
        color: #FFF;
        font-size: 14pt;
        font-stretch: condensed;
        line-height: 10pt;
    }

    #global-actions > li.active * {
        color: #EBEBEB !important;
    }

    .Icon--home::before,
    .Icon--homeFilled::before {
        margin-top: 4px !important;
        margin-left: -5px;
    }

    .Icon--notifications::before,
    .Icon--notificationsFilled::before,
    .Icon--dm::before,
    .Icon--dmFilled::before {
        margin-top: 5px !important;
        margin-left: -5px !important;
    }

    .count.new-count {
        background: transparent !important;
    }

    .count-inner {
        background: transparent !important;
        font-size: 10pt !important;
    }

    .global-nav .search-input,
    .global-nav .search-input:focus {

        /*** HEADER SEARCHBAR ***/
        background: transparent;
        border: none;
        color: #FFF;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .global-nav .search-icon .Icon {
        color: #FFF;
    }

    .typeahead-accounts.social-context .typeahead-account-item .typeahead-user-item-info .fullname {
        color: var(--primary-color);
    }

    .typeahead-accounts.social-context .typeahead-account-item .typeahead-user-item-info {
        height: 20px;
    }

    .typeahead-item.typeahead-topic-item {
        margin-top: -5px !important;
        margin-left: 5px !important;
    }

    .typeahead .dropdown-inner > .has-results ~ .has-results,
    .typeahead .dropdown-inner > .has-items ~ .has-items {
        border: none;
        margin-top: 10px;
    }

    .dropdown-menu .typeahead-items li > a:focus,
    .dropdown-menu .typeahead-items li > a:hover,
    .dropdown-menu .typeahead-items .selected,
    .dropdown-menu .typeahead-items .selected a {
        background: var(--card-bg) !important;
    }

    .typeahead strong,
    .dropdown-menu .typeahead-items li > a:focus,
    .dropdown-menu .typeahead-items li > a:hover,
    .dropdown-menu .typeahead-items .selected,
    .dropdown-menu .typeahead-items .selected a,
    .typeahead a:hover,
    .typeahead a:hover strong,
    .typeahead a:hover .fullname,
    .typeahead .selected a,
    .typeahead .selected strong,
    .typeahead .selected .fullname,
    .typeahead .selected .Icon--close {
        color: var(--secondary-color) !important;
    }

    .typeahead button.clear-recent-searches {
        color: #189EF3 !important;
        margin-top: -5px !important;
    }

    .typeahead-item .Icon.Icon--close {
        margin-top: 10px !important;
    }

    #user-dropdown-toggle {

        /** HEADER ACCOUNT ***/
        margin-top: 4px !important;
    }

    .DashUserDropdown-userInfo .fullname,
    .dropdown-menu li > a,
    .DashUserDropdown.dropdown-menu button {
        color: var(--primary-color) !important;
    }

    .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink {
        margin-bottom: 15px;
    }

    .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:hover,
    .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:focus,
    .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:active {
        background: transparent !important;
    }

    .DashUserDropdown-linkIcon {
        position: relative !important;
        top: -5px !important;
    }

    .DashUserDropdown-linkIcon.Icon.Icon--medium.js-nightmode-icon {
        margin-top: 5px !important;
    }

    #global-new-tweet-button {

        /*** HEADER TWEET BUTTON ***/
        background: transparent;
        font-size: 14pt;
        font-stretch: condensed !important;
    }

    /* MODAL */

    /** DESCRIPTION : change the elements of the modal windows popup **/
    .modal-header,
    .RetweetDialog-commentBox,
    .modal .modal-tweet,
    .modal-content {
        background: var(--body-bg) !important;
        border-radius: 2px;
        border: none;
    }

    .modal-header .modal-title {
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .modal-content {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding: 5px !important;
    }

    .modal .fullname.show-popup-with-id.u-textTruncate,
    .modal .fullname.show-popup-with-id.u-textTruncate:hover {
        color: var(--primary-color) !important;
    }

    .modal .tweet-form .reply-users {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .modal-content .RichEditor {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .modal .EdgeButton--primary,
    .modal .EdgeButton--secondary,
    .modal .EdgeButton--tertiary,
    .modal .EdgeButton--danger {
        background: #189EF3 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        padding-top: 7px;
        padding-bottom: 10px !important;
    }

    .modal .EdgeButton--primary,
    .modal .EdgeButton--secondary {
        background: #189EF3 !important;
    }

    .modal .EdgeButton--tertiary {
        background: #BF4A67 !important;
    }

    .modal .EdgeButton--danger {
        background: #3E4651 !important;
    }

    #global-tweet-dialog-header,
    #Tweetstorm-dialog-header,
    #confirm_dialog-header {

        /*** TWEET MODAL ***/
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    #global-tweet-dialog .modal-header,
    #Tweetstorm-dialog .modal-header {
        border: none !important;
    }

    #global-tweet-dialog .modal-tweet .tweet:hover,
    #global-tweet-dialog .modal-tweet .tweet {
        background: var(--body-bg);
        border: none !important;
    }

    #global-tweet-dialog-body {
        padding: 0px 15px;
    }

    #global-tweet-dialog .modal-tweet-form-container,
    #Tweetstorm-dialog .modal-body {
        background: var(--body-bg);
    }

    #tweet-box-global,
    .tweet-box.rich-editor {
        background: var(--card-bg) !important;
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    #confirm_dialog-body * {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .modal-footer {
        background: transparent !important;
        border: none !important;
    }

    #retweet-tweet-dialog-header {

        /*** RETWEET MODAL ***/
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .RetweetDialog-footer {
        padding: 15px 25px;
    }

    #retweet-with-comment {
        background: var(--card-bg) !important;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .DMDock {

        /*** DM MODAL ***/
        background: var(--body-bg);
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .DMInbox-toolbar.EdgeButton.EdgeButton--small.EdgeButton--secondary.EdgeButton--icon.mark-all-read {
        background: transparent !important;
        box-shadow: none !important;
        padding-top: 2px !important;
        padding-bottom: 0px !important;
    }

    .DMInbox-toolbar.DMComposeButton.EdgeButton.EdgeButton--small.EdgeButton--primary.dm-new-button * {
        color: #FFF !important;
        font-size: 14pt !important;
    }

    .DMActivity-header,
    .DMActivity-body,
    .DMButtonBar {
        background: var(--body-bg);
        border: none;
    }

    .DMActivity-header {
        border-top-right-radius: 2px !important;
        border-top-left-radius: 2px !important;
    }

    .DMActivity-header * {
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .DMTypeaheadHeader {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .TokenizedMultiselect-inputContainer {
        border: none;
    }

    .TokenizedMultiselect-input {
        background: var(--card-bg);
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color) !important;
        height: 34px;
        font-size: 14pt;
        font-stretch: condensed;
        padding: 5px;
    }

    .InputToken {
        background: #189EF3 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        padding-top: 5px;
        padding-bottom: 9px;
    }

    .DMTokenizedMultiselectSuggestion.is-highlighted {
        background: var(--card-bg);
        border-color: transparent;
    }

    .DMTypeaheadItem-title.account-group *,
    .DMEmptyState * {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .DMEmptyState .DMEmptyState-header,
    .DMEmptyState .DMEmptyState-details * {
        color: var(--primary-color);
        line-height: 20pt;
    }

    .DMTypeaheadItem-title.account-group .fullname {
        color: var(--primary-color) !important;
    }

    .DMButtonBar {
        border-bottom-right-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
    }

    .u-bgUserColorLightest,
    .DMComposer-container {
        background: var(--body-bg) !important;
        border: none;
    }

    .u-borderUserColorLighter {
        background: var(--card-bg) !important;
        border: none;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .DMInboxItem {
        border-color: var(--border-color);
    }

    .DMInboxItem:hover {
        background: var(--card-bg) !important;
    }

    .DMInboxItem .fullname,
    .DMUpdateName-name.u-textTruncate {
        color: var(--primary-color);
        font-stretch: condensed;
    }

    .DirectMessage-text .tweet-text,
    .DMInbox-conversationItem * {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .DMReadReceipt {
        font-size: 12pt !important;
        font-stretch: condensed !important;
        margin-top: 10px !important;
    }

    #embed-tweet-dialog-header {

        /*** EMBED MODAL ***/
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .modal-body .embed-code-container *:not(a) {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
        line-height: 18pt;
    }

    .embed-code-container textarea,
    .EmbeddedTweet {
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .EmbeddedTweet * {
        color: var(--primary-color);
        font-size: 12pt;
        font-stretch: condensed;
    }

    .EmbeddedTweet--mediaForward .EmbeddedTweet-tweet {
        border-color: var(--border-color) !important;
        border-radius: 2px !important;
    }

    #report-dialog-header {

        /*** REPORT MODAL ***/
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .PageContainer,
    .report-dialog .new-report-flow-report,
    .ResponsiveLayout {
        background: transparent !important;
    }

    .report-dialog .new-report-flow-report {
        width: 514px !important;
    }

    .PageContainer * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal;
        line-height: 18pt;
    }

    .MomentCapsuleSummaryListDialog-title {

        /*** MOMENT MODAL ***/
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
    }

    .MomentCapsuleSummary-details * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    .MomentCapsuleSummaryListDialog-header .EdgeButton.EdgeButton--primary.EdgeButton--small.MomentCapsuleSummaryListDialog-button.MomentCapsuleSummaryListDialog-button--newMoment {
        padding-bottom: 7px !important;
    }

    .MomentCapsuleSummaryListDialog .MomentCapsuleSummary {
        border-color: var(--border-color);
    }

    .block-dialog .block-section {

        /*** BLOCK MODAL ***/
        border: none;
    }

    .block-dialog .block-section * {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .block-dialog .submit-section,
    .phone-deletion-dialog .submit-section {
        background: transparent;
    }

    .MomentSensitiveContentDialog .modal-body {

        /*** SENSITIVE MODAL ***/
        background: var(--card-bg);
        border-radius: 2px !important;
    }

    .MomentSensitiveContentDialog .modal-body * {
        color: var(--primary-color);
        font-stretch: condensed;
    }

    .MomentSensitiveContentDialog-link {
        font-size: 14pt;
    }

    .MomentMakerVisibilityDialog-description.MomentDialog-text,
    .MomentDialog-text,
    .MomentDialog-toggleText {

        /*** MOMENT MORE MODAL ***/
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        line-height: 20pt;
    }

    .MomentDialog-toggleText {
        position: relative;
        top: -4px !important;
    }

    .MomentMakerVisibilityDialog-shareUrl {
        background: var(--card-bg) !important;
        border: none;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color) !important;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .modal-body.SearchSettingsDialog-body *:not(a) {

        /*** SETTINGS MODAL ***/
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        line-height: 20pt;
    }

    .SearchSettingsDialog-body input[type="checkbox"] {
        margin-top: 8px !important;
    }

    .activity-popup-dialog-in-reply-to-user .account,
    #activity-popup-dialog.reply-users-popup .activity-popup-dialog-users .account,
    #activity-popup-dialog.reply-users-popup .activity-popup-dialog-other-reply-users {

        /*** ACTIVITY MODAL ***/
        border: none;
    }

    #activity-popup-dialog * {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        line-height: 18pt;
    }

    #activity-popup-dialog .Icon.Icon--check.Icon--small {
        line-height: 14pt !important;
    }

    /* ALERT */

    /** DESCRIPTION : change the elements of the alert message popup **/
    .message {
        background: var(--card-bg-2) !important;
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        width: 100% !important;
    }

    .message button {
        margin-right: 25px !important;
    }

    .alert-messages * {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    /* DASHBOARD */

    /** DESCRIPTION : change the elements of the dashboard page **/
    #page-container #timeline.content-main {
        width: 70% !important;
        position: absolute;
        top: 80px;
        right: 0px;
        bottom: 0px;
        left: 0px;
        margin: auto !important;
    }

    .stream-item {

        /*** DASHBOARD TWEET ***/
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 5px;
        margin-bottom: 10px !important;
    }

    .tweet {
        border-bottom: none !important;
    }

    .tweet:hover {
        background: transparent !important;
    }

    .home-tweet-box,
    .top-timeline-tweetbox .timeline-tweet-box {

        /*** DASHBOARD FEED TWEET ***/
        background: var(--card-bg);
        border: none !important;
        border-radius: 2px !important;
    }

    .top-timeline-tweetbox .timeline-tweet-box {
        padding-bottom: 3px !important;
    }

    .top-timeline-tweetbox .timeline-tweet-box {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    #tweet-box-home-timeline {
        background: var(--card-bg);
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    .RichEditor {
        background: var(--card-bg) !important;
        border: none !important;
        border-color: transparent;
        box-shadow: none !important;
    }

    .home-tweet-box .EdgeButton--primary,
    .home-tweet-box .EdgeButton--primary:focus,
    .home-tweet-box .geo-picker .EdgeButton--secondary {
        background: #189EF3 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        padding-bottom: 9px !important;
    }

    .js-add-tweet.EdgeButton.EdgeButton--secondary.EdgeButton--icon.EdgeButton--medium.js-tooltip {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
        color: #189EF3 !important;
        padding-bottom: 0px !important;
    }

    .tweet-form .thumbnail-container {

        /*** DASHBOARD TWEET FEED THUMBNAIL ***/
        background: transparent;
        border: none !important;
    }

    .tweet-form .thumbnail-container .thumbnail-wrapper {
        border-top: 1px dotted var(--border-color);
        padding-top: 20px !important;
    }

    .ComposerThumbnail {
        border: none;
        border-radius: 2px;
