css:
@-moz-document domain("123movies.cafe")
{
  body
  {
    background: #101010;
    font-size: 14px;
    font-weight: normal;
    color: #333;
    margin: 0px;
    padding: 0px;
    -webkit-text-size-adjust: none;
    position: relative;
  }

  #list-eps
  {
    background: #222;
    margin-bottom: 20px;
  }

  #mv-info .mvi-content
  {
    background: #222;
    padding: 20px;
    font-size: 12px;
    position: relative;
    width: 100%;
    margin-top: -5px;
  }

  #list-eps .le-server
  {
    padding: 10px 20px;
    border-bottom: 1px solid #101010;
  }

  element.style
  {
    position: fixed;
    top: 0px;
    background-color: rgb(34, 34, 34);
  }

  .alert-warning
  {
    color: #FFBD22;
    background-color: #222222;
    border-color: #222222;
  }

  #menu ul.top-menu li a
  {
    height: 70px;
    line-height: 70px;
    margin: 0;
    padding: 0 5px;
    font-family: 'Oswald';
    font-size: 16px;
    color: #696969;
    display: inline-block;
  }

  .form-control
  {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #F1F1F1;
    background-color: #333333;
    background-image: none;
    border: 1px solid #222;
    border-radius: 2px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    -webkit-transition: border-color ease-in-out .15s, -webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  }

  #mv-keywords
  {
    margin-bottom: 20px;
    padding: 20px;
    display: block;
    clear: both;
    background: #222222;
  }

  body
  {
    background: #101010;
    font-size: 14px;
    font-weight: normal;
    color: #BDBDBD;
    margin: 0px;
    padding: 0px;
    -webkit-text-size-adjust: none;
    position: relative;
  }

  element.style
  {
    position: fixed;
    top: 0px;
    background-color: rgb(34, 34, 34);
  }

  header
  {
    background: #222 !important;
    height: 70px;
    width: 100%;
    display: table;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    position: fixed;
    top: 0;
    left: 0;
    z-index: 99;
  }

  #commentfb
  {
    padding: 10px 20px;
    margin: 20px 0;
    background: #222;
    display: block;
  }

  #list-eps .le-server .les-content .btn-eps
  {
    padding: 7px 12px 5px 12px;
    min-width: 72px;
    text-align: center;
    border-radius: 3px;
    background: #222222;
    border-bottom: 3px solid #222222;
    font-size: 12px;
    color: #9A9A9A;
    font-weight: bold;
    margin: 5px;
    white-space: nowrap;
    display: inline-block;
  }

  #list-eps .le-server .les-content .btn-eps:hover
  {
    border-color: #70B63D;
    background: #70B63D;
    color: #fff;
  }

  #media-player .jw-background-color,
#media-player .jw-tooltip-title,
#media-player .jw-playlist,
#media-player .jw-playlist-container .jw-option
  {
    background: #222222;
  }

  .jw-skin-seven .jw-controlbar
  {
    border-top: #222 1px solid;
    height: 2.5em;
  }

  #menu .sub-container
  {
    position: absolute;
    top: 60px;
    left: -20px;
    width: 500px;
    background: #222;
    border: 1px solid #222;
    border-top: 1px solid #79C143;
    z-index: 5;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.1);
  }

  #menu ul.top-menu li:hover .sub-container ul.sub-menu li a
  {
    color: #696969;
  }

  .pagination>li>a,
.pagination>li>span
  {
    position: relative;
    float: left;
    padding: 6px 12px;
    margin-left: -1px;
    line-height: 1.42857143;
    color: #337ab7;
    text-decoration: none;
    background-color: #222;
    border: 1px solid #ddd;
  }

  .pagination > li > a,
.pagination > li > span
  {
    border: none;
    color: #5D6969;
  }

  .pagination>li>a:focus,
.pagination>li>a:hover,
.pagination>li>span:focus,
.pagination>li>span:hover
  {
    color: #23527c;
    background-color: #79C142;
    border-color: #79C142;
  }

  .filter-toggle
  {
    float: right;
    height: 40px;
    line-height: 40px;
    padding: 0 15px;
    background: #222;
    border-radius: 3px;
    cursor: pointer;
  }

  .movies-list-wrap .ml-title .nav-tabs li a
  {
    border: none;
    height: 40px;
    line-height: 40px;
    padding: 0 15px;
    text-align: center;
    min-width: 80px;
    color: #696969;
  }

  .nav>li>a:focus,
.nav>li>a:hover
  {
    text-decoration: none;
    background-color: #79C142;
  }

  .social-home
  {
    background: #222;
    text-align: left;
    height: 50px;
  }

  .box
  {
    margin-bottom: 20px;
    background: #222;
    display: block;
    overflow: hidden;
  }

  .news-list-item
  {
    padding: 10px 15px;
    border-bottom: 1px solid #79C143;
  }

  .news-list-item:hover
  {
    background: #79C143;
  }

  .letter-movies-lits .table-striped
  {
    background: rgb(34, 34, 34);
    margin-top: 10px;
  }

  .letter-movies-lits .table-striped > tbody > tr > td
  {
    border-color: #101010;
    vertical-align: middle;
  }

  .letter-movies-lits a
  {
    color: #79C142;
  }

  .movies-letter a.btn-letter
  {
    float: left;
    width: 3.2%;
    margin: 0 0.25% 0.8%;
    padding: 5px 1px;
    border-radius: 3px !important;
    overflow: hidden;
    color: #79C142;
    font-size: 14px;
    background: #222;
    box-shadow: none;
  }

  .nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover
  {
    color: #555;
    cursor: default;
    background-color: #222;
    border: 1px solid #ddd;
    border-bottom-color: transparent;
  }
}
