css:
@-moz-document domain("gameinformer.com") {
/*Theme (c) 2018 Lonely-Creator
Feel free to fork and improve it, I am just a beginner!*/

/*Header*/
header {
    background-color: #121212 !important;
}
/*Search Box*/
.header-dropdown {
    background-color: #0F0F0F !important;
}
/*Search Box Underline*/
.region-header-hamburger-top .search-block-form form, #search-dropdown .search-block-form form, .region-header-hamburger-top .block-views-exposed-filter-blocksearch-content-page-1 form, #search-dropdown .block-views-exposed-filter-blocksearch-content-page-1 form {
    border-bottom: 2px solid #999;
}
/*Author/Publication Date*/
#header-featured-articles-wrapper .author-details,
#body-featured-articles .author-details {
    color: #999 !important;
}
/*Color Behind Feature Images*/
#header-featured-articles-wrapper .article-image, #body-featured-articles .article-image {
    background-color: #121212 !important;
}
/*Nav Links*/
.menu--main li a:link,
.menu--main li a:visited {
    color: #999;
}
/*Nav Deviders*/
.menu--main li::after {
    color: #999;
}
/*Website Background Color*/
main {
    background-color: #121212!important;
}
/*Header Text Color*/
h1 a:link,
h1 a:visited,
.page-title a:link,
.page-title a:visited {
    color: #fff !important;
}
/*Website Text Color*/
body {
    color: #fff !important;
}
/*Review Slider Background*/
.frontpage-review-section {
    background-color: #0f0f0f !important;
}
/*Sidebar Background*/
.sidebar {
    background-color: #0F0F0F !important;
}
/*Sidebar Scrollbar Color*/
.mCS-dark.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar {
    background-color: rgba(255, 255, 255, .75) !important;
}
/*Poll Background*/
body.path-currentpoll {
    background-color: #0F0F0F !important;
}
/*Article Hero Image Background Color*/
.field--name-field-image {
    background-color: #121212 !important;
}
/*Sort Container Borders*/
.sort-container {
    border-top: 1px solid #303030;
    border-bottom: 1px solid #303030;
    color: #fff;
}
/*Sort Container Text Links*/
.sort-container a:link, .sort-container a:visited, .sort-container .sort-button:link, .sort-container .sort-button:visited {
    color: #999 !important;
}
/*Hubs Divider*/
.dept-hubs {
    border-top: 1px solid #303030;
}
/*Previous Hubs Divider*/
.prev-cover-hubs {
    border-top: 1px solid #303030;
}
/*Previous Hubs Text*/
.prev-cover-hubs a:link, .prev-cover-hubs a:visited {
    color: #fff;
}
/*Hubs Archive Divider*/
.hub-archive {
    margin: 3rem auto;
    border-top: 1px solid #303030;
}
/*Hubs Year Divider*/
.hub-year h2 {
    border-bottom: 1px solid #303030;
}
/*Hubs Year Text*/
.hub-year ul a:link, .hub-year ul a:visited {
    color: #fff;
}
/*Divider Between Articles*/
.ds-gi5-teaser, .teaser {
    border-bottom: 1px solid #303030;
}
/*Divider Above Article*/
.author-details {
    border-bottom: 1px solid #303030;
}
/*Social Button Box LeftSide Background*/
.social-share {
    background-color: #121212 !important;
}
/*Social Button Box LeftSite Text*/
.social-share-button a:link,
.social-share-button a:visited {
    color: #fff !important;
}
/*Sticky Nav Menu Dividers Top*/
#stickynav nav li {
    border-top: 1px solid #303030;
}
/*Sticky Nav Menu Dividers Bottom*/
#stickynav nav li:last-child {
    border-bottom: 1px solid #303030;
}
/*Sticky Nav Menu Hover*/
#stickynav nav li:hover a {
    background-color: #0f0f0f;
}
/*Sticky Nav Menu Text*/
#stickynav nav li a {
    color: #fff !important;
}
/*Article Author Footer Background*/
.author-footer {
    background-color: #1c1c1c !important;
}
/*Article Author Name*/
.author-footer-name a:link,
.author-footer-name a:visited {
    color: #fff;
}
/*Recomended Articles Slider Background*/
.recommended-articles-widget {
    background-color: #0F0F0F !important;
}
/*Recomended Articles Slider Left Fade*/
.slick-initialized:before {
    background: linear-gradient(to right, #0f0f0f, rgba(255, 255, 255, 0)) !important;
}
/*Recomended Articles Slider Right Fade*/
.slick-initialized:after {
    background: linear-gradient(to left, #0f0f0f, rgba(255, 255, 255, 0)) !important;
}
/*Cover Galley Table Borders*/
.covers-container>* {
    border-bottom: 1px solid #303030 !important;
    border-right: 1px solid #303030 !important;
}
/*Locale Settings Background*/
[aria-controls="edit-timezone"] {
    background-color: #080808 !important;
}
/*Locale Settings Hover*/
[aria-controls="edit-timezone"]:hover {
    background-color: #121212 !important;
}
/*Locale Settings Border*/
#edit-timezone {
    border: 1px solid #303030 !important;
}
/*Profile Time Zone Dropdown*/
select {
    background: #080808 !important;
}
/*Help Box Background*/
aside {
    background-color: #0f0f0f !important;
}
/*Registration Box Background*/
.user-page .content-wrapper {
    background-color: #0f0f0f !important;
}
}
