css:
@-moz-document domain("keskustelu.jatkoaika.com") {
    /* Mainokset pois */
    .gpt-ad,
    #veikkaus-keskustelu,
    .rekrylift.block {
        display: none;
    }
    .template-forum_list.lidl .p-pageWrapper {
        margin-top: 0px !important;
    }
    html.template-forum_list.lidl {
        background-color: #002131 !important;
    }
    /*sivupalkin ja osioiden headerin piilotus*/
    .p-body-sidebar,
    .p-body-header,
    .p-body-sidebarCol {
        display: none !important;
    }
    /* sivuston leveys*/
    .p-pageWrapper {
        max-width: 1200px !important;
    }
    /*tykkäys yms. nappuloiden pikku säätö*/
    .message .reactionsBar {
        margin-top: 5px !important;
    }
    /* sivuston yleistä tiivistystä*/
    .p-body-inner,
    .p-body-main--withSidebar .p-body-content {
        padding: 5px !important;
    }
    .p-body {
        margin-top: 15px;
    }
    .p-navSticky,
    .p-sectionLinks {
        margin: 5px 5px !important;
    }
    /* tiivistykset loppu*/
    /* Käyttäjänimen fontti viesteissä*/
    .message-userDetails .message-name {
        font-family: Verdana, Helvetica, Arial, sans-serif !important;
        font-size: 18px;
    }
    /*Osioiden fonttikoko */
    .node-title {
        font-size: 18px !important;
    }
    /* Aihealueiden palkin korkeus */
    .block-header {
        padding: 5px 20px !important;
    }
    /*Otsikkolistauksen tiivistys */
    .structItem-cell {
        padding: 5px 10px !important;
    }
    .structItem-cell.structItem-cell--main {
        padding-left: 25px !important;
    }
    /*Alueiden logojen tiivistys */
    .svgIcon {
        padding: 0px 15px !important;
    }
    /* Viestien välinen info */
    .message-attribution {
        font-size: 12px !important;
        font-family: Verdana, Helvetica, Arial, sans-serif !important;
    }
}
