css:
@-moz-document domain("www.bilibili.com"), domain("bangumi.bilibili.com") {
.bpui-slider-tracker .bpui-slider-handle {
    background-image: url(data:image/png;base64,);
    background-size: 20px 19px;
    width: 20px;
    height: 19px!important;
    background-color: rgba(0, 0, 0, 0);
    top: -8px;
    box-shadow: none;
}
.bpui-slider-tracker .bpui-slider-handle:hover {
    box-shadow: none;
}
.bpui-slider.bpui-slider-vertical .bpui-slider-tracker .bpui-slider-handle {
    left: -7px;
}
}
