css:
@-moz-document domain("duckduckgo.com") {

    

/*
   DuckDuckGo - Jim Can Read It! 
        
  Ver: 1.0 Date: 2018-06-12 Initial release
   Ver: 1.1 Date: 2018-10-27 minor change, included screenshot 
 
*/  
/*

*/      



body{
        background-color: #FAFAFA !important;
    } 

/*
 Hide main ad  
*/  
 .results--ads.has-ad, .results--ads  .links_deep{
     display: none !important;
    height: 0 !important;
    color:black !important;
}
.ia-modules.js-ia-modules{
     display: none !important;
    height: 0 !important;
    color:black !important;
}
 .result__snippet {
    font-size: 1em !important;
    font-weight:500;
    color: #000;
    line-height: 1.25;
    margin: 0 0 0.2em;
} 

/*
 Can never tell between purple and blue, change visited color to greenish
*/  
.result__title a:visited {
    color: #009688 !important;
}
 
a.result__url, .result__extras {
    color:#444 ;
    font-style:italic;
}
/* ToDo
*/
.result__title
    
{
    background: #DFE2ED; 
    border: 1px solid #C9CAD6;
    border-radius: 14px 14px 14px 14px; 
    padding: 8px 7px 8px 12px; 
    margin: 0px 0px 4px 1px;
    box-shadow: inset white 0px 0px 1px 0px;
    display: inline-flex;
}

.result__title:hover {
    border: 1px solid #8187ce;
}


}
