css:
@-moz-document domain(organizze.com.br) {

@font-face {
  font-family: 'Material Icons';
  font-style: normal;
  font-weight: 400;
  src: url(https://fonts.gstatic.com/s/materialicons/v38/flUhRq6tzZclQEJ-Vdg-IuiaDsNcIhQ8tQ.woff2) format('woff2');
}

  .material-icons {
  font-family: 'Material Icons';
  font-weight: normal;
  font-style: normal;
  font-size: 24px;
  line-height: 1;
  letter-spacing: normal;
  text-transform: none;
  display: inline-block;
  white-space: nowrap;
  word-wrap: normal;
  direction: ltr;
  -webkit-font-feature-settings: 'liga';
  -webkit-font-smoothing: antialiased;
}
.tool-bar>ul li a {
	color: #1FB6FF;
	transition: all .2s;
}
.tool-bar>ul li a:hover {
	color: #1FB6FF;
	opacity: 0.6;
}
.blue {
	color: #1FB6FF;
}
.zze-layout_header {
	background: #1FB6FF;
}
.zze-layout_header .zze-header-center > nav > ul > li > a {
	color: #fff;
}
body.dashboard section.content .main-container .widget_box.accounts_overview .accounts-list li a span.description big {
	color: #1FB6FF;
}
body.dashboard section.content .main-container .widget_box.fast_add .fast-access ul li.earning a:hover,
body.dashboard section.content .main-container .widget_box.fast_add .fast-access ul li.expense a:hover {
	color: #fff;
}
body.dashboard section.content .main-container .widget_box.fast_add .fast-access ul li.transference a:hover {
	color: #66757f;
}
.zze-layout_header .zze-header-right nav > ul > li.account .drop-down-box .user-menu-group .account-menu li a:hover,
.zze-layout_header .zze-header-right nav > ul > li.account .drop-down-box .user-menu-accounts .add-account a,
.zze-context_messages-app .messages-history .more-messages a {
	color: #1FB6FF;
}
body.dashboard section.content .main-container .widget_box.overview .box-content .group-overview.positive {
	background: #1FB6FF;
}
div#user_account_widget_indication,
div.widget_box.grid-sortable-item.blog_feeds {
	display: none;
}
.zze-context_account-details .account-header.positive {
	background: #1FB6FF;
}
body.transactions .container-legacy .aux-bar.filter-open {
	background: #1FB6FF;
}
body.transactions .container-legacy .aux-bar.filter-open .drop-down a {
	color: #fff;
}
body.dashboard section.content .main-container .widget_box.fast_add .fast-access ul li {
	float: left;
	margin: 0 6px 6px 0;
	width: calc(33.33% - 6px);
}
body.dashboard section.content .main-container .widget_box.fast_add .fast-access ul li a {
	width: 100%;
	border-radius: 6px;
}
body.dashboard section.content .main-container .widget_box.credit_cards_overview .cards-list li span.info a {
	color: #1FB6FF;
}
body.dashboard section.content .main-container .widget_box.credit_cards_overview .cards-list li span.more a:hover {
	background: #1FB6FF;
	border-color: #1FB6FF;
}
.drop-down .drop-down-box .scroll-group>ul li a:hover,
.drop-down .drop-down-box ul li a:hover {
	color: #1FB6FF;
}
body.transactions .container-legacy .balance-bar .inner .all-result .list-links li a {
	color: #1FB6FF;
}
body.transactions .container-legacy .aux-bar .bar-filter > ul > li .drop-down .drop-down-box {
	z-index: 9999;
	color: #657786;
	background: #FFFFFF;
	box-shadow: 0 1px 6px rgba(0, 0, 0, 0.17);
}
.drop-down.black .drop-down-box {
	color: #657786;
	background: #FFFFFF;
	box-shadow: 0 1px 6px rgba(0, 0, 0, 0.17);
}
.drop-down.black .drop-down-box:after {
	border-bottom: 6px solid #fff;
}

body.transactions .container-legacy .aux-bar .bar-filter > ul > li .active a {
	background: #1FB6FF;
}
body.transactions .container-legacy .aux-bar.filter-open .drop-down a {
	color: #657786;
}
.drop-down.black .drop-down-box>.scroll-group>ul li.active>a,
.drop-down.black .drop-down-box>ul li.active>a {
	color: #fff;
	background: #1FB6FF !important;
}
.drop-down.black .drop-down-box>.scroll-group>ul li a:hover,
.drop-down.black .drop-down-box>.scroll-group>ul li a.hover,
.drop-down.black .drop-down-box>ul li a:hover,
.drop-down.black .drop-down-box>ul li a.hover {
	color: #fff;
	background-color: #657786 !important;
}

.zze-layout-premium_bar {
	background: #657786;
}
.zze-layout-premium_bar .zze-layout-premium_bar-content .zze-component_circle-with-crown .avatar {
	background-color: #1FB6FF;
}
.zze-layout_header .zze-header-left .toggle-premium-bar {
	width: 51px;
	background: rgba(0, 0, 0, 0);
}
.zze-layout_header .zze-header-left .toggle-premium-bar + .logo {
	margin-left: 32px;
}
.zze-layout_header .zze-header-left .toggle-premium-bar i:before {
	content: "\E5D2";
	font-family: 'Material Icons' !important;
	color: #fff;
	width: 24px;
	height: 24px;
	line-height: 24px;
	position: absolute;
	top: calc(50% - 12px);
	left: calc(50% - 12px);
	font-size: 20px;
}
.tag.blue {
	color: #fff;
	background: #1FB6FF;
}
.aux-bar.small-bar .list-navigation li.actived a {
	color: #1FB6FF;
	border-bottom: 1px solid #1FB6FF;
}
body.dashboard section.content .main-container .widget_box.credit_cards_overview .cards-list li span.info a,
body.dashboard section.content .main-container .widget_box.bills_to_pay .box-content .group-box table tbody tr td.description a,
body.dashboard section.content .main-container .widget_box.bills_to_receive .box-content .group-box table tbody tr td.description a,
body.reports .reports-content .reports-graphics .filter-bar .list-filter li a:hover:hover,
.aux-bar.small-bar .list-navigation li a:hover:hover {
	color: #666;
	transition: all .2s;
}
body.reports .reports-content .reports-graphics .filter-bar .list-filter li a.active {
	color: #1FB6FF;
}
body.reports .reports-content .reports-graphics .filter-bar .list-filter li a.active:before {
	border-top: 4px solid #1FB6FF;
}
.positive {
	color: #4CD17A !important;
}
.expense {
	color: red;
}
.expense .type span {
	background: #d50118 !important;
}
.earning .type span {
	background: #4CD17A !important;
}
.type span {
	background-image: none;
	border-radius: 60px;
	width: 8px;
	height: 8px;
	margin-right: 10px;
}
.autocomplete>a.open-autocomplete {
	right: 0;
	top: 0;
	width: 100%;
	height: 35px;
}
.autocomplete>a.open-autocomplete .icon.i10 {
	width: 10px;
	height: 10px;
	float: right;
	margin: 12px 10px 0 0;
}

body.pessoal input[type="password"]:focus, 
body.pessoal input[type="date"]:focus, 
body.pessoal input[type="file"]:focus, 
body.pessoal input[type="email"]:focus,
body.pessoal input[type="text"]:focus, 
body.pessoal select:focus,
body.pessoal textarea:focus{
		color: #1FB6FF;
		border-color: #1FB6FF;
}

body.pessoal input[type="password"]:focus small, 
body.pessoal input[type="date"]:focus small, 
body.pessoal input[type="file"]:focus small, 
body.pessoal input[type="email"]:focus small,
body.pessoal input[type="text"]:focus small, 
body.pessoal select:focus small, 
body.pessoal textarea:focus small {
			color: #1FB6FF;
}

.date-picker .calendar .ui-widget.ui-datepicker table.ui-datepicker-calendar tbody tr td.ui-datepicker-current-day a {
		background-color: #1FB6FF;
}

.simple-popup .window .more-options ul li a {
		color: #1FB6FF;
}
.simple-popup .window .more-options ul li a:hover {
		color: #1FB6FF;
}

.simple-popup .window .more-options.open-options a i {
			color: #1FB6FF;
}
.date-picker .calendar .ui-widget.ui-datepicker table.ui-datepicker-calendar tbody tr td.ui-datepicker-today.ui-datepicker-current-day a {
			background-color: #1FB6FF;
}

.simple-popup.transaction-form.transaction-add .window .popup-content .list-options ul li a:hover, 
.simple-popup.transaction-form.transaction-add .window .popup-content .list-options ul li a:hover i {
			color: #1FB6FF;
}
}
